import React from 'react';
import { Check, Crown, Heart, Diamond, Star, Sparkles } from 'lucide-react';

const ProgressBar = ({ steps, onStepClick }) => {
  const stepIcons = [Crown, Heart, Diamond, Star];
  
  return (
    <div className="w-full">
      <h3 className="text-base font-semibold text-gray-900 mb-3 flex items-center">
        <div className="w-2 h-2 bg-yellow-500 rounded-full mr-2"></div>
        Customization Progress
      </h3>
      
      <div className="flex items-center justify-between mb-4">
        {steps.map((step, index) => {
          const IconComponent = stepIcons[index];
          return (
            <React.Fragment key={step.step}>
              {/* Step Circle */}
              <div className="flex flex-col items-center">
                <button
                  onClick={() => onStepClick(step.step)}
                  className={`w-12 h-12 rounded-full flex items-center justify-center text-xs font-semibold transition-all duration-200 hover:scale-110 relative ${
                    step.completed
                      ? 'bg-gradient-to-r from-yellow-500 to-yellow-600 text-black shadow-lg'
                      : step.current
                      ? 'bg-gradient-to-r from-yellow-400 to-yellow-500 text-black shadow-lg'
                      : 'bg-gray-200 text-gray-500 hover:bg-gray-300 border-2 border-gray-300'
                  }`}
                >
                  {step.completed ? (
                    <Check className="w-5 h-5" />
                  ) : (
                    <IconComponent className="w-5 h-5" />
                  )}
                  
                  {/* Sparkle effect for current step */}
                  {step.current && (
                    <div className="absolute -top-1 -right-1">
                      <Sparkles className="w-3 h-3 text-yellow-400 animate-pulse" />
                    </div>
                  )}
                </button>
                
                {/* Step Label */}
                <span className={`text-xs mt-1 font-medium text-center max-w-16 ${
                  step.completed
                    ? 'text-yellow-600'
                    : step.current
                    ? 'text-yellow-500'
                    : 'text-gray-500'
                }`}>
                  {step.label}
                </span>
              </div>
              
              {/* Connector Line */}
              {index < steps.length - 1 && (
                <div className={`flex-1 h-0.5 mx-2 rounded-full transition-all duration-300 ${
                  steps[index + 1].completed || steps[index + 1].current
                    ? 'bg-gradient-to-r from-yellow-400 to-yellow-500'
                    : 'bg-gray-200'
                }`} />
              )}
            </React.Fragment>
          );
        })}
      </div>
      
      {/* Progress Percentage */}
      <div className="w-full bg-gray-200 rounded-full h-1.5 overflow-hidden">
        <div 
          className="bg-gradient-to-r from-yellow-400 to-yellow-500 h-1.5 rounded-full transition-all duration-500 ease-out"
          style={{ 
            width: `${(steps.filter(s => s.completed).length / steps.length) * 100}%` 
          }}
        />
      </div>
      
      <div className="flex justify-between text-xs text-gray-500 mt-2">
        <span className="font-medium">Step {steps.filter(s => s.completed).length} of {steps.length}</span>
        <span className="font-medium">{Math.round((steps.filter(s => s.completed).length / steps.length) * 100)}% Complete</span>
      </div>
    </div>
  );
};

export default ProgressBar;
