import React, { useEffect, useState } from "react";
import { supabase } from '../lib/supabase';

const designOptions = [
  '', 'classic-solitaire', 'halo-setting', 'vintage-antique', 'three-stone'
];
const metalOptions = [
  '', 'white-gold', 'yellow-gold', 'rose-gold', 'platinum'
];
const shapeOptions = [
  '', 'round', 'princess', 'emerald', 'oval'
];
const caratOptions = [
  '', '1.0', '1.5', '2.0', '2.5'
];

const designLabels = {
  '': 'All Designs',
  'classic-solitaire': 'Classic Solitaire',
  'halo-setting': 'Halo Setting',
  'vintage-antique': 'Vintage/Antique',
  'three-stone': 'Three Stone',
};
const metalLabels = {
  '': 'All Metals',
  'white-gold': 'White Gold',
  'yellow-gold': 'Yellow Gold',
  'rose-gold': 'Rose Gold',
  'platinum': 'Platinum',
};
const shapeLabels = {
  '': 'All Shapes',
  'round': 'Round',
  'princess': 'Princess',
  'emerald': 'Emerald',
  'oval': 'Oval',
};
const caratLabels = {
  '': 'All Carats',
  '1.0': '1.0 ct',
  '1.5': '1.5 ct',
  '2.0': '2.0 ct',
  '2.5': '2.5 ct',
};

const Shop = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    design: '',
    metal: '',
    shape: '',
    carat: '',
  });

  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      const { data, error } = await supabase
        .from('ring_images')
        .select('*')
        .order('design', { ascending: true })
        .order('metal', { ascending: true })
        .order('diamond_shape', { ascending: true })
        .order('carat', { ascending: true });
      if (error) {
        setError(error.message);
        setProducts([]);
      } else {
        setProducts(data || []);
      }
      setLoading(false);
    };
    fetchProducts();
  }, []);

  const handleFilterChange = (key, value) => {
    setFilters(f => ({ ...f, [key]: value }));
  };

  const clearFilters = () => {
    setFilters({ design: '', metal: '', shape: '', carat: '' });
  };

  const filteredProducts = products.filter(product => {
    return (
      (!filters.design || product.design === filters.design) &&
      (!filters.metal || product.metal === filters.metal) &&
      (!filters.shape || product.diamond_shape === filters.shape) &&
      (!filters.carat || (product.carat && product.carat.toString() === filters.carat))
    );
  });

  if (loading) return <div className="text-center py-12">Loading products...</div>;
  if (error) return <div className="text-center py-12 text-red-600">Error: {error}</div>;

  return (
  <div className="bg-white min-h-screen font-sans px-4 py-12">
    <h1 className="text-4xl font-serif font-bold text-black mb-10 text-center tracking-tight" style={{ fontFamily: 'Playfair Display, serif' }}>
      Shop All Rings
    </h1>
      {/* Filter Bar */}
      <div className="flex flex-wrap gap-4 justify-center mb-8">
        <select className="border border-gray-300 rounded px-5 py-2 text-black bg-white focus:outline-gold" value={filters.design} onChange={e => handleFilterChange('design', e.target.value)}>
          {designOptions.map(opt => <option key={opt} value={opt}>{designLabels[opt]}</option>)}
        </select>
        <select className="border border-gray-300 rounded px-5 py-2 text-black bg-white focus:outline-gold" value={filters.metal} onChange={e => handleFilterChange('metal', e.target.value)}>
          {metalOptions.map(opt => <option key={opt} value={opt}>{metalLabels[opt]}</option>)}
      </select>
        <select className="border border-gray-300 rounded px-5 py-2 text-black bg-white focus:outline-gold" value={filters.shape} onChange={e => handleFilterChange('shape', e.target.value)}>
          {shapeOptions.map(opt => <option key={opt} value={opt}>{shapeLabels[opt]}</option>)}
      </select>
        <select className="border border-gray-300 rounded px-5 py-2 text-black bg-white focus:outline-gold" value={filters.carat} onChange={e => handleFilterChange('carat', e.target.value)}>
          {caratOptions.map(opt => <option key={opt} value={opt}>{caratLabels[opt]}</option>)}
      </select>
        <button className="px-5 py-2 bg-gold text-black font-bold rounded border border-gold hover:bg-black hover:text-gold transition" onClick={clearFilters}>
          Clear Filters
        </button>
    </div>
      <div className="w-full border-t border-gray-200 my-8" />
    {/* Product Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8 max-w-7xl mx-auto">
        {filteredProducts.length === 0 ? (
          <div className="col-span-full text-center text-gray-500 py-12">No products found for selected filters.</div>
        ) : filteredProducts.map((product) => {
          const imageUrl = product.public_url || (product.image_url
            ? supabase.storage.from('ring-images').getPublicUrl(product.image_url).data.publicUrl
            : '/placeholder-ring.png');
          return (
        <div key={product.id} className="bg-white rounded-2xl shadow-lg border border-gray-200 hover:border-gold transition-all duration-300 flex flex-col items-center group overflow-hidden relative">
          <div className="w-full h-56 flex items-center justify-center overflow-hidden rounded-t-2xl bg-gray-50">
                <img src={imageUrl} alt={product.design} className="w-full h-full object-cover transform group-hover:scale-105 group-hover:-translate-y-1 transition-transform duration-300 ease-in-out" onError={e => { e.target.src = '/placeholder-ring.png'; }} />
          </div>
          <div className="p-5 w-full flex flex-col items-center">
                <div className="font-serif text-lg font-bold mb-1 text-center text-black" style={{ fontFamily: 'Playfair Display, serif' }}>{designLabels[product.design] || product.design}</div>
                <div className="text-gray-700 text-sm mb-1">{metalLabels[product.metal] || product.metal} • {shapeLabels[product.diamond_shape] || product.diamond_shape} • {product.carat ? `${product.carat} ct` : ''}</div>
                <div className="text-gold text-base mb-3 font-semibold">{product.price ? `$${product.price.toLocaleString()}` : ''}</div>
            <a href={`/product/${product.id}`} className="inline-block mt-auto px-6 py-2 bg-black text-gold font-bold rounded-full shadow hover:bg-gold hover:text-black transition-all duration-200 text-sm tracking-wide border border-black focus:outline-none focus:ring-2 focus:ring-gold/40 hover:scale-105">
              View Details
            </a>
          </div>
        </div>
          );
        })}
    </div>
    <style>{`
      .text-gold { color: #bfa14a; }
      .border-gold { border-color: #bfa14a; }
      .bg-gold { background-color: #bfa14a; }
    `}</style>
  </div>
);
};

export default Shop;
