import React, { useState } from "react";
import { useCart } from "../App";

const Checkout = () => {
  const { cart, setCart } = useCart();
  const [form, setForm] = useState({ name: "", address: "", city: "", postal: "", country: "", card: "", exp: "", cvv: "" });
  const [submitted, setSubmitted] = useState(false);
  const subtotal = cart.reduce((sum, item) => sum + item.price * item.qty, 0);
  const shipping = cart.length > 0 ? 50 : 0;
  const total = subtotal + shipping;

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });
  const handleSubmit = e => {
    e.preventDefault();
    if (Object.values(form).some(v => !v)) return alert("Please fill all fields.");
    setSubmitted(true);
    setCart([]);
  };

  if (submitted) {
    return (
      <div className="bg-white min-h-screen font-sans px-4 py-12 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-black mb-4">Thank you for your order!</h1>
          <p className="text-gold-dark mb-4">A confirmation email will be sent to you soon.</p>
          <a href="/shop" className="text-gold font-bold hover:underline">Continue Shopping</a>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white min-h-screen font-sans px-4 py-12">
      <h1 className="text-3xl font-bold text-black mb-8 text-center">Checkout</h1>
      <form onSubmit={handleSubmit} className="max-w-4xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-12">
        {/* Shipping Info */}
        <div className="bg-white rounded-lg shadow p-6 border border-gold mb-8">
          <h2 className="text-xl font-bold text-black mb-4">Shipping Information</h2>
          <input className="w-full border border-gold rounded px-4 py-2 mb-2" name="name" value={form.name} onChange={handleChange} placeholder="Full Name" />
          <input className="w-full border border-gold rounded px-4 py-2 mb-2" name="address" value={form.address} onChange={handleChange} placeholder="Address" />
          <input className="w-full border border-gold rounded px-4 py-2 mb-2" name="city" value={form.city} onChange={handleChange} placeholder="City" />
          <input className="w-full border border-gold rounded px-4 py-2 mb-2" name="postal" value={form.postal} onChange={handleChange} placeholder="Postal Code" />
          <input className="w-full border border-gold rounded px-4 py-2 mb-2" name="country" value={form.country} onChange={handleChange} placeholder="Country" />
        </div>
        {/* Payment Info & Order Summary */}
        <div className="bg-white rounded-lg shadow p-6 border border-gold mb-8">
          <h2 className="text-xl font-bold text-black mb-4">Payment Information</h2>
          <input className="w-full border border-gold rounded px-4 py-2 mb-2" name="card" value={form.card} onChange={handleChange} placeholder="Card Number" />
          <input className="w-full border border-gold rounded px-4 py-2 mb-2" name="exp" value={form.exp} onChange={handleChange} placeholder="Expiration Date" />
          <input className="w-full border border-gold rounded px-4 py-2 mb-2" name="cvv" value={form.cvv} onChange={handleChange} placeholder="CVV" />
          <div className="border-t border-gold pt-4 mt-4">
            <div className="flex justify-between mb-2 text-black"><span>Subtotal</span><span>${subtotal.toLocaleString()}</span></div>
            <div className="flex justify-between mb-2 text-black"><span>Shipping</span><span>${shipping}</span></div>
            <div className="flex justify-between font-bold text-black text-lg"><span>Total</span><span className="text-gold-dark">${total.toLocaleString()}</span></div>
          </div>
          <button type="submit" className="w-full mt-6 py-3 bg-black text-gold font-bold rounded-full shadow hover:bg-gold hover:text-black transition">Place Order</button>
        </div>
      </form>
    </div>
  );
};

export default Checkout;
