import React, { useState, useEffect } from 'react';
import { 
  ChevronRight, 
  ChevronLeft, 
  Check, 
  Sparkles, 
  Gem, 
  Settings, 
  Zap, 
  Crown,
  Heart,
  Star,
  Diamond,
  Circle,
  Square,
  Hexagon,
  CircleDot,
  ShoppingCart,
  Eye,
  Palette,
  Scissors
} from 'lucide-react';
import { getProgressiveRingImage } from '../lib/supabase';
import { ringOptions, basePrice } from '../data/ringOptions';
import RingPreview from './RingPreview';
import CustomizationStep from './CustomizationStep';
import ProgressBar from './ProgressBar';
import { supabase } from '../lib/supabase';

const RingCustomizer = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [selections, setSelections] = useState({
    metal: '',
    design: '',
    shape: '',
    carat: ''
  });
  const [currentImage, setCurrentImage] = useState('');
  const [loading, setLoading] = useState(false);
  const [totalPrice, setTotalPrice] = useState(basePrice);

  const steps = [
    { id: 1, title: 'Choose Metal', icon: Crown, description: 'Select your preferred metal type' },
    { id: 2, title: 'Select Design', icon: Heart, description: 'Pick your ring setting style' },
    { id: 3, title: 'Diamond Shape', icon: Diamond, description: 'Choose your diamond cut' },
    { id: 4, title: 'Carat Weight', icon: Star, description: 'Select diamond size' }
  ];

  // Updated options with icons
  const metals = [
    { value: 'white-gold', label: 'White Gold', sublabel: '14K/18K', icon: Crown, color: 'text-gray-400' },
    { value: 'yellow-gold', label: 'Yellow Gold', sublabel: '14K/18K', icon: Crown, color: 'text-yellow-500' },
    { value: 'rose-gold', label: 'Rose Gold', sublabel: '14K/18K', icon: Crown, color: 'text-rose-400' },
    { value: 'platinum', label: 'Platinum', sublabel: '950', icon: Crown, color: 'text-gray-300' }
  ];

  const designs = [
    { value: 'classic-solitaire', label: 'Classic Solitaire', sublabel: 'Timeless elegance', icon: Circle, color: 'text-blue-500' },
    { value: 'halo-setting', label: 'Halo Setting', sublabel: 'Enhanced brilliance', icon: CircleDot, color: 'text-purple-500' },
    { value: 'vintage-antique', label: 'Vintage/Antique', sublabel: 'Romantic charm', icon: Heart, color: 'text-rose-500' },
    { value: 'three-stone', label: 'Three Stone', sublabel: 'Trinity design', icon: Star, color: 'text-yellow-500' }
  ];

  const shapes = [
    { value: 'round', label: 'Round Brilliant', sublabel: 'Classic sparkle', icon: Circle, color: 'text-blue-500' },
    { value: 'princess', label: 'Princess Cut', sublabel: 'Modern square', icon: Square, color: 'text-purple-500' },
    { value: 'emerald', label: 'Emerald Cut', sublabel: 'Elegant rectangle', icon: Square, color: 'text-green-500' },
    { value: 'oval', label: 'Oval Brilliant', sublabel: 'Elongated beauty', icon: Circle, color: 'text-pink-500' }
  ];

  const carats = [
    { value: '1.0', label: '1.0 Carat', sublabel: 'Perfect balance', icon: Diamond, color: 'text-blue-500' },
    { value: '1.5', label: '1.5 Carat', sublabel: 'Impressive presence', icon: Diamond, color: 'text-purple-500' },
    { value: '2.0', label: '2.0 Carat', sublabel: 'Stunning statement', icon: Diamond, color: 'text-pink-500' },
    { value: '2.5', label: '2.5 Carat', sublabel: 'Ultimate luxury', icon: Diamond, color: 'text-red-500' }
  ];

  const generateImagePath = () => {
    if (!selections.metal) return '';

    let path = `rings/${selections.metal}`;

    if (selections.design) {
      path += `/${selections.design}`;
    }

    if (selections.shape) {
      path += `/${selections.shape}`;
    }

    if (selections.carat) {
      path += `/${selections.carat}ct.png`;
    } else {
      path += '/band.png';
    }

    return path;
  };

  const fetchImage = async (imagePath) => {
    if (!imagePath) return;

    try {
      setLoading(true);

      // Get the public URL from Supabase Storage
      const { data } = supabase.storage
        .from('ring-images')
        .getPublicUrl(imagePath);

      if (data?.publicUrl) {
        setCurrentImage(data.publicUrl);
      } else {
        // Fallback to placeholder
        setCurrentImage('/placeholder-ring.png');
      }
    } catch (error) {
      console.error('Error fetching image:', error);
      setCurrentImage('/placeholder-ring.png');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const imagePath = generateImagePath();
    fetchImage(imagePath);
  }, [selections]);

  // Calculate total price
  useEffect(() => {
    let total = basePrice;

    if (selections.metal) {
      const metal = ringOptions.metals.find(m => m.id === selections.metal);
      total += metal?.price || 0;
    }

    if (selections.design) {
      const design = ringOptions.designs.find(d => d.id === selections.design);
      total += design?.price || 0;
    }

    if (selections.shape) {
      const shape = ringOptions.shapes.find(s => s.id === selections.shape);
      total += shape?.price || 0;
    }

    if (selections.carat) {
      const carat = ringOptions.carats.find(c => c.id === selections.carat);
      total += carat?.price || 0;
    }

    setTotalPrice(total);
  }, [selections]);

  const handleSelection = (step, value) => {
    setSelections(prev => ({
      ...prev,
      [step]: value
    }));

    // Auto-advance to next step
    if (currentStep < 4) {
      setCurrentStep(currentStep + 1);
    }
  };

  const goToStep = (step) => {
    setCurrentStep(step);
  };

  const getCurrentStepData = () => {
    switch (currentStep) {
      case 1:
        return {
          title: 'Step 1: Choose Metal',
          description: 'Select your preferred metal type',
          options: metals,
          selected: selections.metal,
          onSelect: (value) => handleSelection('metal', value)
        };
      case 2:
        return {
          title: 'Step 2: Choose Design',
          description: 'Select your ring design style',
          options: designs,
          selected: selections.design,
          onSelect: (value) => handleSelection('design', value)
        };
      case 3:
        return {
          title: 'Step 3: Choose Diamond Shape',
          description: 'Select your diamond cut',
          options: shapes,
          selected: selections.shape,
          onSelect: (value) => handleSelection('shape', value)
        };
      case 4:
        return {
          title: 'Step 4: Choose Carat Weight',
          description: 'Select your diamond size',
          options: carats,
          selected: selections.carat,
          onSelect: (value) => handleSelection('carat', value)
        };
      default:
        return null;
    }
  };

  const getProgressStatus = () => {
    return [
      { step: 1, label: 'Metal', completed: !!selections.metal, current: currentStep === 1 },
      { step: 2, label: 'Design', completed: !!selections.design, current: currentStep === 2 },
      { step: 3, label: 'Shape', completed: !!selections.shape, current: currentStep === 3 },
      { step: 4, label: 'Carat', completed: !!selections.carat, current: currentStep === 4 }
    ];
  };

  const getCurrentDescription = () => {
    if (!selections.metal) return 'Start by selecting your metal type';

    let description = `Selected: ${metals.find(m => m.value === selections.metal)?.label}`;

    if (selections.design) {
      description += ` • ${designs.find(d => d.value === selections.design)?.label}`;
    }

    if (selections.shape) {
      description += ` • ${shapes.find(s => s.value === selections.shape)?.label}`;
    }

    if (selections.carat) {
      description += ` • ${carats.find(c => c.value === selections.carat)?.label}`;
    }

    return description;
  };

  const handleAddToCart = () => {
    if (selections.metal && selections.design && selections.shape && selections.carat) {
      alert('Ring added to cart! (This would integrate with your shopping cart system)');
    } else {
      alert('Please complete all customization steps first');
    }
  };

  const stepData = getCurrentStepData();

  return (
    <div className="min-h-screen bg-white flex flex-col items-center overflow-x-hidden">
      <div className="w-full max-w-xl px-2 sm:px-4 flex flex-col items-center">
         {/* Header - minimal */}
         <div className="text-center mt-2 mb-2">
           <div className="flex items-center justify-center">
             <Diamond className="w-4 h-4 text-yellow-500 mr-1" />
             <h1 className="text-base font-bold bg-gradient-to-r from-yellow-400 to-yellow-600 bg-clip-text text-transparent">
               Ring Customizer
             </h1>
           </div>
         </div>

         {/* Ring Preview - as large as possible */}
         <div className="w-full flex flex-col items-center mb-1">
           <div className="w-full flex justify-center items-center">
             <img
               src={currentImage}
               alt="Ring preview"
               className="object-contain max-h-80 w-auto mx-auto"
               style={{ maxWidth: '100%' }}
             />
           </div>
         </div>

         {/* Controls - ultra minimal */}
         <div className="w-full flex flex-col gap-0.5 items-center">
           {/* Progress Bar */}
           <div className="w-full text-[10px]">
             <ProgressBar steps={getProgressStatus()} onStepClick={goToStep} tiny />
           </div>

           {/* Current Customization Step */}
           <div className="w-full text-[10px] flex items-center gap-1">
             {currentStep > 1 && (
               <button
                 onClick={() => setCurrentStep(currentStep - 1)}
                 className="flex items-center px-1 py-0.5 text-[10px] bg-gray-100 border border-gray-300 rounded hover:bg-gray-200 transition-all duration-150 mr-1"
                 aria-label="Previous step"
               >
                 <ChevronLeft className="w-3 h-3 mr-0.5" /> Prev
               </button>
             )}
             <div className="flex-1">
               {stepData && (
                 <CustomizationStep 
                   title={null}
                   description={null}
                   options={stepData.options}
                   selected={stepData.selected}
                   onSelect={stepData.onSelect}
                   tiny
                 />
               )}
             </div>
           </div>

           {/* Summary and Add to Cart */}
           <div className="w-full text-[10px]">
             <div className="space-y-0.5">
               <div className="flex items-center justify-between text-[10px]">
                 <div className="flex items-center">
                   <Crown className="w-2.5 h-2.5 mr-1 text-yellow-500" />
                   <span className="text-gray-600">Metal:</span>
                 </div>
                 <span className="font-medium text-gray-900">
                   {selections.metal ? metals.find(m => m.value === selections.metal)?.label : 'Not selected'}
                 </span>
               </div>
               <div className="flex items-center justify-between text-[10px]">
                 <div className="flex items-center">
                   <Heart className="w-2.5 h-2.5 mr-1 text-rose-500" />
                   <span className="text-gray-600">Design:</span>
                 </div>
                 <span className="font-medium text-gray-900">
                   {selections.design ? designs.find(d => d.value === selections.design)?.label : 'Not selected'}
                 </span>
               </div>
               <div className="flex items-center justify-between text-[10px]">
                 <div className="flex items-center">
                   <Diamond className="w-2.5 h-2.5 mr-1 text-blue-500" />
                   <span className="text-gray-600">Shape:</span>
                 </div>
                 <span className="font-medium text-gray-900">
                   {selections.shape ? shapes.find(s => s.value === selections.shape)?.label : 'Not selected'}
                 </span>
               </div>
               <div className="flex items-center justify-between text-[10px]">
                 <div className="flex items-center">
                   <Star className="w-2.5 h-2.5 mr-1 text-yellow-500" />
                   <span className="text-gray-600">Carat:</span>
                 </div>
                 <span className="font-medium text-gray-900">
                   {selections.carat ? carats.find(c => c.value === selections.carat)?.label : 'Not selected'}
                 </span>
               </div>
             </div>
             <div className="w-full mt-0.5">
               <button
                 onClick={handleAddToCart}
                 disabled={!(selections.metal && selections.design && selections.shape && selections.carat)}
                 className="w-full flex items-center justify-center py-0.5 text-[10px] bg-gradient-to-r from-yellow-500 to-yellow-600 text-black rounded-md hover:from-yellow-400 hover:to-yellow-500 disabled:from-gray-300 disabled:to-gray-400 disabled:cursor-not-allowed font-semibold transition-all duration-200 shadow-md hover:shadow-lg min-h-0"
               >
                 <ShoppingCart className="w-2.5 h-2.5 mr-1" />
                 <span>Add to Cart</span>
                 <span className="ml-1 px-1 py-0.5 bg-black/10 rounded text-[10px] font-bold">
                   ${totalPrice.toLocaleString()}
                 </span>
               </button>
               {!(selections.metal && selections.design && selections.shape && selections.carat) && (
                 <p className="text-[10px] text-gray-500 mt-0.5 text-center">
                   Complete all steps to add to cart
                 </p>
               )}
             </div>
           </div>
         </div>
      </div>
    </div>
  );
};

export default RingCustomizer;
