import React, { useState, useEffect } from 'react';
import { ChevronRight, ChevronLeft, Check, Sparkles, Gem, Settings, Zap } from 'lucide-react';
import { getProgressiveRingImage } from '../lib/supabase';
import { ringOptions, basePrice } from '../data/ringOptions';
import RingPreview from './RingPreview';
import CustomizationStep from './CustomizationStep';
import ProgressBar from './ProgressBar';
import { supabase } from '../lib/supabase';

const RingCustomizer = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [selections, setSelections] = useState({
    metal: '',
    design: '',
    shape: '',
    carat: ''
  });
  const [currentImage, setCurrentImage] = useState('');
  const [loading, setLoading] = useState(false);
  const [totalPrice, setTotalPrice] = useState(basePrice);

  const steps = [
    { id: 1, title: 'Choose Metal', icon: Sparkles, description: 'Select your preferred metal type' },
    { id: 2, title: 'Select Design', icon: Settings, description: 'Pick your ring setting style' },
    { id: 3, title: 'Diamond Shape', icon: Gem, description: 'Choose your diamond cut' },
    { id: 4, title: 'Carat Weight', icon: Zap, description: 'Select diamond size' }
  ];

  // Updated options based on your specifications
  const metals = [
    { value: 'white-gold', label: 'White Gold (14K/18K)' },
    { value: 'yellow-gold', label: 'Yellow Gold (14K/18K)' },
    { value: 'rose-gold', label: 'Rose Gold (14K/18K)' },
    { value: 'platinum', label: 'Platinum (950)' }
  ];

  const designs = [
    { value: 'classic-solitaire', label: 'Classic Solitaire' },
    { value: 'halo-setting', label: 'Halo Setting' },
    { value: 'vintage-antique', label: 'Vintage/Antique Style' },
    { value: 'three-stone', label: 'Three Stone (Trinity)' }
  ];

  const shapes = [
    { value: 'round', label: 'Round Brilliant Cut' },
    { value: 'princess', label: 'Princess Cut (Square)' },
    { value: 'emerald', label: 'Emerald Cut (Rectangular)' },
    { value: 'oval', label: 'Oval Brilliant Cut' }
  ];

  const carats = [
    { value: '1.0', label: '1.0 Carat' },
    { value: '1.5', label: '1.5 Carat' },
    { value: '2.0', label: '2.0 Carat' },
    { value: '2.5', label: '2.5 Carat' }
  ];

  const generateImagePath = () => {
    if (!selections.metal) return '';
    
    let path = `rings/${selections.metal}`;
    
    if (selections.design) {
      path += `/${selections.design}`;
    }
    
    if (selections.shape) {
      path += `/${selections.shape}`;
    }
    
    if (selections.carat) {
      path += `/${selections.carat}ct.png`;
    } else {
      path += '/band.png';
    }
    
    return path;
  };

  const fetchImage = async (imagePath) => {
    if (!imagePath) return;

    try {
      setLoading(true);
      
      // Get the public URL from Supabase Storage
      const { data } = supabase.storage
        .from('ring-images')
        .getPublicUrl(imagePath);

      if (data?.publicUrl) {
        setCurrentImage(data.publicUrl);
      } else {
        // Fallback to placeholder
        setCurrentImage('/placeholder-ring.png');
      }
    } catch (error) {
      console.error('Error fetching image:', error);
      setCurrentImage('/placeholder-ring.png');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const imagePath = generateImagePath();
    fetchImage(imagePath);
  }, [selections]);

  // Calculate total price
  useEffect(() => {
    let total = basePrice;
    
    if (selections.metal) {
      const metal = ringOptions.metals.find(m => m.id === selections.metal);
      total += metal?.price || 0;
    }
    
    if (selections.design) {
      const design = ringOptions.designs.find(d => d.id === selections.design);
      total += design?.price || 0;
    }
    
    if (selections.shape) {
      const shape = ringOptions.shapes.find(s => s.id === selections.shape);
      total += shape?.price || 0;
    }
    
    if (selections.carat) {
      const carat = ringOptions.carats.find(c => c.id === selections.carat);
      total += carat?.price || 0;
    }
    
    setTotalPrice(total);
  }, [selections]);

  const handleSelection = (step, value) => {
    setSelections(prev => ({
      ...prev,
      [step]: value
    }));

    // Auto-advance to next step
    if (currentStep < 4) {
      setCurrentStep(currentStep + 1);
    }
  };

  const goToStep = (step) => {
    setCurrentStep(step);
  };

  const getCurrentStepData = () => {
    switch (currentStep) {
      case 1:
        return {
          title: 'Step 1: Choose Metal',
          description: 'Select your preferred metal type',
          options: metals,
          selected: selections.metal,
          onSelect: (value) => handleSelection('metal', value)
        };
      case 2:
        return {
          title: 'Step 2: Choose Design',
          description: 'Select your ring design style',
          options: designs,
          selected: selections.design,
          onSelect: (value) => handleSelection('design', value)
        };
      case 3:
        return {
          title: 'Step 3: Choose Diamond Shape',
          description: 'Select your diamond cut',
          options: shapes,
          selected: selections.shape,
          onSelect: (value) => handleSelection('shape', value)
        };
      case 4:
        return {
          title: 'Step 4: Choose Carat Weight',
          description: 'Select your diamond size',
          options: carats,
          selected: selections.carat,
          onSelect: (value) => handleSelection('carat', value)
        };
      default:
        return null;
    }
  };

  const getProgressStatus = () => {
    return [
      { step: 1, label: 'Metal', completed: !!selections.metal, current: currentStep === 1 },
      { step: 2, label: 'Design', completed: !!selections.design, current: currentStep === 2 },
      { step: 3, label: 'Shape', completed: !!selections.shape, current: currentStep === 3 },
      { step: 4, label: 'Carat', completed: !!selections.carat, current: currentStep === 4 }
    ];
  };

  const getCurrentDescription = () => {
    if (!selections.metal) return 'Start by selecting your metal type';
    
    let description = `Selected: ${metals.find(m => m.value === selections.metal)?.label}`;
    
    if (selections.design) {
      description += ` • ${designs.find(d => d.value === selections.design)?.label}`;
    }
    
    if (selections.shape) {
      description += ` • ${shapes.find(s => s.value === selections.shape)?.label}`;
    }
    
    if (selections.carat) {
      description += ` • ${carats.find(c => c.value === selections.carat)?.label}`;
    }
    
    return description;
  };

  const handleAddToCart = () => {
    if (selections.metal && selections.design && selections.shape && selections.carat) {
      alert('Ring added to cart! (This would integrate with your shopping cart system)');
    } else {
      alert('Please complete all customization steps first');
    }
  };

  const stepData = getCurrentStepData();

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">Ring Customizer</h1>
          <p className="text-lg text-gray-600">Create your perfect ring step by step</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Left Side - Customization Steps */}
          <div className="space-y-6">
            {/* Progress Bar */}
            <div className="bg-white rounded-lg shadow-lg p-6">
              <ProgressBar steps={getProgressStatus()} onStepClick={goToStep} />
            </div>

            {/* Current Step */}
            {stepData && (
              <div className="bg-white rounded-lg shadow-lg p-6">
                <CustomizationStep {...stepData} />
              </div>
            )}

            {/* Navigation */}
            <div className="bg-white rounded-lg shadow-lg p-6">
              <div className="flex justify-between items-center">
                <button
                  onClick={() => setCurrentStep(Math.max(1, currentStep - 1))}
                  disabled={currentStep === 1}
                  className="px-6 py-2 bg-gray-500 text-white rounded-md hover:bg-gray-600 disabled:bg-gray-300 disabled:cursor-not-allowed"
                >
                  Previous
                </button>
                
                <button
                  onClick={() => setCurrentStep(Math.min(4, currentStep + 1))}
                  disabled={currentStep === 4}
                  className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:bg-gray-300 disabled:cursor-not-allowed"
                >
                  Next
                </button>
              </div>
            </div>

            {/* Add to Cart */}
            <div className="bg-white rounded-lg shadow-lg p-6">
              <button
                onClick={handleAddToCart}
                disabled={!(selections.metal && selections.design && selections.shape && selections.carat)}
                className="w-full py-3 bg-green-600 text-white rounded-md hover:bg-green-700 disabled:bg-gray-300 disabled:cursor-not-allowed font-semibold"
              >
                Add to Cart
              </button>
            </div>
          </div>

          {/* Right Side - Ring Preview */}
          <div className="bg-white rounded-lg shadow-lg p-6">
            <RingPreview 
              imageUrl={currentImage} 
              loading={loading}
              description={getCurrentDescription()}
            />
          </div>
        </div>

        {/* Current Selections Summary */}
        <div className="mt-8 bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Your Selections</h3>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="text-center p-3 bg-gray-50 rounded-md">
              <p className="text-sm text-gray-600">Metal</p>
              <p className="font-medium">
                {selections.metal ? metals.find(m => m.value === selections.metal)?.label : 'Not selected'}
              </p>
            </div>
            <div className="text-center p-3 bg-gray-50 rounded-md">
              <p className="text-sm text-gray-600">Design</p>
              <p className="font-medium">
                {selections.design ? designs.find(d => d.value === selections.design)?.label : 'Not selected'}
              </p>
            </div>
            <div className="text-center p-3 bg-gray-50 rounded-md">
              <p className="text-sm text-gray-600">Shape</p>
              <p className="font-medium">
                {selections.shape ? shapes.find(s => s.value === selections.shape)?.label : 'Not selected'}
              </p>
            </div>
            <div className="text-center p-3 bg-gray-50 rounded-md">
              <p className="text-sm text-gray-600">Carat</p>
              <p className="font-medium">
                {selections.carat ? carats.find(c => c.value === selections.carat)?.label : 'Not selected'}
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RingCustomizer;
