import React, { useState, useEffect, useContext, createContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import RingCustomizer from './components/RingCustomizer';
import AdminPanel from './components/AdminPanel';
import Home from './pages/Home';
import Shop from './pages/Shop';
import ProductDetail from './pages/ProductDetail';
import Cart from './pages/Cart';
import Checkout from './pages/Checkout';
import Wishlist from './pages/Wishlist';
import Account from './pages/Account';
import './index.css';
import AdminLogin from './pages/AdminLogin';
import ChangePassword from './components/ChangePassword';
import Login from './pages/Login';
import SignUp from './pages/SignUp';
import AuthPage from './pages/AuthPage';

// Placeholder pages
const featuredProducts = [
  {
    id: 1,
    name: 'Classic Solitaire',
    image: 'https://images.unsplash.com/photo-1517841905240-472988babdf9?auto=format&fit=crop&w=400&q=80',
    price: 1200
  },
  {
    id: 2,
    name: 'Halo Setting',
    image: 'https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=400&q=80',
    price: 1850
  },
  {
    id: 3,
    name: 'Three Stone',
    image: 'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=400&q=80',
    price: 2100
  }
];

const Contact = () => (
  <div className="p-8 max-w-xl mx-auto">
    <h1 className="text-3xl font-bold mb-6 text-center text-yellow-700">Contact Us</h1>
    <form className="bg-white rounded-lg shadow p-6 flex flex-col gap-4 mb-8">
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">Name</label>
        <input type="text" className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-yellow-500" placeholder="Your Name" />
      </div>
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
        <input type="email" className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-yellow-500" placeholder="you@email.com" />
      </div>
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">Message</label>
        <textarea className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-yellow-500" rows={4} placeholder="How can we help you?" />
      </div>
      <button type="submit" className="bg-yellow-500 hover:bg-yellow-600 text-white font-semibold px-6 py-2 rounded-md shadow transition">Send Message</button>
    </form>
    <div className="text-center text-gray-700">
      <p className="mb-1 font-semibold">Shemesh Gold</p>
      <p>123 Gold Street, Tel Aviv, Israel</p>
      <p>+972 3-123-4567</p>
      <p className="mt-2">Email: <a href="mailto:info@shemeshgold.com" className="text-yellow-700 hover:underline">info@shemeshgold.com</a></p>
    </div>
  </div>
);

const TEAM = [
  {
    name: "Avi Shemesh",
    title: "Founder & Master Jeweler",
    funFact: "Handcrafts every prototype himself.",
    img: null, // Add image path if available
  },
  {
    name: "Leah Gold",
    title: "Customer Experience Lead",
    funFact: "Loves matching rings to stories.",
    img: null,
  },
];

const VALUES = [
  {
    icon: (
      <svg className="w-8 h-8 text-yellow-600" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M12 8c-1.657 0-3 1.343-3 3 0 2.5 3 5 3 5s3-2.5 3-5c0-1.657-1.343-3-3-3z" /><circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2" /></svg>
    ),
    title: "Ethical Sourcing",
    desc: "Responsibly sourced materials for peace of mind."
  },
  {
    icon: (
      <svg className="w-8 h-8 text-yellow-600" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M12 6v6l4 2" /><circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2" /></svg>
    ),
    title: "Craftsmanship",
    desc: "Every ring is meticulously handcrafted."
  },
  {
    icon: (
      <svg className="w-8 h-8 text-yellow-600" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M8 12l2 2 4-4" /><circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2" /></svg>
    ),
    title: "Personal Service",
    desc: "Guidance at every step of your journey."
  },
  {
    icon: (
      <svg className="w-8 h-8 text-yellow-600" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M12 8v4l3 3" /><circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2" /></svg>
    ),
    title: "Celebrating Love",
    desc: "Jewelry for life's most meaningful moments."
  },
];

const About = () => {
  const [hovered, setHovered] = useState(null);
  return (
    <div className="p-8 max-w-4xl mx-auto font-sans">
      <h1 className="text-4xl font-extrabold mb-8 text-center relative inline-block after:content-[''] after:block after:h-1 after:w-24 after:mx-auto after:bg-gradient-to-r after:from-yellow-400 after:to-yellow-700 after:rounded-full after:mt-2 text-gray-900">
        About <span className="text-yellow-700">Shemesh Gold</span>
      </h1>
      <section className="mb-12 animate-fade-in-up">
        <h2 className="text-2xl font-semibold mb-3 text-gray-800 relative inline-block after:content-[''] after:block after:h-1 after:w-12 after:mx-auto after:bg-yellow-400 after:rounded-full after:mt-1">
          Our Story
        </h2>
        <div className="bg-gradient-to-br from-yellow-50/60 to-white/90 rounded-xl shadow-lg p-6">
          <p className="text-gray-700 mb-4 leading-relaxed">Founded in the heart of Tel Aviv, Shemesh Gold is dedicated to crafting exquisite rings and fine jewelry that celebrate life's most precious moments. Our artisans blend timeless tradition with modern design, ensuring every piece is as unique as the person who wears it.</p>
          <p className="text-gray-700 leading-relaxed">We believe in ethical sourcing, exceptional craftsmanship, and personal service. Whether you're designing a custom engagement ring or selecting a gift, we're here to make your experience unforgettable.</p>
        </div>
      </section>
      <section className="mb-12 animate-fade-in-up" style={{ animationDelay: '0.1s' }}>
        <h2 className="text-2xl font-semibold mb-3 text-gray-800 relative inline-block after:content-[''] after:block after:h-1 after:w-12 after:mx-auto after:bg-yellow-400 after:rounded-full after:mt-1">
          Why Choose Us?
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6 mt-6">
          {VALUES.map((v, i) => (
            <div key={v.title} className="flex flex-col items-center bg-white/80 rounded-lg shadow-md p-5 transition-transform duration-200 hover:scale-105 hover:shadow-lg animate-fade-in-up" style={{ animationDelay: `${0.15 + i * 0.05}s` }}>
              {v.icon}
              <h3 className="mt-3 font-bold text-lg text-yellow-700">{v.title}</h3>
              <p className="text-gray-600 text-sm text-center mt-1">{v.desc}</p>
            </div>
          ))}
        </div>
      </section>
      <section className="mb-12 animate-fade-in-up" style={{ animationDelay: '0.2s' }}>
        <h2 className="text-2xl font-semibold mb-3 text-gray-800 relative inline-block after:content-[''] after:block after:h-1 after:w-12 after:mx-auto after:bg-yellow-400 after:rounded-full after:mt-1">
          Meet Our Team
        </h2>
        <div className="flex flex-col sm:flex-row gap-8 mt-6">
          {TEAM.map((member, idx) => (
            <div
              key={member.name}
              className={`flex-1 bg-white rounded-xl shadow-lg p-6 flex flex-col items-center transition-all duration-200 border-2 ${hovered === idx ? 'border-yellow-500 scale-105' : 'border-transparent'} cursor-pointer animate-fade-in-up`}
              onMouseEnter={() => setHovered(idx)}
              onMouseLeave={() => setHovered(null)}
              style={{ animationDelay: `${0.25 + idx * 0.05}s` }}
            >
              <div className="w-20 h-20 bg-gradient-to-br from-yellow-200 to-yellow-400 rounded-full mb-3 flex items-center justify-center text-2xl text-yellow-800 font-bold shadow-inner">
                {member.img ? <img src={member.img} alt={member.name} className="rounded-full w-full h-full object-cover" /> : member.name.split(' ').map(n => n[0]).join('')}
              </div>
              <h3 className="font-semibold text-lg text-gray-900">{member.name}</h3>
              <p className="text-sm text-gray-600 mb-2">{member.title}</p>
              {hovered === idx && (
                <div className="mt-2 text-xs text-yellow-700 bg-yellow-50 rounded px-2 py-1 shadow animate-fade-in">
                  {member.funFact}
                </div>
              )}
            </div>
          ))}
        </div>
      </section>
      <section className="text-center animate-fade-in-up" style={{ animationDelay: '0.3s' }}>
        <h2 className="text-xl font-semibold mb-2 text-gray-800">Ready to create your story?</h2>
        <a href="/contact" className="inline-block mt-3 px-6 py-3 bg-gradient-to-r from-yellow-400 to-yellow-600 text-white font-bold rounded-full shadow-lg hover:from-yellow-500 hover:to-yellow-700 transition-all duration-200">Contact Us</a>
      </section>
      <style>{`
        @keyframes fade-in-up {
          0% { opacity: 0; transform: translateY(30px); }
          100% { opacity: 1; transform: translateY(0); }
        }
        .animate-fade-in-up {
          animation: fade-in-up 0.7s cubic-bezier(.4,0,.2,1) both;
        }
        .animate-fade-in {
          animation: fade-in 0.4s cubic-bezier(.4,0,.2,1) both;
        }
        @keyframes fade-in {
          0% { opacity: 0; }
          100% { opacity: 1; }
        }
      `}</style>
    </div>
  );
};

const CartCount = () => {
  const { cart } = useCart();
  const totalItems = cart.reduce((sum, item) => sum + (item.qty || 1), 0);

  if (totalItems === 0) return null;

  return (
    <span className="absolute -top-2 -right-2 bg-gold text-black text-xs font-bold rounded-full w-5 h-5 flex items-center justify-center">
      {totalItems}
    </span>
  );
};

const Header = () => {
  const [open, setOpen] = React.useState(false);
  return (
    <header className="bg-black/90 shadow-lg sticky top-0 z-50 backdrop-blur-md">
      <nav className="max-w-7xl mx-auto flex items-center justify-between px-6 py-4">
        <a href="/" className="text-2xl font-extrabold text-gold tracking-wide font-serif" style={{ fontFamily: 'Playfair Display, serif' }}>Shemesh Gold</a>
        <button className="md:hidden text-gold focus:outline-none" onClick={() => setOpen(!open)} aria-label="Toggle menu">
          <svg className="w-8 h-8" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M4 6h16M4 12h16M4 18h16" /></svg>
        </button>
        <ul className={`flex-col md:flex-row md:flex gap-8 items-center font-semibold text-white text-lg font-sans transition-all duration-300 ${open ? 'flex absolute top-20 left-0 w-full bg-black/95 py-6 z-40' : 'hidden md:flex'}`} style={{ fontFamily: 'Inter, sans-serif' }}>
          {/* Main Navigation */}
          <li><a href="/" className="hover:text-gold transition-colors duration-200 px-2 py-1 rounded">Home</a></li>
          <li><a href="/shop" className="hover:text-gold transition-colors duration-200 px-2 py-1 rounded">Shop</a></li>
          <li><a href="/customize" className="hover:text-gold transition-colors duration-200 px-2 py-1 rounded">Customize</a></li>
          <li><a href="/wishlist" className="hover:text-gold transition-colors duration-200 px-2 py-1 rounded">Wishlist</a></li>

          {/* Divider */}
          <div className="hidden md:block w-px h-6 bg-gold/30"></div>

          {/* Company Info */}
          <li><a href="/about" className="hover:text-gold transition-colors duration-200 px-2 py-1 rounded">About</a></li>
          <li><a href="/contact" className="hover:text-gold transition-colors duration-200 px-2 py-1 rounded">Contact</a></li>

          {/* Divider */}
          <div className="hidden md:block w-px h-6 bg-gold/30"></div>

          {/* User Actions */}
          <li><a href="/auth" className="hover:text-gold transition-colors duration-200 px-2 py-1 rounded">Login / Sign Up</a></li>
          <li>
            <a href="/account" className="hover:text-gold transition-colors duration-200 px-2 py-1 rounded">
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
                <path strokeLinecap="round" strokeLinejoin="round" d="M17.982 18.725A7.488 7.488 0 0012 15.75a7.488 7.488 0 00-5.982 2.975m11.963 0a9 9 0 10-11.963 0m11.963 0A8.966 8.966 0 0112 21a8.966 8.966 0 01-5.982-2.275M15 9.75a3 3 0 11-6 0 3 3 0 016 0z" />
              </svg>
            </a>
          </li>
          <li>
            <a href="/cart" className="hover:text-gold transition-colors duration-200 px-2 py-1 rounded relative">
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
                <path strokeLinecap="round" strokeLinejoin="round" d="M2.25 3h1.386c.51 0 .955.343 1.087.836l.383 1.437m0 0l1.7 6.385m-.383-7.822L6.75 7.5m0 0h10.5m-10.5 0l1.7 6.385m0 0A2.25 2.25 0 0010.125 16.5h3.75a2.25 2.25 0 002.175-1.615l1.7-6.385m-10.5 0h10.5" />
              </svg>
              <CartCount />
            </a>
          </li>
        </ul>
      </nav>
