// Cart Context
const CartContext = createContext();
export const useCart = () => useContext(CartContext);

const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Listen for auth changes
  useEffect(() => {
    const { supabase } = require('./lib/supabase');

    // Get initial session
    supabase.auth.getSession().then(({ data: { session } }) => {
      setUser(session?.user ?? null);
      setLoading(false);
    });

    // Listen for auth changes
    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user ?? null);
    });

    return () => subscription.unsubscribe();
  }, []);

  // Load cart from Supabase when user changes
  useEffect(() => {
    if (!user) {
      setCart([]);
      return;
    }

    const loadCart = async () => {
      try {
        const { supabase } = require('./lib/supabase');
        const { data, error } = await supabase
          .from('cart_items')
          .select('*')
          .eq('user_id', user.id)
          .order('created_at', { ascending: true });

        if (error) {
          console.error('Error loading cart:', error);
          return;
        }

        setCart(data || []);
      } catch (error) {
        console.error('Error loading cart:', error);
      }
    };

    loadCart();
  }, [user]);

  const addToCart = async (product) => {
    if (!user) {
      alert('Please log in to add items to cart');
      return;
    }

    try {
      const { supabase } = require('./lib/supabase');
      const isCustom = product.id && product.id.startsWith('custom-');
      // Check if item already exists in cart (custom: match by id, regular: match by product_id)
      let existingItem;
      if (isCustom) {
        const { data } = await supabase
          .from('cart_items')
          .select('*')
          .eq('user_id', user.id)
          .eq('design', product.design)
          .eq('metal', product.metal)
          .eq('shape', product.shape)
          .eq('carat', product.carat)
          .single();
        existingItem = data;
      } else {
        const { data } = await supabase
          .from('cart_items')
          .select('*')
          .eq('user_id', user.id)
          .eq('product_id', product.id)
          .single();
        existingItem = data;
      }

      if (existingItem) {
        // Update quantity
        const { data, error } = await supabase
          .from('cart_items')
          .update({ qty: existingItem.qty + 1 })
          .eq('id', existingItem.id)
          .select()
          .single();

        if (error) throw error;

        setCart(prev => prev.map(item => 
          item.id === existingItem.id ? { ...item, qty: item.qty + 1 } : item
        ));
      } else {
        // Add new item
        const insertObj = {
          user_id: user.id,
          design: product.design,
          design_label: product.designLabel || product.name,
          metal: product.metal,
          metal_label: product.metalLabel || product.metal,
          shape: product.shape,
          shape_label: product.shapeLabel || product.shape,
          carat: product.carat,
          price: product.price,
          image_url: product.image,
          qty: 1
        };
        if (!isCustom) {
          insertObj.product_id = product.id;
        }
        const { data, error } = await supabase
          .from('cart_items')
          .insert(insertObj)
          .select()
          .single();

        if (error) throw error;

        setCart(prev => [...prev, data]);
      }
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Error adding item to cart');
    }
  };

  const removeFromCart = async (id) => {
    if (!user) return;

    try {
      const { supabase } = require('./lib/supabase');
      const { error } = await supabase
        .from('cart_items')
        .delete()
        .eq('id', id)
        .eq('user_id', user.id);

      if (error) throw error;

      setCart(prev => prev.filter(item => item.id !== id));
    } catch (error) {
      console.error('Error removing from cart:', error);
      alert('Error removing item from cart');
    }
  };

  const updateQuantity = async (id, qty) => {
    if (!user) return;

    if (qty <= 0) {
      removeFromCart(id);
      return;
    }

    try {
      const { supabase } = require('./lib/supabase');
      const { error } = await supabase
        .from('cart_items')
        .update({ qty })
        .eq('id', id)
        .eq('user_id', user.id);

      if (error) throw error;

      setCart(prev => prev.map(item => 
        item.id === id ? { ...item, qty } : item
      ));
    } catch (error) {
      console.error('Error updating quantity:', error);
      alert('Error updating quantity');
    }
  };

  const clearCart = async () => {
    if (!user) return;

    try {
      const { supabase } = require('./lib/supabase');
      const { error } = await supabase
        .from('cart_items')
        .delete()
        .eq('user_id', user.id);

      if (error) throw error;

      setCart([]);
    } catch (error) {
      console.error('Error clearing cart:', error);
      alert('Error clearing cart');
    }
  };

  return (
    <CartContext.Provider value={{ cart, setCart, addToCart, removeFromCart, updateQuantity, clearCart }}>
      {children}
    </CartContext.Provider>
  );
};

