import React, { useEffect, useState } from 'react';
import { supabase } from '../lib/supabase';

export default function Account() {
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState({ name: '', address: '', phone: '' });
  const [editing, setEditing] = useState(false);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const getUserAndProfile = async () => {
      const { data: { session } } = await supabase.auth.getSession();
      if (!session?.user) {
        window.location.href = '/auth';
        return;
      }
      setUser(session.user);
      const { data: profileData } = await supabase
        .from('profiles')
        .select('*')
        .eq('id', session.user.id)
        .single();
      if (profileData) setProfile(profileData);
      // Fetch orders for this user
      const { data: ordersData } = await supabase
        .from('orders')
        .select('*')
        .eq('user_id', session.user.id)
        .order('created_at', { ascending: false });
      setOrders(ordersData || []);
      setLoading(false);
    };
    getUserAndProfile();
  }, []);

  const handleUpdate = async (e) => {
    e.preventDefault();
    setMessage('');
    const { error } = await supabase
      .from('profiles')
      .update({
        name: profile.name,
        address: profile.address,
        phone: profile.phone,
      })
      .eq('id', user.id);
    if (error) setMessage(error.message);
    else setMessage('Profile updated!');
    setEditing(false);
  };

  const handleLogout = async () => {
    await supabase.auth.signOut();
    window.location.href = '/auth';
  };

  if (loading) return <div className="text-center py-12">Loading...</div>;

  return (
    <div style={{ background: '#faf6f2', minHeight: '100vh', padding: '2rem' }}>
      <div style={{
        maxWidth: 700, margin: '2rem auto', background: '#fff', borderRadius: 16, boxShadow: '0 2px 12px #0001', padding: 32
      }}>
        <h2 style={{ fontFamily: 'serif', fontWeight: 700, fontSize: 32, color: '#8d5c2c', marginBottom: 32 }}>My Account</h2>
        <div style={{
          background: '#fcf7f1', borderRadius: 12, padding: 24, marginBottom: 32, boxShadow: '0 1px 4px #0001'
        }}>
          {message && <div style={{ color: 'green', marginBottom: 12 }}>{message}</div>}
          {!editing ? (
            <>
              <div style={{ marginBottom: 8 }}><b>Name:</b> {profile.name}</div>
              <div style={{ marginBottom: 8 }}><b>Email:</b> {user.email}</div>
              <div style={{ marginBottom: 8 }}><b>Address:</b> {profile.address}</div>
              <div style={{ marginBottom: 8 }}><b>Phone:</b> {profile.phone || '-'}</div>
              <button
                style={{
                  background: '#8d5c2c', color: '#fff', border: 'none', borderRadius: 6,
                  padding: '8px 18px', fontWeight: 600, marginRight: 12, marginTop: 8, cursor: 'pointer'
                }}
                onClick={() => setEditing(true)}
              >Edit Profile</button>
              <button
                style={{
                  background: '#e74c3c', color: '#fff', border: 'none', borderRadius: 6,
                  padding: '8px 18px', fontWeight: 600, marginTop: 8, cursor: 'pointer'
                }}
                onClick={handleLogout}
              >Logout</button>
            </>
          ) : (
            <form onSubmit={handleUpdate}>
              <div style={{ marginBottom: 12 }}>
                <label style={{ fontWeight: 600 }}>Name:</label>
                <input
                  type="text"
                  value={profile.name}
                  onChange={e => setProfile({ ...profile, name: e.target.value })}
                  required
                  style={{ width: '100%', padding: 8, borderRadius: 4, border: '1px solid #ccc', marginTop: 4 }}
                />
              </div>
              <div style={{ marginBottom: 12 }}>
                <label style={{ fontWeight: 600 }}>Address:</label>
                <input
                  type="text"
                  value={profile.address}
                  onChange={e => setProfile({ ...profile, address: e.target.value })}
                  required
                  style={{ width: '100%', padding: 8, borderRadius: 4, border: '1px solid #ccc', marginTop: 4 }}
                />
              </div>
              <div style={{ marginBottom: 12 }}>
                <label style={{ fontWeight: 600 }}>Phone:</label>
                <input
                  type="text"
                  value={profile.phone || ''}
                  onChange={e => setProfile({ ...profile, phone: e.target.value })}
                  style={{ width: '100%', padding: 8, borderRadius: 4, border: '1px solid #ccc', marginTop: 4 }}
                />
              </div>
              <button
                type="submit"
                style={{
                  background: '#8d5c2c', color: '#fff', border: 'none', borderRadius: 6,
                  padding: '8px 18px', fontWeight: 600, marginRight: 12, marginTop: 8, cursor: 'pointer'
                }}
              >Save</button>
              <button
                type="button"
                style={{
                  background: '#bbb', color: '#fff', border: 'none', borderRadius: 6,
                  padding: '8px 18px', fontWeight: 600, marginTop: 8, cursor: 'pointer'
                }}
                onClick={() => setEditing(false)}
              >Cancel</button>
            </form>
          )}
        </div>
        <div style={{
          background: '#fcf7f1', borderRadius: 12, padding: 24, boxShadow: '0 1px 4px #0001'
        }}>
          <h3 style={{ fontFamily: 'serif', fontWeight: 700, fontSize: 22, color: '#8d5c2c', marginBottom: 16 }}>My Orders</h3>
          <table style={{ width: '100%', borderCollapse: 'collapse', marginBottom: 0 }}>
            <thead>
              <tr style={{ color: '#8d5c2c', fontWeight: 700, fontSize: 16 }}>
                <th align="left" style={{ padding: 8 }}>ID</th>
                <th align="left" style={{ padding: 8 }}>Date</th>
                <th align="left" style={{ padding: 8 }}>Status</th>
                <th align="left" style={{ padding: 8 }}>Items</th>
                <th align="left" style={{ padding: 8 }}>Total</th>
              </tr>
            </thead>
            <tbody>
              {orders.length === 0 ? (
                <tr>
                  <td colSpan={5} style={{ textAlign: 'center', color: '#bfa14a', padding: 16 }}>No orders yet.</td>
                </tr>
              ) : (
                orders.map(order => (
                  <tr key={order.id}>
                    <td style={{ padding: 8 }}>{order.id.slice(0, 8)}...</td>
                    <td style={{ padding: 8 }}>{new Date(order.created_at).toLocaleDateString()}</td>
                    <td style={{ padding: 8 }}>{order.status}</td>
                    <td style={{ padding: 8 }}>
                      {Array.isArray(order.items)
                        ? order.items.map((item, i) => (
                            <span key={i}>
                              {item.product} x{item.qty}
                              {i < order.items.length - 1 ? ', ' : ''}
                            </span>
                          ))
                        : '-'}
                    </td>
                    <td style={{ padding: 8 }}>${order.total?.toLocaleString()}</td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
