import React, { useState, useRef } from 'react';
import { addToCartSupabase } from '../lib/cartSupabase';

const metalColors = {
  'white-gold': '#e5e4e2',
  'yellow-gold': '#ffe066',
  'rose-gold': '#e6b7a9',
  'platinum': '#bfc1c2',
};
const metalLabels = {
  'white-gold': 'White Gold',
  'yellow-gold': 'Yellow Gold',
  'rose-gold': 'Rose Gold',
  'platinum': 'Platinum',
};
const caratOptions = [
  { value: '1.0', label: '1.0ct F-G VS+' },
  { value: '1.5', label: '1.5ct F-G VS+' },
  { value: '2.0', label: '2.0ct F-G VS+' },
  { value: '2.5', label: '2.5ct F-G VS+' },
];

const ProductCard = ({ ring }) => {
  const [selectedMetal, setSelectedMetal] = useState(ring.metal);
  const [selectedCarat, setSelectedCarat] = useState('1.0');
  const [zoom, setZoom] = useState(false);
  const [zoomPos, setZoomPos] = useState({ x: 50, y: 50 });
  const imageRef = useRef(null);
  const imageUrl = ring.images[selectedMetal] || ring.images[ring.metal] || '/placeholder-ring.png';

  // Get price based on selected carat
  const getPriceForCarat = (carat) => {
    if (!ring.priceData) return ring.price || 5000;
    
    switch (carat) {
      case '1.0':
        return ring.priceData.price_1_0ct || 5000;
      case '1.5':
        return ring.priceData.price_1_5ct || 7500;
      case '2.0':
        return ring.priceData.price_2_0ct || 10000;
      case '2.5':
        return ring.priceData.price_2_5ct || 12500;
      default:
        return ring.priceData.price_1_0ct || 5000;
    }
  };

  const currentPrice = getPriceForCarat(selectedCarat);

  // Handle mouse move for zoom
  const handleMouseMove = (e) => {
    if (!zoom || !imageRef.current) return;
    const rect = imageRef.current.getBoundingClientRect();
    const x = Math.max(0, Math.min(100, ((e.clientX - rect.left) / rect.width) * 100));
    const y = Math.max(0, Math.min(100, ((e.clientY - rect.top) / rect.height) * 100));
    setZoomPos({ x, y });
  };

  // Toggle zoom on click
  const handleImageClick = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setZoom(prevZoom => !prevZoom);
    if (!zoom) {
      handleMouseMove(e);
    }
  };

  // Close zoom when clicking outside
  const handleZoomClick = (e) => {
    e.stopPropagation();
    setZoom(false);
  };

  const handleAddToCart = async () => {
    try {
      await addToCartSupabase({
        ...ring,
        metal: selectedMetal,
        carat: selectedCarat,
        price: getPriceForCarat(selectedCarat),
        diamond_shape: ring.diamond_shape,
        id: ring.id,
        images: ring.images
      });
      alert('Added to cart!');
    } catch (e) {
      alert('You must be logged in to add to cart.');
    }
  };

  return (
    <div className="w-full max-w-4xl bg-white rounded-lg shadow-lg p-8 flex flex-col md:flex-row gap-8 items-start">
      {/* Left: Image and Metal Selector */}
      <div className="flex flex-col items-center md:items-start w-full md:w-1/2">
        <div className="w-full flex flex-col items-center relative">
          <div
            ref={imageRef}
            className="w-80 h-80 mb-4 rounded shadow-lg overflow-hidden bg-white relative cursor-zoom-in"
            style={{ position: 'relative' }}
            onMouseMove={handleMouseMove}
            onClick={handleImageClick}
            tabIndex={0}
            role="button"
            aria-label="Click to zoom product image"
            onKeyDown={(e) => {
              if (e.key === 'Enter' || e.key === ' ') {
                e.preventDefault();
                handleImageClick(e);
              }
            }}
          >
            <img
              src={imageUrl}
              alt={ring.designLabel}
              className="w-full h-full object-contain"
              draggable={false}
              style={{ pointerEvents: 'none' }}
            />
            {/* Zoom indicator */}
            {!zoom && (
              <div className="absolute top-2 right-2 bg-black bg-opacity-50 text-white text-xs px-2 py-1 rounded">
                Click to zoom
              </div>
            )}
            {/* Zoomed image overlay */}
            {zoom && (
              <div
                className="fixed z-50 border-2 border-gold rounded-lg shadow-2xl bg-white"
                style={{
                  top: '50%',
                  left: '50%',
                  transform: 'translate(-50%, -50%)',
                  width: '400px',
                  height: '400px',
                  backgroundImage: `url(${imageUrl})`,
                  backgroundRepeat: 'no-repeat',
                  backgroundSize: '200% 200%',
                  backgroundPosition: `${zoomPos.x}% ${zoomPos.y}%`,
                  boxShadow: '0 8px 32px 0 rgba(0,0,0,0.3)',
                  display: 'block',
                }}
                onClick={handleZoomClick}
                tabIndex={0}
                role="button"
                aria-label="Close zoom"
                onKeyDown={(e) => {
                  if (e.key === 'Escape') {
                    setZoom(false);
                  }
                }}
              />
            )}
            {/* Backdrop for zoom */}
            {zoom && (
              <div
                className="fixed inset-0 bg-black bg-opacity-50 z-40"
                onClick={() => setZoom(false)}
                style={{ cursor: 'pointer' }}
              />
            )}
          </div>
        </div>
        {/* Metal Selector as colored circles */}
        <div className="flex flex-col items-center mt-4">
          <span className="text-sm font-semibold mb-2">Choose Metal</span>
          <div className="flex flex-row gap-4">
            {ring.availableMetals.map(metal => (
              <button
                key={metal}
                className={`w-8 h-8 rounded-full border-2 ${selectedMetal === metal ? 'border-gold ring-2 ring-gold' : 'border-gray-300'} flex items-center justify-center transition`}
                style={{ backgroundColor: metalColors[metal] || '#eee' }}
                onClick={() => setSelectedMetal(metal)}
                aria-label={metalLabels[metal]}
              />
            ))}
          </div>
        </div>
      </div>
      {/* Right: Details */}
      <div className="flex-1 flex flex-col items-start">
        {/* Breadcrumb */}
        <div className="text-xs text-gray-500 mb-2">
          Home / ENGAGEMENT RINGS / <span className="text-black font-semibold">{ring.designLabel}</span>
        </div>
        {/* Title */}
        <h1 className="text-2xl md:text-3xl font-bold text-black mb-2 leading-tight">{ring.designLabel} Diamond Ring</h1>
        {/* SKU and Tag */}
        <div className="flex items-center gap-3 mb-2">
          <span className="text-xs text-gray-400">SKU: SH-XXXX</span>
          <span className="bg-rose-100 text-rose-700 text-xs font-semibold px-2 py-1 rounded">Natural</span>
        </div>
        {/* Price */}
        <div className="text-3xl font-bold text-black mb-4">${currentPrice.toLocaleString()}</div>
        {/* Description */}
        <p className="text-gray-700 mb-4 max-w-lg">{ring.description}</p>
        {/* Metal label */}
        <div className="mb-2 text-black"><span className="font-semibold">Metal:</span> {metalLabels[selectedMetal]}</div>
        {/* Carat Selector */}
        <div className="mb-4">
          <span className="font-semibold text-black mr-2">Carat weight:</span>
          {caratOptions.map(opt => (
            <button
              key={opt.value}
              className={`inline-block px-4 py-2 rounded-full border text-sm font-semibold mr-2 mb-2 ${selectedCarat === opt.value ? 'bg-gold text-black border-gold' : 'bg-white text-black border-gray-300 hover:bg-gold/20'}`}
              onClick={() => setSelectedCarat(opt.value)}
            >
              {opt.label}
            </button>
          ))}
        </div>
        {/* Add to Cart */}
        <button
          className="mt-4 px-8 py-3 bg-black text-gold font-bold rounded-full shadow hover:bg-gold hover:text-black transition text-lg tracking-wide border border-black focus:outline-none focus:ring-2 focus:ring-gold/40 hover:scale-105"
          onClick={handleAddToCart}
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
