import React from 'react';
import { Loader2, Heart, ShoppingCart, Eye, Sparkles, Info } from 'lucide-react';

const RingPreview = ({ imageUrl, loading, description }) => {
  return (
    <div className="w-full h-full flex flex-col">
      <h3 className="text-base font-semibold text-gray-900 mb-2 flex items-center">
        <Eye className="w-4 h-4 mr-1 text-yellow-500" />
        Ring Preview
      </h3>
      
      {/* Image Container */}
      <div className="flex-1 relative bg-gradient-to-br from-gray-50 to-gray-100 rounded-lg overflow-hidden border border-gray-200 shadow-inner min-h-0">
        {loading ? (
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="text-center">
              <Loader2 className="animate-spin h-8 w-8 text-yellow-500 mx-auto mb-2" />
              <p className="text-sm text-gray-600">Loading...</p>
            </div>
          </div>
        ) : imageUrl ? (
          <img
            src={imageUrl}
            alt="Ring Preview"
            className="w-full h-full object-contain p-4"
            onError={(e) => {
              e.target.src = '/placeholder-ring.png';
            }}
          />
        ) : (
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="text-center p-4">
              <div className="w-20 h-20 bg-gradient-to-br from-yellow-100 to-yellow-200 rounded-full mx-auto mb-2 flex items-center justify-center">
                <Sparkles className="w-8 h-8 text-yellow-500" />
              </div>
              <p className="text-gray-600 font-medium text-sm">No ring selected</p>
              <p className="text-xs text-gray-500 mt-1">Start customizing to see your ring</p>
            </div>
          </div>
        )}
      </div>

      {/* Description */}
      <div className="mt-2 bg-gradient-to-r from-gray-50 to-gray-100 rounded p-2 border border-gray-200">
        <p className="text-gray-600 text-xs leading-relaxed">
          {description || 'Begin by selecting your metal type to see your ring preview'}
        </p>
      </div>
    </div>
  );
};

export default RingPreview;
