import React, { useState, useEffect } from 'react';
import { Upload, Image, Settings, Sparkles, Gem, Zap, Check, X, Trash2, Eye, AlertCircle, ChevronDown, ChevronRight, Plus, Search, Filter, DollarSign, Edit3, Save } from 'lucide-react';
import { supabase } from '../lib/supabase';

const AdminPanel = () => {
  const [selectedMetal, setSelectedMetal] = useState('');
  const [selectedDesign, setSelectedDesign] = useState('');
  const [selectedShape, setSelectedShape] = useState('');
  const [selectedCarat, setSelectedCarat] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [uploading, setUploading] = useState(false);
  const [uploadedImages, setUploadedImages] = useState([]);
  const [message, setMessage] = useState('');
  const [expandedSections, setExpandedSections] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [filterMetal, setFilterMetal] = useState('');
  const [filterDesign, setFilterDesign] = useState('');
  const [loading, setLoading] = useState(true);
  const [deleteTarget, setDeleteTarget] = useState(null); // {id, imageUrl}
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [price, setPrice] = useState(''); // Price for new uploads
  const [editingPrice, setEditingPrice] = useState(null); // {id, price} for inline editing
  const [savingPrice, setSavingPrice] = useState(false);

  // Updated options based on your specifications
  const designs = [
    { value: 'classic-solitaire', label: 'Classic Solitaire' },
    { value: 'halo-setting', label: 'Halo Setting' },
    { value: 'vintage-antique', label: 'Vintage/Antique Style' },
    { value: 'three-stone', label: 'Three Stone (Trinity)' }
  ];

  const metals = [
    { value: 'white-gold', label: 'White Gold (14K/18K)' },
    { value: 'yellow-gold', label: 'Yellow Gold (14K/18K)' },
    { value: 'rose-gold', label: 'Rose Gold (14K/18K)' },
    { value: 'platinum', label: 'Platinum (950)' }
  ];

  const shapes = [
    { value: 'round', label: 'Round Brilliant Cut' },
    { value: 'princess', label: 'Princess Cut (Square)' },
    { value: 'emerald', label: 'Emerald Cut (Rectangular)' },
    { value: 'oval', label: 'Oval Brilliant Cut' }
  ];

  const carats = [
    { value: '1.0', label: '1.0 Carat' },
    { value: '1.5', label: '1.5 Carat' },
    { value: '2.0', label: '2.0 Carat' },
    { value: '2.5', label: '2.5 Carat' }
  ];

  useEffect(() => {
    fetchUploadedImages();
  }, []);

  const fetchUploadedImages = async () => {
    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('ring_images')
        .select('*')
        .order('metal', { ascending: true })
        .order('design', { ascending: true })
        .order('diamond_shape', { ascending: true })
        .order('carat', { ascending: true });

      if (error) throw error;
      setUploadedImages(data || []);
      console.log('Updated uploadedImages:', data); // Debug log
    } catch (error) {
      console.error('Error fetching images:', error);
      setMessage('Error fetching uploaded images: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Validate file type
      if (!file.type.startsWith('image/')) {
        setMessage('Please select an image file (PNG, JPG, etc.)');
        return;
      }

      // Validate file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setMessage('File size must be less than 5MB');
        return;
      }

      setSelectedFile(file);
      const url = URL.createObjectURL(file);
      setPreviewUrl(url);
      setMessage('');
    }
  };

  const generateImagePath = () => {
    if (!selectedDesign) return '';
    let path = `rings/${selectedDesign}`;
    if (selectedMetal) {
      path += `/${selectedMetal}`;
    }
    if (selectedShape) {
      path += `/${selectedShape}`;
    }
    if (selectedCarat) {
      path += `/${selectedCarat}ct.png`;
    } else {
      path += '/band.png';
    }
    return path;
  };

  const checkDuplicate = async () => {
    const imagePath = generateImagePath();
    if (!imagePath) return false;

    try {
      const { data, error } = await supabase
        .from('ring_images')
        .select('*')
        .eq('image_url', imagePath)
        .single();

      if (error && error.code !== 'PGRST116') {
        throw error;
      }

      return !!data;
    } catch (error) {
      console.error('Error checking duplicate:', error);
      return false;
    }
  };

  const uploadImage = async () => {
    // Validate required fields
    if (!selectedFile) {
      setMessage('Please select an image file');
      return;
    }
    if (!selectedDesign || !selectedMetal || !selectedShape) {
      setMessage('Please select all required options (design, metal, shape)');
      return;
    }
    if (!price || isNaN(parseFloat(price)) || parseFloat(price) <= 0) {
      setMessage('Please enter a valid price');
      return;
    }

    const caratValue = '1.0';
    let path = `rings/${selectedDesign}/${selectedMetal}/${selectedShape}/${caratValue}ct.png`;
    // Check for duplicate (ignore carat)
    const isDuplicate = uploadedImages.some(img =>
      img.design === selectedDesign &&
      img.metal === selectedMetal &&
      img.diamond_shape === selectedShape
    );
    if (isDuplicate) {
      setMessage('An image for this combination already exists');
      return;
    }
    setUploading(true);
    setMessage('');
    try {
      // Upload file to Supabase Storage
      const { data: uploadData, error: uploadError } = await supabase.storage
        .from('ring-images')
        .upload(path, selectedFile, {
          cacheControl: '3600',
          upsert: false
        });
      if (uploadError) {
        console.error('Storage upload error:', uploadError);
        throw new Error(`Storage error: ${uploadError.message}`);
      }
      // Get public URL
      const { data: urlData } = supabase.storage
        .from('ring-images')
        .getPublicUrl(path);
      // Save metadata to database (carat = 1.0)
      const { error: dbError } = await supabase
        .from('ring_images')
        .insert({
          metal: selectedMetal,
          design: selectedDesign,
          diamond_shape: selectedShape,
          carat: parseFloat(caratValue),
          image_url: path,
          public_url: urlData.publicUrl,
          price: parseFloat(price)
        });
      if (dbError) {
        console.error('Database error:', dbError);
        throw new Error(`Database error: ${dbError.message}`);
      }
      setMessage('✅ Image uploaded successfully!');
      setSelectedFile(null);
      setPreviewUrl('');
      setSelectedMetal('');
      setSelectedDesign('');
      setSelectedShape('');
      setSelectedCarat('');
      setPrice('');
      // Reset file input
      const fileInput = document.getElementById('file-input');
      if (fileInput) fileInput.value = '';
      // Refresh the list
      fetchUploadedImages();
    } catch (error) {
      console.error('Upload error:', error);
      setMessage('❌ Error uploading image: ' + error.message);
    } finally {
      setUploading(false);
    }
  };

  const deleteImage = async (id, imageUrl) => {
    // Remove confirm() and use modal instead
    setDeleteTarget({ id, imageUrl });
    setShowDeleteModal(true);
  };

  const handleDeleteConfirm = async () => {
    if (!deleteTarget) return;
    try {
      // Debug: log the path being deleted
      console.log('Attempting to delete from storage:', deleteTarget.imageUrl);
      const { error: storageError } = await supabase.storage
        .from('ring-images')
        .remove([deleteTarget.imageUrl]);
      if (storageError) {
        console.error('Storage deletion error:', storageError);
        throw storageError;
      }
      // Delete from database
      const { error: dbError } = await supabase
        .from('ring_images')
        .delete()
        .eq('id', deleteTarget.id);
      if (dbError) throw dbError;
      setMessage('✅ Image deleted successfully!');
      setPreviewUrl(''); // Clear preview after delete
      setSelectedFile(null); // Clear selected file after delete
      fetchUploadedImages();
    } catch (error) {
      console.error('Delete error:', error);
      setMessage('❌ Error deleting image: ' + error.message);
    } finally {
      setShowDeleteModal(false);
      setDeleteTarget(null);
    }
  };

  const handleDeleteCancel = () => {
    setShowDeleteModal(false);
    setDeleteTarget(null);
  };

  const toggleSection = (sectionKey) => {
    setExpandedSections(prev => ({
      ...prev,
      [sectionKey]: !prev[sectionKey]
    }));
  };

  const getMetalLabel = (value) => {
    return metals.find(m => m.value === value)?.label || value;
  };

  const getDesignLabel = (value) => {
    return designs.find(d => d.value === value)?.label || value;
  };

  const getShapeLabel = (value) => {
    return shapes.find(s => s.value === value)?.label || value;
  };

  // Price editing functions
  const startEditingPrice = (id, currentPrice) => {
    setEditingPrice({ id, price: currentPrice?.toString() || '' });
  };

  const cancelEditingPrice = () => {
    setEditingPrice(null);
  };

  const savePrice = async () => {
    if (!editingPrice || !editingPrice.price || isNaN(parseFloat(editingPrice.price))) {
      setMessage('Please enter a valid price');
      return;
    }

    setSavingPrice(true);
    try {
      const { error } = await supabase
        .from('ring_images')
        .update({ price: parseFloat(editingPrice.price) })
        .eq('id', editingPrice.id);

      if (error) throw error;

      setMessage('✅ Price updated successfully!');
      setEditingPrice(null);
      fetchUploadedImages(); // Refresh the list
    } catch (error) {
      console.error('Error updating price:', error);
      setMessage('❌ Error updating price: ' + error.message);
    } finally {
      setSavingPrice(false);
    }
  };

  // Filter and search images
  const filteredImages = uploadedImages.filter(image => {
    const matchesSearch = !searchTerm || 
      image.metal?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      image.design?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      image.diamond_shape?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      image.carat?.toString().includes(searchTerm) ||
      image.price?.toString().includes(searchTerm);

    const matchesMetal = !filterMetal || image.metal === filterMetal;
    const matchesDesign = !filterDesign || image.design === filterDesign;

    return matchesSearch && matchesMetal && matchesDesign;
  });

  // Group images by metal
  const groupedImages = filteredImages.reduce((acc, image) => {
    const metal = image.metal || 'unknown';
    if (!acc[metal]) {
      acc[metal] = [];
    }
    acc[metal].push(image);
    return acc;
  }, {});

  // Helper to generate all possible (design, metal, shape) combinations
  const getAllCombinations = () => {
    const combos = [];
    for (const design of designs) {
      for (const metal of metals) {
        for (const shape of shapes) {
          combos.push({
            design: design.value,
            metal: metal.value,
            shape: shape.value,
          });
        }
      }
    }
    return combos;
  };

  // Check if any image exists for this (design, metal, shape)
  const isUploaded = (combo) => {
    return uploadedImages.some(img =>
      img.design === combo.design &&
      img.metal === combo.metal &&
      img.diamond_shape === combo.shape
    );
  };

  const missingCombinations = getAllCombinations().filter(combo =>
    combo.design && combo.metal && combo.shape && !isUploaded(combo)
  );

  return (
    <div className="bg-gray-50 min-h-screen font-sans px-4 py-8">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-8">
          <div className="flex items-center justify-between mb-4">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Ring Image & Price Management</h1>
              <p className="text-gray-600 mt-1">Upload and manage ring images and prices for the customization system</p>
            </div>
            <div className="flex items-center space-x-2">
              <Settings className="w-5 h-5 text-gray-400" />
              <span className="text-sm text-gray-500">Admin Panel</span>
            </div>
          </div>

          {/* Message Display */}
          {message && (
            <div className={`p-3 rounded-md mb-4 ${
              message.includes('✅') 
                ? 'bg-green-50 border border-green-200 text-green-700' 
                : message.includes('❌') 
                ? 'bg-red-50 border border-red-200 text-red-700'
                : 'bg-blue-50 border border-blue-200 text-blue-700'
            }`}>
              {message}
            </div>
          )}
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Upload Section */}
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6" id="upload-section">
            <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
              <Upload className="w-5 h-5 mr-2 text-blue-500" />
              Upload New Image
            </h2>

            <div className="space-y-4">
              {/* Design Selection */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Design * <span className="text-red-500">Required</span>
                </label>
                <select
                  value={selectedDesign}
                  onChange={(e) => setSelectedDesign(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="">Select Design</option>
                  {designs.map(design => (
                    <option key={design.value} value={design.value}>
                      {design.label}
                    </option>
                  ))}
                </select>
              </div>

              {/* Metal Selection */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Metal * <span className="text-red-500">Required</span>
                </label>
                <select
                  value={selectedMetal}
                  onChange={(e) => setSelectedMetal(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="">Select Metal</option>
                  {metals.map(metal => (
                    <option key={metal.value} value={metal.value}>
                      {metal.label}
                    </option>
                  ))}
                </select>
              </div>

              {/* Shape Selection */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Diamond Shape * <span className="text-red-500">Required</span>
                </label>
                <select
                  value={selectedShape}
                  onChange={(e) => setSelectedShape(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="">Select Shape</option>
                  {shapes.map(shape => (
                    <option key={shape.value} value={shape.value}>
                      {shape.label}
                    </option>
                  ))}
                </select>
              </div>

              {/* Price Input */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Price (USD) * <span className="text-red-500">Required</span>
                </label>
                <div className="relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <DollarSign className="h-5 w-5 text-gray-400" />
                  </div>
                  <input
                    type="number"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    placeholder="0.00"
                    min="0"
                    step="0.01"
                    className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
                <p className="text-xs text-gray-500 mt-1">
                  Enter the price for this ring configuration
                </p>
              </div>

              {/* File Upload */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Image File * <span className="text-red-500">Required</span>
                </label>
                <input
                  id="file-input"
                  type="file"
                  accept="image/*"
                  onChange={handleFileSelect}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <p className="text-xs text-gray-500 mt-1">
                  Supported formats: PNG, JPG, WebP. Max size: 5MB
                </p>
              </div>

              {/* Preview */}
              {previewUrl && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Preview
                  </label>
                  <div className="border border-gray-300 rounded-md p-2">
                    <img
                      src={previewUrl}
                      alt="Preview"
                      className="max-w-full h-32 object-contain mx-auto"
                    />
                  </div>
                </div>
              )}

              {/* Upload Button */}
              <button
                onClick={uploadImage}
                disabled={uploading || !selectedFile || !selectedDesign || !selectedMetal || !selectedShape || !price}
                className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed flex items-center justify-center"
              >
                {uploading ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Uploading...
                  </>
                ) : (
                  <>
                    <Upload className="w-4 h-4 mr-2" />
                    Upload Image
                  </>
                )}
              </button>
            </div>
          </div>

          {/* Image Management Section */}
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
              <Image className="w-5 h-5 mr-2 text-green-500" />
              Manage Images & Prices
          </h2>

            {/* Search and Filter */}
            <div className="mb-4 space-y-2">
              <div className="flex space-x-2">
                <div className="flex-1">
                  <input
                    type="text"
                    placeholder="Search images or prices..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
                <button
                  onClick={() => {
                    setSearchTerm('');
                    setFilterMetal('');
                    setFilterDesign('');
                  }}
                  className="px-3 py-2 text-sm text-gray-600 hover:text-gray-800"
                >
                  Clear
                </button>
              </div>
              <div className="flex space-x-2">
                <select
                  value={filterMetal}
                  onChange={(e) => setFilterMetal(e.target.value)}
                  className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="">All Metals</option>
                  {metals.map(metal => (
                    <option key={metal.value} value={metal.value}>
                      {metal.label}
                    </option>
                  ))}
                </select>
                <select
                  value={filterDesign}
                  onChange={(e) => setFilterDesign(e.target.value)}
                  className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="">All Designs</option>
                  {designs.map(design => (
                    <option key={design.value} value={design.value}>
                      {design.label}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            {/* Image List */}
            <div className="max-h-96 overflow-y-auto">
              {loading ? (
                <div className="flex items-center justify-center py-8">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
                </div>
              ) : filteredImages.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  <Image className="w-12 h-12 mx-auto mb-2 text-gray-300" />
                  <p>No images found</p>
                </div>
              ) : (
                <div className="space-y-2">
                  {Object.entries(groupedImages).map(([metal, images]) => (
                    <div key={metal} className="border border-gray-200 rounded-md">
                      <button
                        onClick={() => toggleSection(metal)}
                        className="w-full px-4 py-2 bg-gray-50 hover:bg-gray-100 flex items-center justify-between text-left"
                      >
                        <span className="font-medium text-gray-700">
                          {getMetalLabel(metal)} ({images.length})
                        </span>
                        {expandedSections[metal] ? (
                          <ChevronDown className="w-4 h-4" />
                        ) : (
                          <ChevronRight className="w-4 h-4" />
                        )}
                      </button>

                      {expandedSections[metal] && (
                        <div className="p-4 space-y-2">
                          {images.map((image) => (
                            <div key={image.id} className="flex items-center justify-between p-2 bg-white border border-gray-200 rounded">
                              <div className="flex items-center space-x-3">
                                <img
                                  src={image.public_url}
                                  alt="Ring"
                                  className="w-12 h-12 object-cover rounded"
                                  onError={(e) => {
                                    e.target.src = '/placeholder-ring.png';
                                  }}
                                />
                                <div>
                                  <p className="text-sm font-medium text-gray-900">
                                    {getDesignLabel(image.design)}
                                  </p>
                                  <p className="text-xs text-gray-500">
                                    {image.diamond_shape && getShapeLabel(image.diamond_shape)}
                                    {image.carat && ` • ${image.carat}ct`}
                                  </p>
                                </div>
                              </div>
                              <div className="flex items-center space-x-2">
                                {/* Price Display/Edit */}
                                <div className="flex items-center space-x-1">
                                  {editingPrice?.id === image.id ? (
                                    <div className="flex items-center space-x-1">
                                      <div className="relative">
                                        <div className="absolute inset-y-0 left-0 pl-2 flex items-center pointer-events-none">
                                          <DollarSign className="h-4 w-4 text-gray-400" />
                                        </div>
                                        <input
                                          type="number"
                                          value={editingPrice.price}
                                          onChange={(e) => setEditingPrice({...editingPrice, price: e.target.value})}
                                          className="w-20 pl-6 pr-2 py-1 text-sm border border-gray-300 rounded focus:outline-none focus:ring-1 focus:ring-blue-500"
                                          min="0"
                                          step="0.01"
                                        />
                                      </div>
                                      <button
                                        onClick={savePrice}
                                        disabled={savingPrice}
                                        className="p-1 text-green-600 hover:text-green-800"
                                        title="Save price"
                                      >
                                        {savingPrice ? (
                                          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-green-600"></div>
                                        ) : (
                                          <Save className="w-4 h-4" />
                                        )}
                                      </button>
                                      <button
                                        onClick={cancelEditingPrice}
                                        className="p-1 text-gray-600 hover:text-gray-800"
                                        title="Cancel"
                                      >
                                        <X className="w-4 h-4" />
                                      </button>
                                    </div>
                                  ) : (
                                    <div className="flex items-center space-x-1">
                                      <span className="text-sm font-medium text-gray-900">
                                        ${image.price ? parseFloat(image.price).toLocaleString() : '—'}
                                      </span>
                                      <button
                                        onClick={() => startEditingPrice(image.id, image.price)}
                                        className="p-1 text-gray-400 hover:text-blue-500"
                                        title="Edit price"
                                      >
                                        <Edit3 className="w-4 h-4" />
                                      </button>
                                    </div>
                                  )}
                                </div>
                                <div className="flex items-center space-x-1">
                                  <button
                                    onClick={() => window.open(image.public_url, '_blank')}
                                    className="p-1 text-gray-400 hover:text-blue-500"
                                    title="View image"
                                  >
                                    <Eye className="w-4 h-4" />
                                  </button>
                                  <button
                                    onClick={() => deleteImage(image.id, image.image_url)}
                                    className="p-1 text-gray-400 hover:text-red-500"
                                    title="Delete image"
                                  >
                                    <Trash2 className="w-4 h-4" />
                                  </button>
                                </div>
                              </div>
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Statistics */}
        <div className="mt-8 bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Statistics</h3>
          <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
            <div className="text-center p-4 bg-blue-50 rounded-lg">
              <div className="text-2xl font-bold text-blue-600">{uploadedImages.length}</div>
              <div className="text-sm text-gray-600">Total Images</div>
            </div>
            <div className="text-center p-4 bg-green-50 rounded-lg">
              <div className="text-2xl font-bold text-green-600">
                {new Set(uploadedImages.map(img => img.metal)).size}
              </div>
              <div className="text-sm text-gray-600">Metals</div>
            </div>
            <div className="text-center p-4 bg-purple-50 rounded-lg">
              <div className="text-2xl font-bold text-purple-600">
                {new Set(uploadedImages.map(img => img.design)).size}
              </div>
              <div className="text-sm text-gray-600">Designs</div>
            </div>
            <div className="text-center p-4 bg-yellow-50 rounded-lg">
              <div className="text-2xl font-bold text-yellow-600">
                {new Set(uploadedImages.map(img => img.diamond_shape)).size}
              </div>
              <div className="text-sm text-gray-600">Shapes</div>
            </div>
            <div className="text-center p-4 bg-red-50 rounded-lg">
              <div className="text-2xl font-bold text-red-600">
                ${uploadedImages.reduce((sum, img) => sum + (img.price || 0), 0).toLocaleString()}
              </div>
              <div className="text-sm text-gray-600">Total Value</div>
            </div>
          </div>
        </div>

        {/* Section: Uploaded Images Grid */}
        <div className="mt-12">
          <h2 className="text-xl font-bold mb-4 text-green-700">Uploaded Images ({uploadedImages.length})</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {uploadedImages.map((img, idx) => (
              <div key={img.id || idx} className="bg-green-50 border border-green-200 rounded-lg p-4 flex flex-col items-center">
                <img
                  src={img.image_url ? supabase.storage.from('ring-images').getPublicUrl(img.image_url).data.publicUrl + `?t=${Date.now()}` : '/placeholder-ring.png'}
                  alt={`${img.design} ${img.metal} ${img.diamond_shape}`}
                  className="w-32 h-32 object-contain mb-2 rounded shadow"
                  onError={e => { e.target.src = '/placeholder-ring.png'; }}
                />
                <div className="font-semibold mb-1">{getDesignLabel(img.design)}</div>
                <div className="mb-1">{getMetalLabel(img.metal)}</div>
                <div className="mb-1">{getShapeLabel(img.diamond_shape)}</div>
                <div className="mb-2 text-lg font-bold text-green-700">
                  ${img.price ? parseFloat(img.price).toLocaleString() : '—'}
                </div>
                <button
                  className="mt-2 bg-red-500 text-white font-bold px-4 py-1 rounded hover:bg-red-700 transition"
                  onClick={() => deleteImage(img.id, img.image_url)}
                >
                  Delete
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Section: Missing Images Grid */}
        <div className="mt-12">
          <h2 className="text-xl font-bold mb-4 text-red-700">Images to Upload ({missingCombinations.length})</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {missingCombinations.map((combo, idx) => (
              <div key={idx} className="bg-red-50 border border-red-200 rounded-lg p-4 flex flex-col items-center">
                <div className="font-semibold mb-2">{designs.find(d => d.value === combo.design)?.label}</div>
                <div className="mb-1">{metals.find(m => m.value === combo.metal)?.label}</div>
                <div className="mb-1">{shapes.find(s => s.value === combo.shape)?.label}</div>
                {/* No carat shown, since not required */}
                <button
                  className="bg-gold text-black font-bold px-4 py-2 rounded hover:bg-black hover:text-gold transition"
                  onClick={() => {
                    setSelectedDesign(combo.design);
                    setSelectedMetal(combo.metal);
                    setSelectedShape(combo.shape);
                    setSelectedCarat(''); // No carat pre-selected
                    const uploadSection = document.getElementById('upload-section');
                    if (uploadSection) {
                      uploadSection.scrollIntoView({ behavior: 'smooth' });
                    }
                  }}
                >
                  Upload
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
      {/* Delete Confirmation Modal */}
      {showDeleteModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-40">
          <div className="bg-white rounded-lg shadow-lg p-6 max-w-sm w-full">
            <h3 className="text-lg font-semibold mb-2 text-gray-900 flex items-center">
              <AlertCircle className="w-5 h-5 text-red-500 mr-2" />
              Confirm Deletion
            </h3>
            <p className="text-gray-700 mb-4">Are you sure you want to delete this image? This action cannot be undone.</p>
            <div className="flex justify-end gap-2">
              <button
                onClick={handleDeleteCancel}
                className="px-4 py-2 rounded bg-gray-200 text-gray-700 hover:bg-gray-300"
              >
                Cancel
              </button>
              <button
                onClick={handleDeleteConfirm}
                className="px-4 py-2 rounded bg-red-600 text-white hover:bg-red-700"
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
