import React from "react";

const demoProducts = [
  {
    id: "solitaire-white-gold",
    name: "Classic Solitaire Diamond Ring",
    image: "https://images.unsplash.com/photo-1517841905240-472988babdf9?auto=format&fit=crop&w=400&q=80",
    price: 1200,
  },
  {
    id: "halo-yellow-gold",
    name: "Halo Setting Yellow Gold",
    image: "https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=400&q=80",
    price: 1850,
  },
  {
    id: "vintage-platinum",
    name: "Vintage Platinum Emerald Cut",
    image: "https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=400&q=80",
    price: 2100,
  },
  {
    id: "three-stone-rose-gold",
    name: "Three Stone Rose Gold Oval",
    image: "https://images.unsplash.com/photo-1522312346375-d1a52e2b99b3?auto=format&fit=crop&w=400&q=80",
    price: 1950,
  },
  {
    id: "princess-cut-white-gold",
    name: "Princess Cut White Gold",
    image: "https://images.unsplash.com/photo-1519125323398-675f0ddb6308?auto=format&fit=crop&w=400&q=80",
    price: 2300,
  },
  {
    id: "emerald-cut-platinum",
    name: "Emerald Cut Platinum Band",
    image: "https://images.unsplash.com/photo-1465101046530-73398c7f28ca?auto=format&fit=crop&w=400&q=80",
    price: 2500,
  },
  {
    id: "cushion-cut-yellow-gold",
    name: "Cushion Cut Yellow Gold",
    image: "https://images.unsplash.com/photo-1503342217505-b0a15ec3261c?auto=format&fit=crop&w=400&q=80",
    price: 1750,
  },
  {
    id: "oval-diamond-rose-gold",
    name: "Oval Diamond Rose Gold",
    image: "https://images.unsplash.com/photo-1519864600265-abb23847ef2c?auto=format&fit=crop&w=400&q=80",
    price: 2050,
  },
];

const Shop = () => (
  <div className="bg-white min-h-screen font-sans px-4 py-12">
    <h1 className="text-4xl font-serif font-bold text-black mb-10 text-center tracking-tight" style={{ fontFamily: 'Playfair Display, serif' }}>
      Shop All Rings
    </h1>
    {/* Filters (static for now) */}
    <div className="flex flex-wrap gap-4 justify-center mb-12">
      <select className="border border-gray-300 rounded px-5 py-2 text-black bg-white focus:outline-gold">
        <option>All Metals</option>
      </select>
      <select className="border border-gray-300 rounded px-5 py-2 text-black bg-white focus:outline-gold">
        <option>All Designs</option>
      </select>
      <select className="border border-gray-300 rounded px-5 py-2 text-black bg-white focus:outline-gold">
        <option>All Shapes</option>
      </select>
    </div>
    <div className="w-full border-t border-gray-200 my-12" />
    {/* Product Grid */}
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-10 max-w-6xl mx-auto">
      {demoProducts.map(product => (
        <div key={product.id} className="bg-white rounded-lg shadow p-6 border border-gray-200 flex flex-col items-center hover:shadow-lg transition-all duration-200 cursor-pointer">
          <img src={product.image} alt={product.name} className="w-28 h-28 object-cover rounded-full mb-5 border border-gray-200 shadow-inner" />
          <div className="font-serif text-lg font-bold mb-1 text-center" style={{ fontFamily: 'Playfair Display, serif' }}>{product.name}</div>
          <div className="text-gold text-base mb-3 font-semibold">${product.price.toLocaleString()}</div>
          <a href={`/product/${product.id}`} className="inline-block mt-auto px-6 py-2 bg-black text-white font-bold rounded shadow hover:bg-gold hover:text-black transition text-sm tracking-wide border border-black">View Details</a>
        </div>
      ))}
    </div>
  </div>
);

export default Shop;
