                        <div className="mb-1">{getShapeLabel(img.diamond_shape)}</div>
                        <div className="text-xs text-green-600 mb-2 text-center">
                          <div>1.0ct: ${pricing?.price_1_0ct?.toLocaleString() || '—'}</div>
                          <div>1.5ct: ${pricing?.price_1_5ct?.toLocaleString() || '—'}</div>
                          <div>2.0ct: ${pricing?.price_2_0ct?.toLocaleString() || '—'}</div>
                          <div>2.5ct: ${pricing?.price_2_5ct?.toLocaleString() || '—'}</div>
                        </div>
                        <div className="flex space-x-2">
                          <button
                            className="bg-green-500 text-white font-bold px-3 py-1 rounded hover:bg-green-700 transition text-xs"
                            onClick={() => openPriceModal(img.design)}
                          >
                            <DollarSign className="w-3 h-3 inline mr-1" />
                            Prices
                          </button>
                          <button
                            className="bg-red-500 text-white font-bold px-3 py-1 rounded hover:bg-red-700 transition text-xs"
                            onClick={() => deleteImage(img.id, img.image_url)}
                          >
                            Delete
                          </button>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>

              {/* Section: Missing Images Grid */}
              <div className="mt-12">
                <h2 className="text-xl font-bold mb-4 text-red-700">Images to Upload ({missingCombinations.length})</h2>
                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                  {missingCombinations.map((combo, idx) => (
                    <div key={idx} className="bg-red-50 border border-red-200 rounded-lg p-4 flex flex-col items-center">
                      <div className="font-semibold mb-2">{designs.find(d => d.value === combo.design)?.label}</div>
                      <div className="mb-1">{metals.find(m => m.value === combo.metal)?.label}</div>
                      <div className="mb-1">{shapes.find(s => s.value === combo.shape)?.label}</div>
                      {/* No carat shown, since not required */}
                      <button
                        className="bg-gold text-black font-bold px-4 py-2 rounded hover:bg-black hover:text-gold transition"
                        onClick={() => {
                          setSelectedDesign(combo.design);
                          setSelectedMetal(combo.metal);
                          setSelectedShape(combo.shape);
                          setSelectedCarat(''); // No carat pre-selected
                          const uploadSection = document.getElementById('upload-section');
                          if (uploadSection) {
                            uploadSection.scrollIntoView({ behavior: 'smooth' });
                          }
                        }}
                      >
                        Upload
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </>
        )}
        {activeTab === 'orders' && (
          <div className="mt-8">
            {/* Header */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-8">
              <div className="flex items-center justify-between mb-4">
                <div>
                  <h1 className="text-3xl font-bold text-gray-900">Order Management</h1>
                  <p className="text-gray-600 mt-1">View and manage customer orders</p>
                </div>
                <div className="flex items-center space-x-2">
                  <Settings className="w-5 h-5 text-gray-400" />
                  <span className="text-sm text-gray-500">Admin Panel</span>
                </div>
              </div>

              {/* Message Display */}
              {message && (
                <div className={`p-3 rounded-md mb-4 ${
                  message.includes('✅') 
                    ? 'bg-green-50 border border-green-200 text-green-700' 
                    : message.includes('❌') 
                    ? 'bg-red-50 border border-red-200 text-red-700'
                    : 'bg-blue-50 border border-blue-200 text-blue-700'
                }`}>
                  {message}
                </div>
              )}
            </div>

            {/* Orders Section */}
            <div className="overflow-x-auto">
              <table className="w-full text-left border-collapse">
                <thead>
                  <tr className="text-gold font-bold text-base">
                    <th className="py-2 px-3">ID</th>
                    <th className="py-2 px-3">Date</th>
                    <th className="py-2 px-3">Customer</th>
                    <th className="py-2 px-3">Status</th>
                    <th className="py-2 px-3">Total</th>
                    <th className="py-2 px-3">Details</th>
                  </tr>
                </thead>
                <tbody>
                  {ordersLoading ? (
                    <tr>
                      <td colSpan={6} className="py-8 text-center text-gray-500">Loading orders...</td>
                    </tr>
                  ) : orders.length === 0 ? (
                    <tr>
                      <td colSpan={6} className="py-8 text-center text-gray-500">No orders found.</td>
                    </tr>
                  ) : (
                    orders.map(order => (
                      <React.Fragment key={order.id}>
                        <tr className="border-t border-gold/10 hover:bg-gold/10 transition">
                          <td className="py-2 px-3 font-mono text-black">{order.id.slice(0, 8)}...</td>
                          <td className="py-2 px-3 text-black">{new Date(order.created_at).toLocaleDateString()}</td>
                          <td className="py-2 px-3 text-black">{order.shipping_address?.email || order.user_id}</td>
                          <td className="py-2 px-3 text-black">{order.status}</td>
                          <td className="py-2 px-3 text-black">${order.total?.toLocaleString()}</td>
                          <td className="py-2 px-3">
                            <button onClick={() => setExpandedOrderId(expandedOrderId === order.id ? null : order.id)} className="text-blue-600 underline text-sm">{expandedOrderId === order.id ? 'Hide' : 'View'}</button>
                          </td>
                        </tr>
                        {expandedOrderId === order.id && (
                          <tr>
                            <td colSpan={6} className="bg-gold/5 p-4">
                              <div>
                                <strong>Items:</strong>
                                <ul className="list-disc ml-6">
                                  {Array.isArray(order.items) ? order.items.map((item, i) => (
                                    <li key={i}>{item.product} x{item.qty} (${item.price})</li>
                                  )) : <li>-</li>}
                                </ul>
                                <div className="mt-2">
                                  <strong>Shipping Address:</strong>
                                  <div className="ml-4 text-sm">
                                    {order.shipping_address ? (
                                      <>
                                        <div>{order.shipping_address.firstName} {order.shipping_address.lastName}</div>
                                        <div>{order.shipping_address.address}</div>
                                        <div>{order.shipping_address.city}, {order.shipping_address.state} {order.shipping_address.postalCode}</div>
                                        <div>{order.shipping_address.country}</div>
                                        <div>{order.shipping_address.phone}</div>
                                      </>
                                    ) : 'N/A'}
                                  </div>
                                </div>
                                <div className="mt-2">
                                  <strong>Payment Info:</strong>
                                  <div className="ml-4 text-sm">
                                    {order.payment_info ? (
                                      <>
                                        <div>Card: **** **** **** {order.payment_info.cardLast4}</div>
                                        <div>Type: {order.payment_info.cardType}</div>
                                      </>
                                    ) : 'N/A'}
                                  </div>
                                </div>
                              </div>
                            </td>
                          </tr>
                        )}
                      </React.Fragment>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        )}
        {activeTab === 'messages' && (
          <div className="mt-8">
            {/* Header */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-8">
              <div className="flex items-center justify-between mb-4">
                <div>
                  <h1 className="text-3xl font-bold text-gray-900">Message Management</h1>
                  <p className="text-gray-600 mt-1">View and manage customer messages</p>
                </div>
                <div className="flex items-center space-x-2">
                  <Settings className="w-5 h-5 text-gray-400" />
                  <span className="text-sm text-gray-500">Admin Panel</span>
                </div>
              </div>

              {/* Message Display */}
              {message && (
                <div className={`p-3 rounded-md mb-4 ${
                  message.includes('✅') 
                    ? 'bg-green-50 border border-green-200 text-green-700' 
                    : message.includes('❌') 
                    ? 'bg-red-50 border border-red-200 text-red-700'
                    : 'bg-blue-50 border border-blue-200 text-blue-700'
                }`}>
                  {message}
                </div>
              )}
            </div>

            {/* Messages Section */}
            <div className="overflow-x-auto">
              <table className="w-full text-left border-collapse">
                <thead>
                  <tr className="text-gold font-bold text-base">
                    <th className="py-2 px-3">Name</th>
                    <th className="py-2 px-3">Email</th>
                    <th className="py-2 px-3">Date</th>
                    <th className="py-2 px-3">Message</th>
                    <th className="py-2 px-3">Status</th>
                  </tr>
                </thead>
                <tbody>
                  {messagesLoading ? (
                    <tr>
                      <td colSpan={5} className="py-8 text-center text-gray-500">Loading messages...</td>
                    </tr>
                  ) : messages.length === 0 ? (
                    <tr>
                      <td colSpan={5} className="py-8 text-center text-gray-500">No messages found.</td>
                    </tr>
                  ) : (
                    messages.map(msg => (
                      <tr key={msg.id} className="border-t border-gold/10 hover:bg-gold/10 transition">
                        <td className="py-2 px-3 text-black">{msg.name}</td>
                        <td className="py-2 px-3 text-black">{msg.email}</td>
                        <td className="py-2 px-3 text-black">{new Date(msg.created_at).toLocaleDateString()}</td>
                        <td className="py-2 px-3 text-black max-w-xs truncate" title={msg.message}>{msg.message}</td>
                        <td className="py-2 px-3 text-black">{msg.status || 'unread'}</td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        )}
        {activeTab === 'reviews' && (
          <div className="mt-8 text-center text-gray-500 text-lg">
            <Gem className="mx-auto mb-2 text-yellow-400" />
            <p>Reviews management coming soon!</p>
          </div>
        )}
        {activeTab === 'settings' && (
          <div className="mt-8 text-center text-gray-500 text-lg">
            <Settings className="mx-auto mb-2 text-gray-400" />
            <p>Settings panel coming soon!</p>
          </div>
        )}
      </div>

      {/* Delete Confirmation Modal */}
      {showDeleteModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-40">
          <div className="bg-white rounded-lg shadow-lg p-6 max-w-sm w-full">
            <h3 className="text-lg font-semibold mb-2 text-gray-900 flex items-center">
              <AlertCircle className="w-5 h-5 text-red-500 mr-2" />
              Confirm Deletion
            </h3>
            <p className="text-gray-700 mb-4">Are you sure you want to delete this image? This action cannot be undone.</p>
            <div className="flex justify-end gap-2">
              <button
                onClick={handleDeleteCancel}
                className="px-4 py-2 rounded bg-gray-200 text-gray-700 hover:bg-gray-300"
              >
                Cancel
              </button>
              <button
                onClick={handleDeleteConfirm}
                className="px-4 py-2 rounded bg-red-600 text-white hover:bg-red-700"
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Price Edit Modal */}
      {showPriceModal && editingPrices && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-40">
          <div className="bg-white rounded-lg shadow-lg p-6 max-w-md w-full">
            <h3 className="text-lg font-semibold mb-4 text-gray-900 flex items-center">
              <DollarSign className="w-5 h-5 text-green-500 mr-2" />
              Edit Prices - {getDesignLabel(editingPrices.design)}
            </h3>
            <p className="text-sm text-gray-600 mb-4">
              These prices apply to all metals and shapes for this design.
            </p>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">1.0 Carat Price</label>
                <input
                  type="number"
                  value={priceForm.price_1_0ct}
                  onChange={(e) => setPriceForm(prev => ({ ...prev, price_1_0ct: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
                  placeholder="5000.00"
                  min="0"
                  step="0.01"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">1.5 Carat Price</label>
                <input
                  type="number"
                  value={priceForm.price_1_5ct}
                  onChange={(e) => setPriceForm(prev => ({ ...prev, price_1_5ct: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
                  placeholder="7500.00"
                  min="0"
                  step="0.01"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">2.0 Carat Price</label>
                <input
                  type="number"
                  value={priceForm.price_2_0ct}
                  onChange={(e) => setPriceForm(prev => ({ ...prev, price_2_0ct: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
                  placeholder="10000.00"
                  min="0"
                  step="0.01"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">2.5 Carat Price</label>
                <input
                  type="number"
                  value={priceForm.price_2_5ct}
                  onChange={(e) => setPriceForm(prev => ({ ...prev, price_2_5ct: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
                  placeholder="12500.00"
                  min="0"
                  step="0.01"
                />
              </div>
            </div>

            <div className="flex justify-end gap-2 mt-6">
              <button
                onClick={closePriceModal}
                className="px-4 py-2 rounded bg-gray-200 text-gray-700 hover:bg-gray-300"
              >
                Cancel
              </button>
              <button
                onClick={handlePriceUpdate}
                className="px-4 py-2 rounded bg-green-600 text-white hover:bg-green-700"
              >
                Update Prices
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
