import React, { useState } from 'react';

const ProductCard = ({ ring, onAddToCart }) => {
  const [selectedMetal, setSelectedMetal] = useState(ring.metal);

  // Find the image for the selected metal
  const imageUrl = ring.images[selectedMetal] || ring.images[ring.metal] || '/placeholder-ring.png';

  return (
    <div className="bg-white rounded-lg shadow p-6 flex flex-col items-center">
      <img src={imageUrl} alt={ring.designLabel} className="w-32 h-32 object-contain mb-4" />
      <h3 className="font-bold text-lg mb-1">{ring.designLabel}</h3>
      <p className="text-gray-600 text-sm mb-2">{ring.description}</p>
      <div className="mb-3">
        <label className="mr-2 font-semibold">Metal:</label>
        <select
          value={selectedMetal}
          onChange={e => setSelectedMetal(e.target.value)}
          className="border rounded px-2 py-1"
        >
          {ring.availableMetals.map(metal => (
            <option key={metal} value={metal}>{metal}</option>
          ))}
        </select>
      </div>
      <button
        className="bg-gold text-black font-bold px-6 py-2 rounded hover:bg-black hover:text-gold transition"
        onClick={() => onAddToCart({ ...ring, metal: selectedMetal })}
      >
        Add to Cart
      </button>
    </div>
  );
};

export default ProductCard;
