import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './RingCustomizer.css';

const RingCustomizer = () => {
  const [ringData, setRingData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [priceInfo, setPriceInfo] = useState(null);
  
  // User selections
  const [selections, setSelections] = useState({
    diamondShape: null,
    ringDesign: null,
    ringMetal: null,
    diamondWeight: null
  });

  // Fetch ring data on component mount
  useEffect(() => {
    fetchRingData();
  }, []);

  // Calculate price when selections change
  useEffect(() => {
    if (selections.diamondShape && selections.ringDesign && 
        selections.ringMetal && selections.diamondWeight) {
      calculatePrice();
    }
  }, [selections]);

  const fetchRingData = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/rings');
      setRingData(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to load ring customization options');
      console.error('Error fetching ring data:', err);
    } finally {
      setLoading(false);
    }
  };

  const calculatePrice = async () => {
    try {
      const response = await axios.post('/api/calculate-price', selections);
      setPriceInfo(response.data);
    } catch (err) {
      console.error('Error calculating price:', err);
    }
  };

  const handleSelection = (category, item) => {
    setSelections(prev => ({
      ...prev,
      [category]: item.id
    }));
  };

  const isSelected = (category, itemId) => {
    return selections[category] === itemId;
  };

  const getSelectedItem = (category) => {
    if (!ringData || !selections[category]) return null;
    return ringData[category].find(item => item.id === selections[category]);
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <h2>Loading Ring Customization Options...</h2>
          <div className="spinner"></div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container">
        <div className="error">
          <h2>Error</h2>
          <p>{error}</p>
          <button className="btn" onClick={fetchRingData}>Try Again</button>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="customizer-header">
        <h1 className="section-title">Design Your Perfect Ring</h1>
        <p className="subtitle">Choose from our premium selection to create your dream ring</p>
      </div>

      <div className="customizer-layout">
        {/* Left Panel - Customization Options */}
        <div className="customization-panel">
          {/* Diamond Shape Selection */}
          <div className="card">
            <h3>💎 Diamond Shape</h3>
            <p>Select your preferred diamond cut</p>
            <div className="options-grid">
              {ringData.diamondShapes.map(shape => (
                <div
                  key={shape.id}
                  className={`option-card ${isSelected('diamondShape', shape.id) ? 'selected' : ''}`}
                  onClick={() => handleSelection('diamondShape', shape)}
                >
                  <div className="option-image">
                    <img src={shape.image} alt={shape.name} />
                  </div>
                  <div className="option-info">
                    <h4>{shape.name}</h4>
                    {shape.price > 0 && <p className="price">+${shape.price.toLocaleString()}</p>}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Ring Design Selection */}
          <div className="card">
            <h3>💍 Ring Design</h3>
            <p>Choose your ring setting style</p>
            <div className="options-grid">
              {ringData.ringDesigns.map(design => (
                <div
                  key={design.id}
                  className={`option-card ${isSelected('ringDesign', design.id) ? 'selected' : ''}`}
                  onClick={() => handleSelection('ringDesign', design)}
                >
                  <div className="option-image">
                    <img src={design.image} alt={design.name} />
                  </div>
                  <div className="option-info">
                    <h4>{design.name}</h4>
                    {design.price > 0 && <p className="price">+${design.price.toLocaleString()}</p>}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Ring Metal Selection */}
          <div className="card">
            <h3>🥇 Ring Metal</h3>
            <p>Select your preferred metal type</p>
            <div className="options-grid">
              {ringData.ringMetals.map(metal => (
                <div
                  key={metal.id}
                  className={`option-card ${isSelected('ringMetal', metal.id) ? 'selected' : ''}`}
                  onClick={() => handleSelection('ringMetal', metal)}
                >
                  <div className="option-image">
                    <img src={metal.image} alt={metal.name} />
                  </div>
                  <div className="option-info">
                    <h4>{metal.name}</h4>
                    {metal.price > 0 && <p className="price">+${metal.price.toLocaleString()}</p>}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Diamond Weight Selection */}
          <div className="card">
            <h3>⚖️ Diamond Weight</h3>
            <p>Choose your diamond carat weight</p>
            <div className="options-grid">
              {ringData.diamondWeights.map(weight => (
                <div
                  key={weight.id}
                  className={`option-card ${isSelected('diamondWeight', weight.id) ? 'selected' : ''}`}
                  onClick={() => handleSelection('diamondWeight', weight)}
                >
                  <div className="option-info">
                    <h4>{weight.name}</h4>
                    <p className="price">+${weight.price.toLocaleString()}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Right Panel - Preview and Price */}
        <div className="preview-panel">
          <div className="card preview-card">
            <h3>🎯 Your Custom Ring</h3>
            
            {/* Ring Preview */}
            <div className="ring-preview">
              {getSelectedItem('diamondShape') && (
                <div className="preview-item">
                  <img 
                    src={getSelectedItem('diamondShape').image} 
                    alt="Diamond Shape" 
                    className="preview-image"
                  />
                  <div className="preview-details">
                    <h4>Diamond Shape</h4>
                    <p>{getSelectedItem('diamondShape').name}</p>
                  </div>
                </div>
              )}
              
              {getSelectedItem('ringDesign') && (
                <div className="preview-item">
                  <img 
                    src={getSelectedItem('ringDesign').image} 
                    alt="Ring Design" 
                    className="preview-image"
                  />
                  <div className="preview-details">
                    <h4>Ring Design</h4>
                    <p>{getSelectedItem('ringDesign').name}</p>
                  </div>
                </div>
              )}
              
              {getSelectedItem('ringMetal') && (
                <div className="preview-item">
                  <img 
                    src={getSelectedItem('ringMetal').image} 
                    alt="Ring Metal" 
                    className="preview-image"
                  />
                  <div className="preview-details">
                    <h4>Ring Metal</h4>
                    <p>{getSelectedItem('ringMetal').name}</p>
                  </div>
                </div>
              )}
              
              {getSelectedItem('diamondWeight') && (
                <div className="preview-item">
                  <div className="preview-details">
                    <h4>Diamond Weight</h4>
                    <p>{getSelectedItem('diamondWeight').name}</p>
                  </div>
                </div>
              )}
            </div>

            {/* Price Information */}
            {priceInfo && (
              <div className="price-section">
                <h3>💰 Price Breakdown</h3>
                <div className="price-breakdown">
                  <div className="price-item">
                    <span>Base Price:</span>
                    <span>${priceInfo.breakdown.basePrice.toLocaleString()}</span>
                  </div>
                  <div className="price-item">
                    <span>Diamond Shape:</span>
                    <span>+${priceInfo.breakdown.diamondShape.toLocaleString()}</span>
                  </div>
                  <div className="price-item">
                    <span>Ring Design:</span>
                    <span>+${priceInfo.breakdown.ringDesign.toLocaleString()}</span>
                  </div>
                  <div className="price-item">
                    <span>Ring Metal:</span>
                    <span>+${priceInfo.breakdown.ringMetal.toLocaleString()}</span>
                  </div>
                  <div className="price-item">
                    <span>Diamond Weight:</span>
                    <span>+${priceInfo.breakdown.diamondWeight.toLocaleString()}</span>
                  </div>
                  <div className="price-item total">
                    <span>Total Price:</span>
                    <span>${priceInfo.totalPrice.toLocaleString()}</span>
                  </div>
                </div>
                
                <button className="btn btn-success" style={{ width: '100%', marginTop: '1rem' }}>
                  Add to Cart - ${priceInfo.totalPrice.toLocaleString()}
                </button>
              </div>
            )}

            {!priceInfo && (
              <div className="selection-required">
                <p>Please select all options to see pricing</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default RingCustomizer;
