import React, { useState } from "react";
import { useCart } from "../App";

const Checkout = () => {
  const { cart, setCart } = useCart();
  const [form, setForm] = useState({ name: "", address: "", city: "", postal: "", country: "", card: "", exp: "", cvv: "" });
  const [submitted, setSubmitted] = useState(false);
  const subtotal = cart.reduce((sum, item) => sum + item.price * item.qty, 0);
  const shipping = cart.length > 0 ? 50 : 0;
  const total = subtotal + shipping;

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });
  const handleSubmit = e => {
    e.preventDefault();
    if (Object.values(form).some(v => !v)) return alert("Please fill all fields.");
    setSubmitted(true);
    setCart([]);
  };

  if (submitted) {
    return (
      <div className="bg-white min-h-screen font-sans px-4 py-12 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-black mb-4">Thank you for your order!</h1>
          <p className="text-gold-dark mb-4">A confirmation email will be sent to you soon.</p>
          <a href="/shop" className="text-gold font-bold hover:underline">Continue Shopping</a>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white min-h-screen font-sans px-4 py-12">
      <h1 className="text-4xl font-serif font-bold mb-8 text-center text-black" style={{ fontFamily: 'Playfair Display, serif' }}>
        Checkout
      </h1>
      <form className="bg-white rounded-lg shadow p-8 flex flex-col gap-4 mb-8 border border-gray-200 max-w-xl mx-auto">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Name</label>
          <input type="text" className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-gold" placeholder="Your Name" />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
          <input type="email" className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-gold" placeholder="you@email.com" />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Address</label>
          <input type="text" className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-gold" placeholder="123 Gold Street" />
        </div>
        <button type="submit" className="bg-gold hover:bg-black hover:text-gold text-white font-semibold px-6 py-2 rounded-md shadow transition border border-gold">Place Order</button>
      </form>
      <div className="max-w-xl mx-auto bg-white rounded-lg shadow p-6 border border-gray-200">
        <h2 className="text-2xl font-serif font-bold mb-4 text-black" style={{ fontFamily: 'Playfair Display, serif' }}>
          Order Summary
        </h2>
        <ul className="divide-y divide-gray-200 mb-4">
          <li className="py-2 flex justify-between">
            <span>Classic Solitaire</span>
            <span className="text-gold font-semibold">$1,200</span>
          </li>
        </ul>
        <div className="text-right font-bold text-black">Total: <span className="text-gold">$1,200</span></div>
      </div>
      <div className="text-center mt-8">
        <span className="text-green-600 font-semibold">Thank you for your order!</span>
      </div>
    </div>
  );
};

export default Checkout;
