import React, { useEffect, useState } from 'react';
import { supabase } from '../lib/supabase';

export default function Account() {
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState({ name: '', surname: '', address: '' });
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const getUserAndProfile = async () => {
      const { data: { session } } = await supabase.auth.getSession();
      if (!session?.user) {
        window.location.href = '/auth';
        return;
      }
      setUser(session.user);
      // Fetch profile
      const { data, error } = await supabase
        .from('profiles')
        .select('*')
        .eq('id', session.user.id)
        .single();
      if (data) setProfile(data);
      setLoading(false);
    };
    getUserAndProfile();
  }, []);

  const handleUpdate = async (e) => {
    e.preventDefault();
    setMessage('');
    const { error } = await supabase
      .from('profiles')
      .update({
        name: profile.name,
        surname: profile.surname,
        address: profile.address,
      })
      .eq('id', user.id);
    if (error) setMessage(error.message);
    else setMessage('Profile updated!');
  };

  const handleLogout = async () => {
    await supabase.auth.signOut();
    window.location.href = '/auth';
  };

  if (loading) return <div className="text-center py-12">Loading...</div>;

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <form onSubmit={handleUpdate} className="bg-white p-8 rounded shadow-md w-full max-w-md">
        <h2 className="text-2xl font-bold mb-6 text-center">My Account</h2>
        {message && <div className="mb-4 text-green-600">{message}</div>}
        <input
          type="text"
          placeholder="Name"
          className="w-full mb-3 px-3 py-2 border rounded"
          value={profile.name}
          onChange={e => setProfile({ ...profile, name: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Surname"
          className="w-full mb-3 px-3 py-2 border rounded"
          value={profile.surname}
          onChange={e => setProfile({ ...profile, surname: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Address"
          className="w-full mb-3 px-3 py-2 border rounded"
          value={profile.address}
          onChange={e => setProfile({ ...profile, address: e.target.value })}
          required
        />
        <input
          type="email"
          className="w-full mb-6 px-3 py-2 border rounded bg-gray-100"
          value={user.email}
          disabled
        />
        <button
          type="submit"
          className="w-full bg-black text-gold font-bold py-2 rounded hover:bg-gold hover:text-black transition"
        >
          Update Profile
        </button>
        <button
          type="button"
          className="w-full mt-4 bg-red-600 text-white font-bold py-2 rounded hover:bg-red-700 transition"
          onClick={handleLogout}
        >
          Log Out
        </button>
      </form>
    </div>
  );
}
