import React, { useEffect, useState } from "react";
import { supabase } from '../lib/supabase';

const Shop = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      const { data, error } = await supabase
        .from('ring_images')
        .select('*')
        .order('design', { ascending: true })
        .order('metal', { ascending: true })
        .order('diamond_shape', { ascending: true })
        .order('carat', { ascending: true });
      if (error) {
        setError(error.message);
        setProducts([]);
      } else {
        setProducts(data || []);
      }
      setLoading(false);
    };
    fetchProducts();
  }, []);

  if (loading) return <div className="text-center py-12">Loading products...</div>;
  if (error) return <div className="text-center py-12 text-red-600">Error: {error}</div>;

  return (
    <div className="bg-white min-h-screen font-sans px-4 py-12">
      <h1 className="text-4xl font-serif font-bold text-black mb-10 text-center tracking-tight" style={{ fontFamily: 'Playfair Display, serif' }}>
        Shop All Rings
      </h1>
      <div className="w-full border-t border-gray-200 my-12" />
      {/* Product Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8 max-w-7xl mx-auto">
        {products.map((product) => {
          // Get public URL for image
          const imageUrl = product.public_url || (product.image_url
            ? supabase.storage.from('ring-images').getPublicUrl(product.image_url).data.publicUrl
            : '/placeholder-ring.png');
          return (
            <div key={product.id} className="bg-white rounded-2xl shadow-lg border border-gray-200 hover:border-gold transition-all duration-300 flex flex-col items-center group overflow-hidden relative">
              <div className="w-full h-56 flex items-center justify-center overflow-hidden rounded-t-2xl bg-gray-50">
                <img src={imageUrl} alt={product.design} className="w-full h-full object-cover transform group-hover:scale-105 group-hover:-translate-y-1 transition-transform duration-300 ease-in-out" onError={e => { e.target.src = '/placeholder-ring.png'; }} />
              </div>
              <div className="p-5 w-full flex flex-col items-center">
                <div className="font-serif text-lg font-bold mb-1 text-center text-black" style={{ fontFamily: 'Playfair Display, serif' }}>{product.design}</div>
                <div className="text-gray-700 text-sm mb-1">{product.metal} • {product.diamond_shape} • {product.carat ? `${product.carat} ct` : ''}</div>
                <div className="text-gold text-base mb-3 font-semibold">{product.price ? `$${product.price.toLocaleString()}` : ''}</div>
                <a href={`/product/${product.id}`} className="inline-block mt-auto px-6 py-2 bg-black text-gold font-bold rounded-full shadow hover:bg-gold hover:text-black transition-all duration-200 text-sm tracking-wide border border-black focus:outline-none focus:ring-2 focus:ring-gold/40 hover:scale-105">
                  View Details
                </a>
              </div>
            </div>
          );
        })}
      </div>
      <style>{`
        .text-gold { color: #bfa14a; }
        .border-gold { border-color: #bfa14a; }
        .bg-gold { background-color: #bfa14a; }
      `}</style>
    </div>
  );
};

export default Shop;
