import React from "react";
import { useCart } from "../App";

const Cart = () => {
  const { cart, removeFromCart } = useCart();
  const subtotal = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  if (cart.length === 0) {
    return (
      <div className="bg-white min-h-screen font-sans px-4 py-12 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-black mb-4">Your Cart is Empty</h1>
          <a href="/shop" className="text-gold font-bold hover:underline">Shop Rings</a>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white min-h-screen font-sans px-4 py-12">
      <h1 className="text-3xl font-bold text-black mb-8 text-center">Your Cart</h1>
      <div className="max-w-3xl mx-auto bg-white rounded-lg shadow p-6 border border-gold mb-8">
        {cart.map(item => (
          <div key={item.id} className="flex items-center justify-between border-b border-gold py-4">
            <div className="flex items-center gap-4">
              <img src={item.image} alt={item.name} className="w-16 h-16 object-cover rounded" />
              <div>
                <div className="font-semibold text-black">{item.name}</div>
                <div className="text-gold-dark text-sm">{item.metal}, {item.design}, {item.shape}</div>
                <div className="text-black text-sm">Qty: {item.qty}</div>
              </div>
            </div>
            <div className="flex flex-col items-end">
              <div className="font-bold text-black mb-2">${(item.price * item.qty).toLocaleString()}</div>
              <button onClick={() => removeFromCart(item.id)} className="px-3 py-1 bg-white border border-gold text-black rounded font-bold text-xs hover:bg-gold hover:text-black transition">Remove</button>
            </div>
          </div>
        ))}
        {/* Subtotal */}
        <div className="flex justify-end mt-6">
          <div className="text-lg font-bold text-black">Subtotal: <span className="text-gold-dark">${subtotal.toLocaleString()}</span></div>
        </div>
        {/* Checkout Button */}
        <div className="flex justify-end mt-4">
          <a href="/checkout" className="px-8 py-3 bg-black text-gold font-bold rounded-full shadow hover:bg-gold hover:text-black transition">Proceed to Checkout</a>
        </div>
      </div>
    </div>
  );
};

export default Cart;
