import React, { useState } from "react";

const mockOrders = [
  { id: "1234", total: 2499, date: "2024-06-01" },
  { id: "1233", total: 1999, date: "2024-05-15" },
];

const Account = () => {
  const [profile, setProfile] = useState({ name: "John Doe", email: "john@example.com" });
  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState(profile);

  const handleEdit = () => setEditing(true);
  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });
  const handleSave = () => { setProfile(form); setEditing(false); };

  return (
    <div className="bg-white min-h-screen font-sans px-4 py-12">
      <h1 className="text-3xl font-bold text-black mb-8 text-center">My Account</h1>
      <div className="max-w-3xl mx-auto bg-white rounded-lg shadow p-6 border border-gold mb-8">
        {/* Profile Info */}
        <div className="mb-6">
          {editing ? (
            <>
              <input className="w-full border border-gold rounded px-4 py-2 mb-2" name="name" value={form.name} onChange={handleChange} />
              <input className="w-full border border-gold rounded px-4 py-2 mb-2" name="email" value={form.email} onChange={handleChange} />
              <button className="px-4 py-2 bg-gold text-black rounded font-bold mt-2 mr-2" onClick={handleSave}>Save</button>
              <button className="px-4 py-2 bg-white border border-gold text-black rounded font-bold mt-2" onClick={() => setEditing(false)}>Cancel</button>
            </>
          ) : (
            <>
              <div className="font-semibold text-black mb-1">Name: {profile.name}</div>
              <div className="text-gold-dark text-sm mb-1">Email: {profile.email}</div>
              <button className="px-4 py-2 bg-gold text-black rounded font-bold mt-2" onClick={handleEdit}>Edit Profile</button>
            </>
          )}
        </div>
        {/* Order History */}
        <div>
          <h2 className="text-xl font-bold text-black mb-4">Order History</h2>
          <div className="border-t border-gold pt-4">
            {mockOrders.map(order => (
              <div key={order.id} className="flex justify-between mb-2 text-black">
                <span>Order #{order.id} <span className="text-xs text-gray-400">({order.date})</span></span>
                <span>${order.total.toLocaleString()}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Account;
