import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminPanel.css';

const AdminPanel = () => {
  const [ringData, setRingData] = useState(null);
  const [adminSettings, setAdminSettings] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [activeTab, setActiveTab] = useState('diamondShapes');
  const [editingItem, setEditingItem] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const [ringsResponse, settingsResponse] = await Promise.all([
        axios.get('/api/rings'),
        axios.get('/api/admin/settings')
      ]);
      setRingData(ringsResponse.data);
      setAdminSettings(settingsResponse.data);
      setError(null);
    } catch (err) {
      setError('Failed to load admin data');
      console.error('Error fetching admin data:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleImageUpload = async (file, category, itemId) => {
    try {
      const formData = new FormData();
      formData.append('image', file);
      
      const response = await axios.post('/api/admin/upload', formData);
      
      // Update the item with new image URL
      const updatedData = { ...ringData };
      const item = updatedData[category].find(item => item.id === itemId);
      if (item) {
        item.image = response.data.imageUrl;
        await updateRingData(updatedData);
      }
    } catch (err) {
      setError('Failed to upload image');
      console.error('Error uploading image:', err);
    }
  };

  const updateRingData = async (newData) => {
    try {
      await axios.put('/api/admin/rings', newData);
      setRingData(newData);
      setSuccess('Ring data updated successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError('Failed to update ring data');
      console.error('Error updating ring data:', err);
    }
  };

  const updateSettings = async (newSettings) => {
    try {
      await axios.put('/api/admin/settings', newSettings);
      setAdminSettings(newSettings);
      setSuccess('Settings updated successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError('Failed to update settings');
      console.error('Error updating settings:', err);
    }
  };

  const handleItemUpdate = (category, itemId, field, value) => {
    const updatedData = { ...ringData };
    const item = updatedData[category].find(item => item.id === itemId);
    if (item) {
      item[field] = value;
      setRingData(updatedData);
    }
  };

  const saveChanges = () => {
    updateRingData(ringData);
  };

  const handlePriceChange = (category, itemId, newPrice) => {
    handleItemUpdate(category, itemId, 'price', parseFloat(newPrice) || 0);
  };

  const handleNameChange = (category, itemId, newName) => {
    handleItemUpdate(category, itemId, 'name', newName);
  };

  const renderCategoryEditor = (category, categoryName) => {
    if (!ringData || !ringData[category]) return null;

    return (
      <div className="category-editor">
        <h3>{categoryName}</h3>
        <div className="items-grid">
          {ringData[category].map(item => (
            <div key={item.id} className="item-card">
              <div className="item-image">
                <img src={item.image || '/placeholder.jpg'} alt={item.name} />
                <div className="image-upload">
                  <label htmlFor={`image-${category}-${item.id}`} className="upload-btn">
                    üì∑ Change Image
                  </label>
                  <input
                    id={`image-${category}-${item.id}`}
                    type="file"
                    accept="image/*"
                    onChange={(e) => {
                      if (e.target.files[0]) {
                        handleImageUpload(e.target.files[0], category, item.id);
                      }
                    }}
                    style={{ display: 'none' }}
                  />
                </div>
              </div>
              
              <div className="item-details">
                <div className="form-group">
                  <label>Name:</label>
                  <input
                    type="text"
                    value={item.name}
                    onChange={(e) => handleNameChange(category, item.id, e.target.value)}
                    className="form-input"
                  />
                </div>
                
                <div className="form-group">
                  <label>Price ($):</label>
                  <input
                    type="number"
                    value={item.price || 0}
                    onChange={(e) => handlePriceChange(category, item.id, e.target.value)}
                    className="form-input"
                    min="0"
                    step="0.01"
                  />
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <h2>Loading Admin Panel...</h2>
          <div className="spinner"></div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container">
        <div className="error">
          <h2>Error</h2>
          <p>{error}</p>
          <button className="btn" onClick={fetchData}>Try Again</button>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="admin-header">
        <h1 className="section-title">Admin Panel</h1>
        <p className="subtitle">Manage ring customization options and pricing</p>
      </div>

      {success && (
        <div className="success">
          <p>{success}</p>
        </div>
      )}

      <div className="admin-layout">
        {/* Settings Panel */}
        <div className="settings-panel">
          <div className="card">
            <h3>‚öôÔ∏è General Settings</h3>
            <div className="form-group">
              <label>Base Price ($):</label>
              <input
                type="number"
                value={adminSettings?.settings?.basePrice || 0}
                onChange={(e) => {
                  const newSettings = {
                    ...adminSettings,
                    settings: {
                      ...adminSettings.settings,
                      basePrice: parseFloat(e.target.value) || 0
                    }
                  };
                  setAdminSettings(newSettings);
                }}
                className="form-input"
                min="0"
                step="0.01"
              />
            </div>
            
            <div className="form-group">
              <label>Currency:</label>
              <select
                value={adminSettings?.settings?.currency || 'USD'}
                onChange={(e) => {
                  const newSettings = {
                    ...adminSettings,
                    settings: {
                      ...adminSettings.settings,
                      currency: e.target.value
                    }
                  };
                  setAdminSettings(newSettings);
                }}
                className="form-select"
              >
                <option value="USD">USD ($)</option>
                <option value="EUR">EUR (‚Ç¨)</option>
                <option value="GBP">GBP (¬£)</option>
              </select>
            </div>
            
            <button 
              className="btn btn-success" 
              onClick={() => updateSettings(adminSettings)}
            >
              Save Settings
            </button>
          </div>
        </div>

        {/* Main Editor */}
        <div className="main-editor">
          {/* Tab Navigation */}
          <div className="tab-navigation">
            <button
              className={`tab-btn ${activeTab === 'diamondShapes' ? 'active' : ''}`}
              onClick={() => setActiveTab('diamondShapes')}
            >
              üíé Diamond Shapes
            </button>
            <button
              className={`tab-btn ${activeTab === 'ringDesigns' ? 'active' : ''}`}
              onClick={() => setActiveTab('ringDesigns')}
            >
              üíç Ring Designs
            </button>
            <button
              className={`tab-btn ${activeTab === 'ringMetals' ? 'active' : ''}`}
              onClick={() => setActiveTab('ringMetals')}
            >
              ü•á Ring Metals
            </button>
            <button
              className={`tab-btn ${activeTab === 'diamondWeights' ? 'active' : ''}`}
              onClick={() => setActiveTab('diamondWeights')}
            >
              ‚öñÔ∏è Diamond Weights
            </button>
          </div>

          {/* Tab Content */}
          <div className="tab-content">
            {activeTab === 'diamondShapes' && renderCategoryEditor('diamondShapes', 'Diamond Shapes')}
            {activeTab === 'ringDesigns' && renderCategoryEditor('ringDesigns', 'Ring Designs')}
            {activeTab === 'ringMetals' && renderCategoryEditor('ringMetals', 'Ring Metals')}
            {activeTab === 'diamondWeights' && renderCategoryEditor('diamondWeights', 'Diamond Weights')}
          </div>

          {/* Save Button */}
          <div className="save-section">
            <button className="btn btn-success" onClick={saveChanges}>
              üíæ Save All Changes
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
