import React from 'react';
import { Loader2, Heart, ShoppingCart } from 'lucide-react';

const RingPreview = ({ imageUrl, loading, description }) => {
  return (
    <div className="w-full">
      <h3 className="text-xl font-semibold text-gray-900 mb-4">Ring Preview</h3>
      
      {/* Image Container */}
      <div className="relative aspect-square bg-gradient-to-br from-gray-50 to-gray-100 rounded-lg overflow-hidden mb-4">
        {loading ? (
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="text-center">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
              <p className="text-gray-600">Loading ring image...</p>
            </div>
          </div>
        ) : imageUrl ? (
          <img
            src={imageUrl}
            alt="Ring Preview"
            className="w-full h-full object-contain"
            onError={(e) => {
              e.target.src = '/placeholder-ring.png';
            }}
          />
        ) : (
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="text-center">
              <div className="w-24 h-24 bg-gray-200 rounded-full mx-auto mb-4 flex items-center justify-center">
                <svg className="w-12 h-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                </svg>
              </div>
              <p className="text-gray-500">No ring selected</p>
              <p className="text-sm text-gray-400">Start customizing to see your ring</p>
            </div>
          </div>
        )}
      </div>

      {/* Description */}
      <div className="bg-gray-50 rounded-lg p-4">
        <h4 className="font-medium text-gray-900 mb-2">Current Selection</h4>
        <p className="text-gray-600 text-sm leading-relaxed">
          {description || 'Begin by selecting your metal type to see your ring preview'}
        </p>
      </div>

      {/* Image Path Info */}
      {imageUrl && !loading && (
        <div className="mt-4 p-3 bg-blue-50 border border-blue-200 rounded-lg">
          <h4 className="font-medium text-blue-900 mb-1">Image Path</h4>
          <p className="text-xs text-blue-700 font-mono break-all">
            {imageUrl.includes('placeholder') ? 'Using placeholder image' : imageUrl}
          </p>
        </div>
      )}

      {/* Instructions */}
      <div className="mt-6 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
        <h4 className="font-medium text-yellow-900 mb-2">How it works</h4>
        <ul className="text-sm text-yellow-800 space-y-1">
          <li>• Select metal to see the basic ring band</li>
          <li>• Choose design to see the ring style</li>
          <li>• Pick diamond shape to see the setting</li>
          <li>• Select carat weight for the final ring</li>
        </ul>
      </div>
    </div>
  );
};

export default RingPreview;
