import React from 'react';
import { Check } from 'lucide-react';

const CustomizationStep = ({ options, selectedValue, onSelect, stepType }) => {
  return (
    <div className="grid grid-cols-2 gap-3">
      {options.map((option) => (
        <div
          key={option.id}
          onClick={() => onSelect(option.id)}
          className={`option-card card p-3 border-2 transition-all duration-200 ${
            selectedValue === option.id ? 'selected' : 'border-gray-200 hover:border-gray-300'
          }`}
        >
          {/* Option Image */}
          <div className="aspect-square bg-gradient-to-br from-gray-50 to-gray-100 rounded-lg overflow-hidden mb-3">
            <img
              src={option.image}
              alt={option.name}
              className="w-full h-full object-cover"
              onError={(e) => {
                e.target.src = '/placeholder-option.png';
              }}
            />
          </div>

          {/* Option Details */}
          <div className="space-y-2">
            <div className="flex items-start justify-between">
              <h4 className="font-semibold text-gray-900 text-sm">{option.name}</h4>
              {selectedValue === option.id && (
                <Check className="text-primary-500 flex-shrink-0 ml-2" size={20} />
              )}
            </div>
            
            <p className="text-gray-600 text-xs leading-relaxed">
              {option.description}
            </p>
            
            {option.price > 0 && (
              <div className="text-primary-600 font-medium text-sm">
                +${option.price.toLocaleString()}
              </div>
            )}
          </div>

          {/* Selection Indicator */}
          {selectedValue === option.id && (
            <div className="absolute top-2 right-2 w-6 h-6 bg-primary-500 rounded-full flex items-center justify-center">
              <Check className="text-white" size={14} />
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default CustomizationStep;
