import { Auth } from '@supabase/auth-ui-react';
import { ThemeSupa } from '@supabase/auth-ui-shared';
import { supabase } from '../lib/supabase';
import { useEffect, useState } from 'react';

export default function Account() {
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState({ full_name: '', phone: '', address: '', city: '', postal_code: '', country: '' });
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    supabase.auth.getUser().then(({ data }) => {
      setUser(data.user);
      if (data.user) fetchProfile(data.user.id);
      else setLoading(false);
    });
    // Listen for login/logout
    const { data: listener } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user ?? null);
      if (session?.user) fetchProfile(session.user.id);
      else setProfile({ full_name: '', phone: '', address: '', city: '', postal_code: '', country: '' });
    });
    return () => listener?.subscription.unsubscribe();
  }, []);

  async function fetchProfile(userId) {
    setLoading(true);
    const { data, error } = await supabase
      .from('customer_profiles')
      .select('*')
      .eq('id', userId)
      .single();
    if (data) setProfile(data);
    setLoading(false);
  }

  async function handleSave(e) {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    const { error } = await supabase
      .from('customer_profiles')
      .upsert({ id: user.id, ...profile });
    if (error) setMessage('Error saving profile');
    else setMessage('Profile updated!');
    setLoading(false);
    setEditing(false);
  }

  if (!user) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-white">
        <div className="w-full max-w-md p-8 bg-white rounded shadow border border-gray-200">
          <h1 className="text-2xl font-bold mb-6 text-center text-black">Sign In / Register</h1>
          <Auth
            supabaseClient={supabase}
            appearance={{ theme: ThemeSupa }}
            providers={['google', 'github', 'email']}
            magicLink
          />
        </div>
      </div>
    );
  }

  if (loading) {
    return <div className="flex justify-center items-center min-h-screen">Loading...</div>;
  }

  return (
    <div className="flex justify-center items-center min-h-screen bg-white">
      <div className="w-full max-w-md p-8 bg-white rounded shadow border border-gray-200">
        <h1 className="text-2xl font-bold mb-6 text-center text-black">My Profile</h1>
        {message && <div className="mb-4 text-green-600 text-center">{message}</div>}
        <form onSubmit={handleSave} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Full Name</label>
            <input type="text" className="w-full px-3 py-2 border border-gray-300 rounded-md" value={profile.full_name || ''} onChange={e => setProfile(p => ({ ...p, full_name: e.target.value }))} disabled={!editing} />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Phone</label>
            <input type="text" className="w-full px-3 py-2 border border-gray-300 rounded-md" value={profile.phone || ''} onChange={e => setProfile(p => ({ ...p, phone: e.target.value }))} disabled={!editing} />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Address</label>
            <input type="text" className="w-full px-3 py-2 border border-gray-300 rounded-md" value={profile.address || ''} onChange={e => setProfile(p => ({ ...p, address: e.target.value }))} disabled={!editing} />
          </div>
          <div className="flex gap-2">
            <div className="flex-1">
              <label className="block text-sm font-medium text-gray-700 mb-1">City</label>
              <input type="text" className="w-full px-3 py-2 border border-gray-300 rounded-md" value={profile.city || ''} onChange={e => setProfile(p => ({ ...p, city: e.target.value }))} disabled={!editing} />
            </div>
            <div className="flex-1">
              <label className="block text-sm font-medium text-gray-700 mb-1">Postal Code</label>
              <input type="text" className="w-full px-3 py-2 border border-gray-300 rounded-md" value={profile.postal_code || ''} onChange={e => setProfile(p => ({ ...p, postal_code: e.target.value }))} disabled={!editing} />
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Country</label>
            <input type="text" className="w-full px-3 py-2 border border-gray-300 rounded-md" value={profile.country || ''} onChange={e => setProfile(p => ({ ...p, country: e.target.value }))} disabled={!editing} />
          </div>
          <div className="flex gap-2 mt-4">
            {!editing ? (
              <button type="button" className="w-full bg-gold text-black font-bold py-2 rounded" onClick={() => setEditing(true)}>Edit</button>
            ) : (
              <>
                <button type="submit" className="w-full bg-black text-gold font-bold py-2 rounded">Save</button>
                <button type="button" className="w-full bg-gray-200 text-black font-bold py-2 rounded" onClick={() => { setEditing(false); fetchProfile(user.id); }}>Cancel</button>
              </>
            )}
          </div>
        </form>
      </div>
    </div>
  );
}
