import React, { useEffect, useState } from "react";
import { fetchCartSupabase, removeFromCartSupabase, clearCartSupabase } from "../lib/cartSupabase";

const Cart = () => {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    fetchCartSupabase().then(setCart);
  }, []);

  const handleRemove = async (id) => {
    await removeFromCartSupabase(id);
    setCart(await fetchCartSupabase());
  };

  const handleClear = async () => {
    await clearCartSupabase();
    setCart([]);
  };

  const subtotal = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  if (cart.length === 0) {
    return (
      <div className="bg-white min-h-screen font-sans px-4 py-12 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-black mb-4">Your Cart is Empty</h1>
          <a href="/shop" className="text-gold font-bold hover:underline">Shop Rings</a>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white min-h-screen font-sans px-4 py-12">
      <h1 className="text-4xl font-serif font-bold mb-8 text-center text-black" style={{ fontFamily: 'Playfair Display, serif' }}>
        My Cart
      </h1>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8 max-w-4xl mx-auto">
        {cart.map(item => (
          <div key={item.id} className="bg-white rounded-lg shadow p-6 border border-gray-200 flex flex-col items-center">
            <img src={item.image_url || '/placeholder-ring.png'} alt="Cart Ring" className="w-20 h-20 object-cover rounded-full" />
            <div className="font-serif text-lg font-bold mb-1 text-center" style={{ fontFamily: 'Playfair Display, serif' }}>{item.design}</div>
            <div className="text-gold text-base mb-3 font-semibold">${(item.price * item.quantity).toLocaleString()}</div>
            <button onClick={() => handleRemove(item.id)} className="bg-black text-white font-bold rounded shadow hover:bg-gold hover:text-black transition text-sm tracking-wide border border-black px-6 py-2 mt-2">Remove</button>
          </div>
        ))}
      </div>
      <div className="w-full border-t border-gray-200 my-12" />
      <div className="max-w-2xl mx-auto text-right">
        <span className="font-bold text-lg text-black">Subtotal: </span>
        <span className="font-bold text-lg text-gold">${subtotal.toLocaleString()}</span>
      </div>
      <div className="text-center mt-8">
        <button className="bg-gold hover:bg-black hover:text-gold text-white font-semibold px-8 py-3 rounded-md shadow transition border border-gold" onClick={handleClear}>Clear Cart</button>
      </div>
    </div>
  );
};

export default Cart;
