import React from 'react';
import { Check } from 'lucide-react';

const CustomizationStep = ({ title, description, options, selected, onSelect }) => {
  return (
    <div className="w-full h-full flex flex-col">
      <div className="mb-2">
        <h3 className="text-sm font-semibold text-gray-900 mb-0.5">{title}</h3>
        <p className="text-xs text-gray-600">{description}</p>
      </div>

      <div className="flex-1 grid grid-cols-2 gap-1.5">
        {options.map((option) => {
          return (
            <button
              key={option.value}
              onClick={() => onSelect(option.value)}
              className={`p-1.5 rounded-md border-2 transition-all duration-200 text-left group hover:scale-102 ${
                selected === option.value
                  ? 'border-yellow-500 bg-yellow-50 shadow-sm ring-1 ring-yellow-500/30'
                  : 'border-gray-200 bg-gray-50 hover:border-gray-300 hover:shadow-sm'
              }`}
            >
              <div className="flex items-center space-x-2">
                {/* Image */}
                <div className={`flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center transition-all duration-200 overflow-hidden ${
                  selected === option.value 
                    ? 'ring-2 ring-yellow-500 shadow-md scale-110' 
                    : 'ring-1 ring-gray-200 hover:ring-gray-300'
                }`}>
                  {option.image ? (
                    <img 
                      src={option.image} 
                      alt={option.label}
                      className="w-full h-full object-cover"
                      onError={(e) => {
                        e.target.style.display = 'none';
                        e.target.nextSibling.style.display = 'flex';
                      }}
                    />
                  ) : null}
                  {option.icon && (
                    <div className={`w-full h-full flex items-center justify-center ${
                      selected === option.value 
                        ? 'bg-yellow-500 text-black' 
                        : `bg-gray-100 ${option.color || 'text-gray-600'}`
                    }`}>
                      <option.icon className="w-4 h-4" />
                    </div>
                  )}
                </div>

                {/* Content */}
                <div className="flex-1 min-w-0">
                  <h4 className="font-semibold text-gray-900 text-xs truncate">{option.label}</h4>
                  {option.sublabel && (
                    <p className="text-xs text-gray-500 truncate mt-0.5">{option.sublabel}</p>
                  )}
                </div>

                {/* Check mark */}
                {selected === option.value && (
                  <div className="flex-shrink-0">
                    <div className="w-4 h-4 bg-yellow-500 rounded-full flex items-center justify-center">
                      <Check className="w-2 h-2 text-white" />
                    </div>
                  </div>
                )}
              </div>
            </button>
          );
        })}
      </div>

      {selected && (
        <div className="mt-1 p-1 bg-yellow-50 border border-yellow-200 rounded text-xs">
          <div className="flex items-center">
            <Check className="w-2 h-2 text-yellow-500 mr-1" />
            <span className="text-yellow-700 font-medium">
              {options.find(opt => opt.value === selected)?.label} selected
            </span>
          </div>
        </div>
      )}
    </div>
  );
};

export default CustomizationStep;
