import React from 'react';
import { Check, Crown, Heart, Diamond, Star } from 'lucide-react';

const ProgressBar = ({ steps, onStepClick }) => {
  const stepIcons = [Crown, Heart, Diamond, Star];
  
  return (
    <div className="w-full">
      <h3 className="text-base font-semibold text-gray-900 mb-3 flex items-center">
        <div className="w-2 h-2 bg-blue-500 rounded-full mr-2"></div>
        Customization Progress
      </h3>
      
      <div className="flex items-center justify-between mb-4">
        {steps.map((step, index) => {
          const IconComponent = stepIcons[index];
          return (
            <React.Fragment key={step.step}>
              {/* Step Circle */}
              <div className="flex flex-col items-center">
                <button
                  onClick={() => onStepClick(step.step)}
                  className={`w-10 h-10 rounded-full flex items-center justify-center text-xs font-semibold transition-all duration-200 hover:scale-110 ${
                    step.completed
                      ? 'bg-gradient-to-r from-green-500 to-emerald-500 text-white shadow-lg'
                      : step.current
                      ? 'bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow-lg'
                      : 'bg-gray-100 text-gray-500 hover:bg-gray-200 border-2 border-gray-200'
                  }`}
                >
                  {step.completed ? (
                    <Check className="w-4 h-4" />
                  ) : (
                    <IconComponent className="w-4 h-4" />
                  )}
                </button>
                
                {/* Step Label */}
                <span className={`text-xs mt-1 font-medium ${
                  step.completed
                    ? 'text-green-600'
                    : step.current
                    ? 'text-blue-600'
                    : 'text-gray-500'
                }`}>
                  {step.label}
                </span>
              </div>
              
              {/* Connector Line */}
              {index < steps.length - 1 && (
                <div className={`flex-1 h-0.5 mx-2 rounded-full transition-all duration-300 ${
                  steps[index + 1].completed || steps[index + 1].current
                    ? 'bg-gradient-to-r from-green-400 to-blue-400'
                    : 'bg-gray-200'
                }`} />
              )}
            </React.Fragment>
          );
        })}
      </div>
      
      {/* Progress Percentage */}
      <div className="w-full bg-gray-100 rounded-full h-1.5 overflow-hidden">
        <div 
          className="bg-gradient-to-r from-green-400 via-blue-400 to-purple-400 h-1.5 rounded-full transition-all duration-500 ease-out"
          style={{ 
            width: `${(steps.filter(s => s.completed).length / steps.length) * 100}%` 
          }}
        />
      </div>
      
      <div className="flex justify-between text-xs text-gray-500 mt-2">
        <span className="font-medium">Step {steps.filter(s => s.completed).length} of {steps.length}</span>
        <span className="font-medium">{Math.round((steps.filter(s => s.completed).length / steps.length) * 100)}% Complete</span>
      </div>
    </div>
  );
};

export default ProgressBar;
