import React, { useState, useEffect } from 'react';
import { ChevronRight, ChevronLeft, Check, Sparkles, Gem, Settings, Zap } from 'lucide-react';
import { getProgressiveRingImage } from '../lib/supabase';
import { ringOptions, basePrice } from '../data/ringOptions';
import RingPreview from './RingPreview';
import CustomizationStep from './CustomizationStep';
import ProgressBar from './ProgressBar';

const RingCustomizer = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [selections, setSelections] = useState({
    metal: null,
    design: null,
    shape: null,
    carat: null
  });
  const [ringImage, setRingImage] = useState('/placeholder-ring.png');
  const [isLoading, setIsLoading] = useState(false);
  const [totalPrice, setTotalPrice] = useState(basePrice);

  const steps = [
    { id: 1, title: 'Choose Metal', icon: Sparkles, description: 'Select your preferred metal type' },
    { id: 2, title: 'Select Design', icon: Settings, description: 'Pick your ring setting style' },
    { id: 3, title: 'Diamond Shape', icon: Gem, description: 'Choose your diamond cut' },
    { id: 4, title: 'Carat Weight', icon: Zap, description: 'Select diamond size' }
  ];

  // Update ring image when selections change
  useEffect(() => {
    const updateRingImage = async () => {
      setIsLoading(true);
      try {
        const imageUrl = await getProgressiveRingImage(selections);
        setRingImage(imageUrl);
      } catch (error) {
        console.error('Error updating ring image:', error);
        setRingImage('/placeholder-ring.png');
      } finally {
        setIsLoading(false);
      }
    };

    updateRingImage();
  }, [selections]);

  // Calculate total price
  useEffect(() => {
    let total = basePrice;
    
    if (selections.metal) {
      const metal = ringOptions.metals.find(m => m.id === selections.metal);
      total += metal?.price || 0;
    }
    
    if (selections.design) {
      const design = ringOptions.designs.find(d => d.id === selections.design);
      total += design?.price || 0;
    }
    
    if (selections.shape) {
      const shape = ringOptions.shapes.find(s => s.id === selections.shape);
      total += shape?.price || 0;
    }
    
    if (selections.carat) {
      const carat = ringOptions.carats.find(c => c.id === selections.carat);
      total += carat?.price || 0;
    }
    
    setTotalPrice(total);
  }, [selections]);

  const handleSelection = (stepType, value) => {
    setSelections(prev => ({
      ...prev,
      [stepType]: value
    }));
    
    // Auto-advance to next step if not the last step
    if (currentStep < 4) {
      setTimeout(() => setCurrentStep(currentStep + 1), 500);
    }
  };

  const goToStep = (step) => {
    if (step <= currentStep || isStepAccessible(step)) {
      setCurrentStep(step);
    }
  };

  const isStepAccessible = (step) => {
    switch (step) {
      case 1: return true;
      case 2: return selections.metal !== null;
      case 3: return selections.metal !== null && selections.design !== null;
      case 4: return selections.metal !== null && selections.design !== null && selections.shape !== null;
      default: return false;
    }
  };

  const isStepCompleted = (step) => {
    switch (step) {
      case 1: return selections.metal !== null;
      case 2: return selections.design !== null;
      case 3: return selections.shape !== null;
      case 4: return selections.carat !== null;
      default: return false;
    }
  };

  const getStepOptions = (step) => {
    switch (step) {
      case 1: return ringOptions.metals;
      case 2: return ringOptions.designs;
      case 3: return ringOptions.shapes;
      case 4: return ringOptions.carats;
      default: return [];
    }
  };

  const getStepType = (step) => {
    switch (step) {
      case 1: return 'metal';
      case 2: return 'design';
      case 3: return 'shape';
      case 4: return 'carat';
      default: return '';
    }
  };

  const getCurrentSelection = (step) => {
    const stepType = getStepType(step);
    return selections[stepType];
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-soft border-b border-gray-100">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div>
              <h1 className="text-3xl font-serif font-semibold text-gray-900">
                Ring Customizer
              </h1>
              <p className="text-gray-600 mt-1">
                Design your perfect engagement ring
              </p>
            </div>
            <div className="text-right">
              <div className="text-2xl font-serif font-semibold text-primary-600">
                ${totalPrice.toLocaleString()}
              </div>
              <div className="text-sm text-gray-500">Total Price</div>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-5 gap-8">
          {/* Left Column - Customization Steps */}
          <div className="lg:col-span-2">
            {/* Progress Bar */}
            <ProgressBar currentStep={currentStep} totalSteps={4} />
            
            {/* Steps */}
            <div className="space-y-4 mt-8">
              {steps.map((step) => (
                <div
                  key={step.id}
                  className={`card p-4 transition-all duration-300 ${
                    currentStep === step.id ? 'step-active' : ''
                  } ${isStepCompleted(step.id) ? 'step-completed' : ''}`}
                >
                  <div className="flex items-center mb-4">
                    <div className={`w-8 h-8 rounded-full flex items-center justify-center mr-3 ${
                      isStepCompleted(step.id) 
                        ? 'bg-green-500 text-white' 
                        : currentStep === step.id 
                        ? 'bg-primary-500 text-white' 
                        : 'bg-gray-200 text-gray-600'
                    }`}>
                      {isStepCompleted(step.id) ? (
                        <Check size={16} />
                      ) : (
                        <step.icon size={16} />
                      )}
                    </div>
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">
                        {step.title}
                      </h3>
                      <p className="text-gray-600 text-sm">{step.description}</p>
                    </div>
                  </div>

                  {currentStep === step.id && (
                    <CustomizationStep
                      options={getStepOptions(step.id)}
                      selectedValue={getCurrentSelection(step.id)}
                      onSelect={(value) => handleSelection(getStepType(step.id), value)}
                      stepType={getStepType(step.id)}
                    />
                  )}

                  {isStepCompleted(step.id) && currentStep !== step.id && (
                    <div className="flex items-center justify-between p-4 bg-green-50 rounded-lg border border-green-200">
                      <div className="flex items-center">
                        <Check className="text-green-600 mr-2" size={20} />
                        <span className="text-green-800 font-medium">
                          {getStepOptions(step.id).find(opt => opt.id === getCurrentSelection(step.id))?.name}
                        </span>
                      </div>
                      <button
                        onClick={() => goToStep(step.id)}
                        className="text-green-600 hover:text-green-800 text-sm font-medium"
                      >
                        Change
                      </button>
                    </div>
                  )}
                </div>
              ))}
            </div>

            {/* Navigation */}
            <div className="flex justify-between mt-8">
              <button
                onClick={() => setCurrentStep(Math.max(1, currentStep - 1))}
                disabled={currentStep === 1}
                className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
              >
                <ChevronLeft size={20} className="mr-2" />
                Previous
              </button>
              
              <button
                onClick={() => setCurrentStep(Math.min(4, currentStep + 1))}
                disabled={currentStep === 4 || !isStepAccessible(currentStep + 1)}
                className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
              >
                Next
                <ChevronRight size={20} className="ml-2" />
              </button>
            </div>
          </div>

          {/* Right Column - Ring Preview */}
          <div className="lg:col-span-3">
            <div className="sticky top-8">
              <RingPreview
                imageUrl={ringImage}
                isLoading={isLoading}
                selections={selections}
                totalPrice={totalPrice}
                ringOptions={ringOptions}
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RingCustomizer;
