import React, { useState, useEffect } from 'react';
import { Upload, Image, Settings, Sparkles, Gem, Zap, Check, X } from 'lucide-react';
import { ringOptions } from '../data/ringOptions';

const AdminPanel = () => {
  const [selectedLayer, setSelectedLayer] = useState('');
  const [selectedOption, setSelectedOption] = useState('');
  const [uploadedImages, setUploadedImages] = useState({});
  const [isUploading, setIsUploading] = useState(false);
  const [uploadStatus, setUploadStatus] = useState('');

  const layerTypes = [
    { id: 'base', name: 'Base Ring', icon: Image, description: 'Plain ring without customization' },
    { id: 'metals', name: 'Metal Overlay', icon: Sparkles, description: 'Ring color/material' },
    { id: 'designs', name: 'Design Overlay', icon: Settings, description: 'Ring setting style' },
    { id: 'shapes', name: 'Diamond Overlay', icon: Gem, description: 'Diamond shape' },
    { id: 'carats', name: 'Weight Overlay', icon: Zap, description: 'Diamond size' }
  ];

  const getLayerOptions = (layerType) => {
    switch (layerType) {
      case 'metals': return ringOptions.metals;
      case 'designs': return ringOptions.designs;
      case 'shapes': return ringOptions.shapes;
      case 'carats': return ringOptions.carats;
      default: return [];
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file || !selectedLayer || (selectedLayer !== 'base' && !selectedOption)) {
      setUploadStatus('Please select a layer type and option first');
      return;
    }

    setIsUploading(true);
    setUploadStatus('Uploading...');

    try {
      const formData = new FormData();
      formData.append('image', file);
      formData.append('layerType', selectedLayer);
      formData.append('layerId', selectedOption || 'base');

      const response = await fetch('/api/admin/upload-layer', {
        method: 'POST',
        body: formData
      });

      const result = await response.json();

      if (result.success) {
        setUploadStatus('Upload successful!');
        setUploadedImages(prev => ({
          ...prev,
          [`${selectedLayer}-${selectedOption || 'base'}`]: result.imagePath
        }));
        
        // Reset form
        event.target.value = '';
        setSelectedOption('');
      } else {
        setUploadStatus('Upload failed: ' + result.error);
      }
    } catch (error) {
      setUploadStatus('Upload failed: ' + error.message);
    } finally {
      setIsUploading(false);
    }
  };

  const renderUploadedImages = () => {
    const images = Object.entries(uploadedImages);
    
    if (images.length === 0) {
      return (
        <div className="text-center py-12 text-gray-500">
          <Image size={48} className="mx-auto mb-4 opacity-50" />
          <p>No images uploaded yet</p>
          <p className="text-sm">Start by uploading layer images</p>
        </div>
      );
    }

    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {images.map(([key, imagePath]) => {
          const [layerType, layerId] = key.split('-');
          const layer = layerTypes.find(l => l.id === layerType);
          const option = layerType === 'base' ? 'Base Ring' : 
            getLayerOptions(layerType).find(o => o.id === layerId)?.name || layerId;

          return (
            <div key={key} className="card p-4">
              <div className="aspect-square bg-gray-100 rounded-lg overflow-hidden mb-3">
                <img
                  src={imagePath}
                  alt={option}
                  className="w-full h-full object-cover"
                  onError={(e) => {
                    e.target.src = '/placeholder-option.png';
                  }}
                />
              </div>
              <div className="text-center">
                <h4 className="font-semibold text-gray-900 text-sm">{layer?.name}</h4>
                <p className="text-gray-600 text-xs">{option}</p>
              </div>
            </div>
          );
        })}
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-soft border-b border-gray-100">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div>
              <h1 className="text-3xl font-serif font-semibold text-gray-900">
                Ring Customizer Admin
              </h1>
              <p className="text-gray-600 mt-1">
                Upload ring layer images for customization
              </p>
            </div>
            <a
              href="/"
              className="btn-secondary flex items-center"
            >
              ← Back to Customizer
            </a>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Upload Section */}
          <div className="card p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Upload Layer Images</h2>
            
            <div className="space-y-4">
              {/* Layer Type Selection */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Layer Type
                </label>
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
                  {layerTypes.map((layer) => (
                    <div
                      key={layer.id}
                      onClick={() => {
                        setSelectedLayer(layer.id);
                        setSelectedOption('');
                      }}
                      className={`option-card card p-3 border-2 cursor-pointer transition-all duration-200 ${
                        selectedLayer === layer.id ? 'selected' : 'border-gray-200 hover:border-gray-300'
                      }`}
                    >
                      <div className="flex items-center">
                        <layer.icon size={20} className="mr-2 text-gray-600" />
                        <div>
                          <h4 className="font-medium text-gray-900 text-sm">{layer.name}</h4>
                          <p className="text-gray-600 text-xs">{layer.description}</p>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Option Selection */}
              {selectedLayer && selectedLayer !== 'base' && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Specific Option
                  </label>
                  <div className="grid grid-cols-2 gap-3">
                    {getLayerOptions(selectedLayer).map((option) => (
                      <div
                        key={option.id}
                        onClick={() => setSelectedOption(option.id)}
                        className={`option-card card p-3 border-2 cursor-pointer transition-all duration-200 ${
                          selectedOption === option.id ? 'selected' : 'border-gray-200 hover:border-gray-300'
                        }`}
                      >
                        <h4 className="font-medium text-gray-900 text-sm">{option.name}</h4>
                        <p className="text-gray-600 text-xs">{option.description}</p>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* File Upload */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Layer Image
                </label>
                <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-primary-400 transition-colors">
                  <input
                    type="file"
                    accept="image/*"
                    onChange={handleFileUpload}
                    disabled={isUploading || !selectedLayer || (selectedLayer !== 'base' && !selectedOption)}
                    className="hidden"
                    id="imageUpload"
                  />
                  <label
                    htmlFor="imageUpload"
                    className={`cursor-pointer flex flex-col items-center ${
                      isUploading || !selectedLayer || (selectedLayer !== 'base' && !selectedOption)
                        ? 'opacity-50 cursor-not-allowed'
                        : ''
                    }`}
                  >
                    <Upload size={32} className="text-gray-400 mb-2" />
                    <p className="text-sm text-gray-600">
                      Click to select image
                    </p>
                    <p className="text-xs text-gray-500 mt-1">
                      PNG with transparency recommended
                    </p>
                  </label>
                </div>
              </div>

              {/* Upload Status */}
              {uploadStatus && (
                <div className={`p-3 rounded-lg text-sm ${
                  uploadStatus.includes('successful') 
                    ? 'bg-green-50 text-green-800 border border-green-200'
                    : uploadStatus.includes('Uploading')
                    ? 'bg-blue-50 text-blue-800 border border-blue-200'
                    : 'bg-red-50 text-red-800 border border-red-200'
                }`}>
                  {uploadStatus}
                </div>
              )}
            </div>
          </div>

          {/* Preview Section */}
          <div className="card p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Uploaded Images</h2>
            {renderUploadedImages()}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
