import React from "react";
import { useParams } from "react-router-dom";
import { ringOptions, basePrice } from "../data/ringOptions";
import { useCart } from '../App';
import ProductCard from '../components/ProductCard';

const demoProducts = [
  {
    id: "solitaire-white-gold-round",
    design: "classic-solitaire",
    designLabel: "Classic Solitaire",
    description: "A timeless single stone setting, perfect for any occasion.",
    metal: "white-gold",
    availableMetals: ["white-gold", "yellow-gold", "rose-gold", "platinum"],
    images: {
      "white-gold": "/rings/classic-solitaire/white-gold/round/1.0ct.png",
      "yellow-gold": "/rings/classic-solitaire/yellow-gold/round/1.0ct.png",
      "rose-gold": "/rings/classic-solitaire/rose-gold/round/1.0ct.png",
      "platinum": "/rings/classic-solitaire/platinum/round/1.0ct.png"
    },
    price: basePrice + ringOptions.metals[0].price + ringOptions.designs[0].price + ringOptions.shapes[0].price + ringOptions.carats[0].price,
  },
  {
    id: "halo-yellow-gold-princess",
    design: "halo-setting",
    designLabel: "Halo Setting",
    description: "Diamond surrounded by smaller stones for extra brilliance.",
    metal: "yellow-gold",
    availableMetals: ["white-gold", "yellow-gold", "rose-gold", "platinum"],
    images: {
      "white-gold": "/rings/halo-setting/white-gold/princess/1.0ct.png",
      "yellow-gold": "/rings/halo-setting/yellow-gold/princess/1.0ct.png",
      "rose-gold": "/rings/halo-setting/rose-gold/princess/1.0ct.png",
      "platinum": "/rings/halo-setting/platinum/princess/1.0ct.png"
    },
    price: basePrice + ringOptions.metals[1].price + ringOptions.designs[1].price + ringOptions.shapes[1].price + ringOptions.carats[0].price,
  },
  {
    id: "vintage-platinum-emerald",
    design: "vintage-antique",
    designLabel: "Vintage/Antique Style",
    description: "Romantic, art-deco inspired design for a unique look.",
    metal: "platinum",
    availableMetals: ["white-gold", "yellow-gold", "rose-gold", "platinum"],
    images: {
      "white-gold": "/rings/vintage-antique/white-gold/emerald/1.0ct.png",
      "yellow-gold": "/rings/vintage-antique/yellow-gold/emerald/1.0ct.png",
      "rose-gold": "/rings/vintage-antique/rose-gold/emerald/1.0ct.png",
      "platinum": "/rings/vintage-antique/platinum/emerald/1.0ct.png"
    },
    price: basePrice + ringOptions.metals[3].price + ringOptions.designs[2].price + ringOptions.shapes[2].price + ringOptions.carats[0].price,
  },
  {
    id: "three-stone-rose-gold-oval",
    design: "three-stone",
    designLabel: "Three Stone",
    description: "Past, present, and future symbolism in a trinity design.",
    metal: "rose-gold",
    availableMetals: ["white-gold", "yellow-gold", "rose-gold", "platinum"],
    images: {
      "white-gold": "/rings/three-stone/white-gold/oval/1.0ct.png",
      "yellow-gold": "/rings/three-stone/yellow-gold/oval/1.0ct.png",
      "rose-gold": "/rings/three-stone/rose-gold/oval/1.0ct.png",
      "platinum": "/rings/three-stone/platinum/oval/1.0ct.png"
    },
    price: basePrice + ringOptions.metals[2].price + ringOptions.designs[3].price + ringOptions.shapes[3].price + ringOptions.carats[0].price,
  },
];

const ProductDetail = () => {
  const { id } = useParams();
  const product = demoProducts.find(p => p.id === id);
  const { addToCart } = useCart();

  if (!product) {
    return (
      <div className="bg-white min-h-screen font-sans px-4 py-12 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-black mb-4">Product Not Found</h1>
          <a href="/shop" className="text-gold font-bold hover:underline">Back to Shop</a>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white min-h-screen font-sans px-4 py-12 flex items-center justify-center">
      <ProductCard ring={product} onAddToCart={addToCart} />
    </div>
  );
};

export default ProductDetail;
