import React, { useState, useEffect } from 'react';
import { Upload, Image, Settings, Sparkles, Gem, Zap, Check, X, Trash2, Eye, AlertCircle } from 'lucide-react';
import { supabase } from '../lib/supabase';

const AdminPanel = () => {
  const [formData, setFormData] = useState({
    metal: '',
    design: '',
    shape: '',
    carat: ''
  });
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [uploadStatus, setUploadStatus] = useState('');
  const [uploadedImages, setUploadedImages] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const ringOptions = {
    metals: [
      { id: 'white-gold', name: 'White Gold' },
      { id: 'yellow-gold', name: 'Yellow Gold' },
      { id: 'rose-gold', name: 'Rose Gold' },
      { id: 'platinum', name: 'Platinum' }
    ],
    designs: [
      { id: 'solitaire', name: 'Solitaire' },
      { id: 'halo', name: 'Halo' },
      { id: 'vintage', name: 'Vintage' },
      { id: 'three-stone', name: 'Three Stone' }
    ],
    shapes: [
      { id: 'round', name: 'Round' },
      { id: 'princess', name: 'Princess' },
      { id: 'emerald', name: 'Emerald' },
      { id: 'oval', name: 'Oval' }
    ],
    carats: [
      { id: '1.0', name: '1.0 Carat' },
      { id: '1.5', name: '1.5 Carat' },
      { id: '2.0', name: '2.0 Carat' },
      { id: '2.5', name: '2.5 Carat' }
    ]
  };

  // Load existing images on component mount
  useEffect(() => {
    loadUploadedImages();
  }, []);

  const loadUploadedImages = async () => {
    try {
      const { data, error } = await supabase
        .from('ring_images')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) throw error;
      setUploadedImages(data || []);
    } catch (error) {
      console.error('Error loading images:', error);
      setUploadStatus('Error loading images: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
    setUploadStatus(''); // Clear status when form changes
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    // Validate file type
    if (!file.type.includes('image/')) {
      setUploadStatus('Please select a valid image file');
      return;
    }

    // Validate file size (max 5MB)
    if (file.size > 5 * 1024 * 1024) {
      setUploadStatus('File size must be less than 5MB');
      return;
    }

    setSelectedFile(file);
    
    // Create preview
    const reader = new FileReader();
    reader.onload = (e) => setPreviewUrl(e.target.result);
    reader.readAsDataURL(file);
    
    setUploadStatus('');
  };

  const validateForm = () => {
    if (!formData.metal || !formData.design || !formData.shape || !formData.carat) {
      setUploadStatus('Please fill in all fields');
      return false;
    }
    if (!selectedFile) {
      setUploadStatus('Please select an image file');
      return false;
    }
    return true;
  };

  const checkDuplicate = async () => {
    try {
      const { data, error } = await supabase
        .from('ring_images')
        .select('id')
        .eq('metal', formData.metal)
        .eq('design', formData.design)
        .eq('diamond_shape', formData.shape)
        .eq('carat', parseFloat(formData.carat))
        .single();

      if (error && error.code !== 'PGRST116') { // PGRST116 = no rows returned
        throw error;
      }

      if (data) {
        setUploadStatus('An image for this combination already exists');
        return true;
      }
      return false;
    } catch (error) {
      console.error('Error checking duplicate:', error);
      return false;
    }
  };

  const uploadImage = async () => {
    if (!validateForm()) return;

    const isDuplicate = await checkDuplicate();
    if (isDuplicate) return;

    setIsUploading(true);
    setUploadStatus('Uploading...');

    try {
      // 1. Upload file to Supabase Storage
      const filePath = `/rings/${formData.metal}/${formData.design}/${formData.shape}/weight-${formData.carat}.png`;
      
      const { data: uploadData, error: uploadError } = await supabase.storage
        .from('ring-images')
        .upload(filePath, selectedFile, {
          cacheControl: '3600',
          upsert: false
        });

      if (uploadError) throw uploadError;

      // 2. Get public URL
      const { data: urlData } = supabase.storage
        .from('ring-images')
        .getPublicUrl(filePath);

      // 3. Save metadata to database
      const { error: dbError } = await supabase
        .from('ring_images')
        .insert({
          metal: formData.metal,
          design: formData.design,
          diamond_shape: formData.shape,
          carat: parseFloat(formData.carat),
          image_url: urlData.publicUrl
        });

      if (dbError) throw dbError;

      // Success!
      setUploadStatus('Upload successful!');
      setFormData({ metal: '', design: '', shape: '', carat: '' });
      setSelectedFile(null);
      setPreviewUrl('');
      
      // Reload images
      await loadUploadedImages();

    } catch (error) {
      console.error('Upload error:', error);
      setUploadStatus('Upload failed: ' + error.message);
    } finally {
      setIsUploading(false);
    }
  };

  const deleteImage = async (id, imageUrl) => {
    if (!window.confirm('Are you sure you want to delete this image?')) return;

    try {
      // Delete from database
      const { error: dbError } = await supabase
        .from('ring_images')
        .delete()
        .eq('id', id);

      if (dbError) throw dbError;

      // Delete from storage (optional - you might want to keep files)
      // const { error: storageError } = await supabase.storage
      //   .from('ring-images')
      //   .remove([imageUrl]);

      setUploadStatus('Image deleted successfully');
      await loadUploadedImages();
    } catch (error) {
      console.error('Delete error:', error);
      setUploadStatus('Delete failed: ' + error.message);
    }
  };

  const getOptionName = (type, id) => {
    const options = ringOptions[type];
    return options?.find(opt => opt.id === id)?.name || id;
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-soft border-b border-gray-100">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div>
              <h1 className="text-3xl font-serif font-semibold text-gray-900">
                Ring Customizer Admin
              </h1>
              <p className="text-gray-600 mt-1">
                Upload structured ring images to Supabase
              </p>
            </div>
            <a href="/" className="btn-secondary flex items-center">
              ‚Üê Back to Customizer
            </a>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Upload Form */}
          <div className="card p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Upload Ring Image</h2>
            
            <div className="space-y-4">
              {/* Form Fields */}
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Metal *
                  </label>
                  <select
                    value={formData.metal}
                    onChange={(e) => handleInputChange('metal', e.target.value)}
                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                  >
                    <option value="">Select Metal</option>
                    {ringOptions.metals.map(metal => (
                      <option key={metal.id} value={metal.id}>{metal.name}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Design *
                  </label>
                  <select
                    value={formData.design}
                    onChange={(e) => handleInputChange('design', e.target.value)}
                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                  >
                    <option value="">Select Design</option>
                    {ringOptions.designs.map(design => (
                      <option key={design.id} value={design.id}>{design.name}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Diamond Shape *
                  </label>
                  <select
                    value={formData.shape}
                    onChange={(e) => handleInputChange('shape', e.target.value)}
                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                  >
                    <option value="">Select Shape</option>
                    {ringOptions.shapes.map(shape => (
                      <option key={shape.id} value={shape.id}>{shape.name}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Carat Weight *
                  </label>
                  <select
                    value={formData.carat}
                    onChange={(e) => handleInputChange('carat', e.target.value)}
                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                  >
                    <option value="">Select Carat</option>
                    {ringOptions.carats.map(carat => (
                      <option key={carat.id} value={carat.id}>{carat.name}</option>
                    ))}
                  </select>
                </div>
              </div>

              {/* File Upload */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Ring Image *
                </label>
                <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-primary-400 transition-colors">
                  <input
                    type="file"
                    accept="image/*"
                    onChange={handleFileSelect}
                    disabled={isUploading}
                    className="hidden"
                    id="imageUpload"
                  />
                  <label
                    htmlFor="imageUpload"
                    className={`cursor-pointer flex flex-col items-center ${
                      isUploading ? 'opacity-50 cursor-not-allowed' : ''
                    }`}
                  >
                    <Upload size={32} className="text-gray-400 mb-2" />
                    <p className="text-sm text-gray-600">
                      Click to select image (PNG recommended)
                    </p>
                    <p className="text-xs text-gray-500 mt-1">
                      Max 5MB, 800x800px recommended
                    </p>
                  </label>
                </div>
              </div>

              {/* Preview */}
              {previewUrl && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Preview
                  </label>
                  <div className="relative w-32 h-32 border border-gray-200 rounded-lg overflow-hidden">
                    <img
                      src={previewUrl}
                      alt="Preview"
                      className="w-full h-full object-cover"
                    />
                  </div>
                </div>
              )}

              {/* Upload Button */}
              <button
                onClick={uploadImage}
                disabled={isUploading || !selectedFile || !formData.metal || !formData.design || !formData.shape || !formData.carat}
                className="w-full btn-primary disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
              >
                {isUploading ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Uploading...
                  </>
                ) : (
                  <>
                    <Upload size={20} className="mr-2" />
                    Upload Image
                  </>
                )}
              </button>

              {/* Status Message */}
              {uploadStatus && (
                <div className={`p-3 rounded-lg text-sm flex items-center ${
                  uploadStatus.includes('successful') 
                    ? 'bg-green-50 text-green-800 border border-green-200'
                    : uploadStatus.includes('Uploading')
                    ? 'bg-blue-50 text-blue-800 border border-blue-200'
                    : 'bg-red-50 text-red-800 border border-red-200'
                }`}>
                  {uploadStatus.includes('successful') ? (
                    <Check size={16} className="mr-2" />
                  ) : uploadStatus.includes('Uploading') ? (
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-current mr-2"></div>
                  ) : (
                    <AlertCircle size={16} className="mr-2" />
                  )}
                  {uploadStatus}
                </div>
              )}
            </div>
          </div>

          {/* Uploaded Images */}
          <div className="card p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Uploaded Images</h2>
            
            {isLoading ? (
              <div className="text-center py-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-500 mx-auto"></div>
                <p className="text-gray-600 mt-2">Loading images...</p>
              </div>
            ) : uploadedImages.length === 0 ? (
              <div className="text-center py-12 text-gray-500">
                <Image size={48} className="mx-auto mb-4 opacity-50" />
                <p>No images uploaded yet</p>
                <p className="text-sm">Start by uploading ring images</p>
              </div>
            ) : (
              <div className="space-y-4 max-h-96 overflow-y-auto">
                {uploadedImages.map((image) => (
                  <div key={image.id} className="flex items-center p-3 border border-gray-200 rounded-lg">
                    <div className="w-16 h-16 bg-gray-100 rounded-lg overflow-hidden mr-4">
                      <img
                        src={image.image_url}
                        alt="Ring"
                        className="w-full h-full object-cover"
                        onError={(e) => {
                          e.target.src = '/placeholder-option.png';
                        }}
                      />
                    </div>
                    <div className="flex-1">
                      <div className="text-sm font-medium text-gray-900">
                        {getOptionName('metals', image.metal)} ‚Ä¢ {getOptionName('designs', image.design)}
                      </div>
                      <div className="text-xs text-gray-600">
                        {getOptionName('shapes', image.diamond_shape)} ‚Ä¢ {image.carat} Carat
                      </div>
                      <div className="text-xs text-gray-500 mt-1">
                        Path: /rings/{image.metal}/{image.design}/{image.diamond_shape}/weight-{image.carat}.png
                      </div>
                    </div>
                    <div className="flex space-x-2">
                      <button
                        onClick={() => window.open(image.image_url, '_blank')}
                        className="p-1 text-gray-400 hover:text-gray-600"
                        title="View image"
                      >
                        <Eye size={16} />
                      </button>
                      <button
                        onClick={() => deleteImage(image.id, image.image_url)}
                        className="p-1 text-red-400 hover:text-red-600"
                        title="Delete image"
                      >
                        <Trash2 size={16} />
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
