import React from 'react';
import { Loader2, Heart, ShoppingCart } from 'lucide-react';

const RingPreview = ({ imageUrl, isLoading, selections, totalPrice, ringOptions }) => {
  const getSelectionName = (type, value) => {
    if (!value) return null;
    
    const options = ringOptions[type === 'metal' ? 'metals' : type === 'design' ? 'designs' : type === 'shape' ? 'shapes' : 'carats'];
    return options.find(opt => opt.id === value)?.name;
  };

  const getSelectionPrice = (type, value) => {
    if (!value) return 0;
    
    const options = ringOptions[type === 'metal' ? 'metals' : type === 'design' ? 'designs' : type === 'shape' ? 'shapes' : 'carats'];
    return options.find(opt => opt.id === value)?.price || 0;
  };

  const hasAllSelections = selections.metal && selections.design && selections.shape && selections.carat;

  return (
    <div className="card p-8">
      {/* Ring Image */}
      <div className="relative aspect-square bg-gradient-to-br from-gray-50 to-gray-100 rounded-xl overflow-hidden mb-8">
        {isLoading ? (
          <div className="absolute inset-0 flex items-center justify-center">
            <Loader2 className="animate-spin text-primary-500" size={40} />
          </div>
        ) : (
          <img
            src={imageUrl}
            alt="Custom Ring"
            className="w-full h-full object-contain p-4"
            onError={(e) => {
              e.target.src = '/placeholder-ring.png';
            }}
          />
        )}
        
        {/* Zoom indicator */}
        <div className="absolute bottom-2 right-2 bg-black bg-opacity-50 text-white text-xs px-2 py-1 rounded">
          Click to zoom
        </div>
      </div>

      {/* Ring Specifications */}
      <div className="space-y-4 mb-6">
        <h3 className="text-lg font-semibold text-gray-900">Your Ring Specifications</h3>
        
        <div className="space-y-3">
          {selections.metal && (
            <div className="flex justify-between items-center py-2 border-b border-gray-100">
              <span className="text-gray-600">Metal</span>
              <div className="text-right">
                <div className="font-medium text-gray-900">{getSelectionName('metal', selections.metal)}</div>
                {getSelectionPrice('metal', selections.metal) > 0 && (
                  <div className="text-sm text-gray-500">+${getSelectionPrice('metal', selections.metal).toLocaleString()}</div>
                )}
              </div>
            </div>
          )}
          
          {selections.design && (
            <div className="flex justify-between items-center py-2 border-b border-gray-100">
              <span className="text-gray-600">Design</span>
              <div className="text-right">
                <div className="font-medium text-gray-900">{getSelectionName('design', selections.design)}</div>
                {getSelectionPrice('design', selections.design) > 0 && (
                  <div className="text-sm text-gray-500">+${getSelectionPrice('design', selections.design).toLocaleString()}</div>
                )}
              </div>
            </div>
          )}
          
          {selections.shape && (
            <div className="flex justify-between items-center py-2 border-b border-gray-100">
              <span className="text-gray-600">Diamond Shape</span>
              <div className="text-right">
                <div className="font-medium text-gray-900">{getSelectionName('shape', selections.shape)}</div>
                {getSelectionPrice('shape', selections.shape) > 0 && (
                  <div className="text-sm text-gray-500">+${getSelectionPrice('shape', selections.shape).toLocaleString()}</div>
                )}
              </div>
            </div>
          )}
          
          {selections.carat && (
            <div className="flex justify-between items-center py-2 border-b border-gray-100">
              <span className="text-gray-600">Carat Weight</span>
              <div className="text-right">
                <div className="font-medium text-gray-900">{getSelectionName('carat', selections.carat)}</div>
                {getSelectionPrice('carat', selections.carat) > 0 && (
                  <div className="text-sm text-gray-500">+${getSelectionPrice('carat', selections.carat).toLocaleString()}</div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Price Breakdown */}
      <div className="bg-gray-50 rounded-lg p-4 mb-6">
        <div className="flex justify-between items-center mb-2">
          <span className="text-gray-600">Base Price</span>
          <span className="font-medium">$1,000</span>
        </div>
        
        {Object.entries(selections).map(([type, value]) => {
          const price = getSelectionPrice(type, value);
          if (price > 0) {
            return (
              <div key={type} className="flex justify-between items-center mb-1">
                <span className="text-gray-600 text-sm">{getSelectionName(type, value)}</span>
                <span className="text-sm">+${price.toLocaleString()}</span>
              </div>
            );
          }
          return null;
        })}
        
        <div className="border-t border-gray-200 pt-2 mt-2">
          <div className="flex justify-between items-center">
            <span className="font-semibold text-gray-900">Total</span>
            <span className="text-xl font-serif font-semibold text-primary-600">
              ${totalPrice.toLocaleString()}
            </span>
          </div>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="space-y-3">
        <button
          disabled={!hasAllSelections}
          className="w-full btn-primary disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
        >
          <ShoppingCart size={20} className="mr-2" />
          Add to Cart
        </button>
        
        <button className="w-full btn-secondary flex items-center justify-center">
          <Heart size={20} className="mr-2" />
          Save Design
        </button>
      </div>

      {/* Completion Status */}
      {!hasAllSelections && (
        <div className="mt-4 p-3 bg-blue-50 rounded-lg border border-blue-200">
          <div className="text-sm text-blue-800">
            <div className="font-medium mb-1">Complete your ring design</div>
            <div className="text-blue-600">
              {!selections.metal && "• Choose your metal type"}
              {!selections.design && "• Select your ring design"}
              {!selections.shape && "• Pick your diamond shape"}
              {!selections.carat && "• Choose carat weight"}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RingPreview;
