import React from 'react';
import { Check } from 'lucide-react';

const CustomizationStep = ({ title, description, options, selected, onSelect }) => {
  return (
    <div className="w-full h-full flex flex-col">
      <div className="mb-3">
        <h3 className="text-base font-semibold text-gray-900 mb-1">{title}</h3>
        <p className="text-xs text-gray-600">{description}</p>
      </div>

      <div className="flex-1 grid grid-cols-1 gap-2">
        {options.map((option) => {
          const IconComponent = option.icon;
          return (
            <button
              key={option.value}
              onClick={() => onSelect(option.value)}
              className={`p-2 rounded-md border-2 transition-all duration-200 text-left group hover:scale-102 ${
                selected === option.value
                  ? 'border-yellow-500 bg-yellow-50 shadow-sm ring-1 ring-yellow-500/30'
                  : 'border-gray-200 bg-gray-50 hover:border-gray-300 hover:shadow-sm'
              }`}
            >
              <div className="flex items-center space-x-2">
                {/* Icon */}
                <div className={`flex-shrink-0 w-6 h-6 rounded-full flex items-center justify-center ${
                  selected === option.value 
                    ? 'bg-yellow-500 text-black' 
                    : `bg-gray-200 ${option.color || 'text-gray-600'}`
                }`}>
                  {IconComponent && <IconComponent className="w-3 h-3" />}
                </div>
                
                {/* Content */}
                <div className="flex-1 min-w-0">
                  <h4 className="font-medium text-gray-900 text-xs truncate">{option.label}</h4>
                  {option.sublabel && (
                    <p className="text-xs text-gray-500 truncate">{option.sublabel}</p>
                  )}
                </div>
                
                {/* Check mark */}
                {selected === option.value && (
                  <div className="flex-shrink-0">
                    <Check className="w-3 h-3 text-yellow-500" />
                  </div>
                )}
              </div>
            </button>
          );
        })}
      </div>

      {selected && (
        <div className="mt-2 p-1.5 bg-yellow-50 border border-yellow-200 rounded text-xs">
          <div className="flex items-center">
            <Check className="w-3 h-3 text-yellow-500 mr-1" />
            <span className="text-yellow-700 font-medium">
              {options.find(opt => opt.value === selected)?.label} selected
            </span>
          </div>
        </div>
      )}
    </div>
  );
};

export default CustomizationStep;
