import React, { useEffect, useState } from "react";
import { useCart } from "../App";
import { ringOptions, basePrice } from "../data/ringOptions";

const demoProducts = [
  {
    id: "solitaire-white-gold-round",
    name: "Classic Solitaire in White Gold (Round)",
    image: ringOptions.designs[0].image,
    price: basePrice + ringOptions.metals[0].price + ringOptions.designs[0].price + ringOptions.shapes[0].price + ringOptions.carats[0].price,
    metal: ringOptions.metals[0].name,
    design: ringOptions.designs[0].name,
    shape: ringOptions.shapes[0].name,
  },
  {
    id: "halo-yellow-gold-princess",
    name: "Halo Setting in Yellow Gold (Princess)",
    image: ringOptions.designs[1].image,
    price: basePrice + ringOptions.metals[1].price + ringOptions.designs[1].price + ringOptions.shapes[1].price + ringOptions.carats[0].price,
    metal: ringOptions.metals[1].name,
    design: ringOptions.designs[1].name,
    shape: ringOptions.shapes[1].name,
  },
  {
    id: "vintage-platinum-emerald",
    name: "Vintage Platinum (Emerald Cut)",
    image: ringOptions.designs[2].image,
    price: basePrice + ringOptions.metals[3].price + ringOptions.designs[2].price + ringOptions.shapes[2].price + ringOptions.carats[0].price,
    metal: ringOptions.metals[3].name,
    design: ringOptions.designs[2].name,
    shape: ringOptions.shapes[2].name,
  },
  {
    id: "three-stone-rose-gold-oval",
    name: "Three Stone in Rose Gold (Oval)",
    image: ringOptions.designs[3].image,
    price: basePrice + ringOptions.metals[2].price + ringOptions.designs[3].price + ringOptions.shapes[3].price + ringOptions.carats[0].price,
    metal: ringOptions.metals[2].name,
    design: ringOptions.designs[3].name,
    shape: ringOptions.shapes[3].name,
  },
];

const Wishlist = () => {
  const { addToCart } = useCart();
  const [wishlist, setWishlist] = useState([]);

  useEffect(() => {
    const stored = localStorage.getItem("wishlist");
    setWishlist(stored ? JSON.parse(stored) : []);
  }, []);

  const removeFromWishlist = (id) => {
    const updated = wishlist.filter(item => item.id !== id);
    setWishlist(updated);
    localStorage.setItem("wishlist", JSON.stringify(updated));
  };

  const addProductToCart = (product) => {
    addToCart(product);
    removeFromWishlist(product.id);
    alert("Added to cart!");
  };

  if (wishlist.length === 0) {
    return (
      <div className="bg-white min-h-screen font-sans px-4 py-12 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-black mb-4">Your Wishlist is Empty</h1>
          <a href="/shop" className="text-gold font-bold hover:underline">Shop Rings</a>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white min-h-screen font-sans px-4 py-12">
      <h1 className="text-4xl font-serif font-bold mb-8 text-center text-black" style={{ fontFamily: 'Playfair Display, serif' }}>
        My Wishlist
      </h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-8 max-w-6xl mx-auto">
        {wishlist.map(item => (
          <div key={item.id} className="bg-white rounded-lg shadow p-6 border border-gray-200 flex flex-col items-center">
            <img src={process.env.PUBLIC_URL + '/ringstestimages/halo seting gold ring.jpg'} alt="Wishlist Ring" className="w-20 h-20 object-cover rounded-full" />
            <div className="font-serif text-lg font-bold mb-1 text-center" style={{ fontFamily: 'Playfair Display, serif' }}>{item.name}</div>
            <div className="text-gold text-base mb-3 font-semibold">{item.price.toLocaleString('en-US', { style: 'currency', currency: 'USD' })}</div>
            <div className="flex gap-2">
              <button className="bg-black text-white font-bold rounded shadow hover:bg-gold hover:text-black transition text-sm tracking-wide border border-black px-6 py-2 mt-2" onClick={() => addProductToCart(item)}>Add to Cart</button>
              <button className="px-4 py-2 bg-white border border-gold text-black rounded font-bold" onClick={() => removeFromWishlist(item.id)}>Remove</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Wishlist;
