import React from "react";
import { ringOptions, basePrice } from "../data/ringOptions";

// Generate a small set of demo products (all combinations would be too many)
const demoProducts = [
  {
    id: "solitaire-white-gold-round",
    name: "Classic Solitaire in White Gold (Round)",
    image: process.env.PUBLIC_URL + "/ringstestimages/three stone gold square.jpg",
    price: 1200,
    metal: "White Gold",
    design: "Solitaire",
    shape: "Round",
  },
  {
    id: "halo-yellow-gold-princess",
    name: "Halo Setting in Yellow Gold (Princess)",
    image: process.env.PUBLIC_URL + "/ringstestimages/three stone platinum square.jpg",
    price: 1850,
    metal: "Yellow Gold",
    design: "Halo",
    shape: "Princess",
  },
  {
    id: "vintage-platinum-emerald",
    name: "Vintage Platinum (Emerald Cut)",
    image: process.env.PUBLIC_URL + "/ringstestimages/vintage rose square.jpg",
    price: 2100,
    metal: "Platinum",
    design: "Vintage",
    shape: "Emerald",
  },
  {
    id: "three-stone-rose-gold-oval",
    name: "Three Stone in Rose Gold (Oval)",
    image: process.env.PUBLIC_URL + "/ringstestimages/halo seting gold ring.jpg",
    price: 1950,
    metal: "Rose Gold",
    design: "Three Stone",
    shape: "Oval",
  },
];

const Shop = () => (
  <div className="bg-white min-h-screen font-sans px-4 py-12">
    <h1 className="text-4xl font-serif font-bold text-black mb-10 text-center tracking-tight" style={{ fontFamily: 'Playfair Display, serif' }}>
      Shop All Rings
    </h1>
    {/* Filters (static for now) */}
    <div className="flex flex-wrap gap-4 justify-center mb-12">
      <select className="border border-gray-300 rounded px-5 py-2 text-black bg-white focus:outline-gold">
        <option>All Metals</option>
        {ringOptions.metals.map(m => <option key={m.id}>{m.name}</option>)}
      </select>
      <select className="border border-gray-300 rounded px-5 py-2 text-black bg-white focus:outline-gold">
        <option>All Designs</option>
        {ringOptions.designs.map(d => <option key={d.id}>{d.name}</option>)}
      </select>
      <select className="border border-gray-300 rounded px-5 py-2 text-black bg-white focus:outline-gold">
        <option>All Shapes</option>
        {ringOptions.shapes.map(s => <option key={s.id}>{s.name}</option>)}
      </select>
    </div>
    <div className="w-full border-t border-gray-200 my-12" />
    {/* Product Grid */}
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8 mt-8">
      {demoProducts.map((product, i) => (
        <div key={product.id} className="bg-white rounded-2xl shadow-lg border border-gray-200 hover:border-gold transition-all duration-300 flex flex-col items-center group overflow-hidden relative">
          <div className="w-full h-56 flex items-center justify-center overflow-hidden rounded-t-2xl bg-gray-50">
            <img src={product.image} alt={product.name} className="w-full h-full object-cover transform group-hover:scale-105 group-hover:-translate-y-1 transition-transform duration-300 ease-in-out" />
          </div>
          <div className="p-5 w-full flex flex-col items-center">
            <div className="font-serif text-lg font-bold mb-1 text-center text-black" style={{ fontFamily: 'Playfair Display, serif' }}>{product.name}</div>
            <div className="text-gold text-base mb-3 font-semibold">${product.price.toLocaleString()}</div>
            <a href={`/product/${product.id}`} className="inline-block mt-auto px-6 py-2 bg-black text-gold font-bold rounded-full shadow hover:bg-gold hover:text-black transition-all duration-200 text-sm tracking-wide border border-black focus:outline-none focus:ring-2 focus:ring-gold/40 hover:scale-105">
              View Details
            </a>
          </div>
        </div>
      ))}
    </div>
    <style>{`
      .text-gold { color: #bfa14a; }
      .border-gold { border-color: #bfa14a; }
      .bg-gold { background-color: #bfa14a; }
    `}</style>
  </div>
);

export default Shop;
