import React from "react";
// Framer Motion import for future animation
// import { motion } from "framer-motion";

const heroBg = "https://images.unsplash.com/photo-1517841905240-472988babdf9?auto=format&fit=crop&w=1200&q=80"; // Example luxury jewelry bg

const Home = () => (
  <div className="bg-white min-h-screen font-sans luxury-bg">
    {/* Hero Section */}
    <section
      className="relative h-[60vh] flex items-center justify-center text-center overflow-hidden"
      style={{ backgroundImage: `url(${heroBg})`, backgroundSize: 'cover', backgroundPosition: 'center' }}
    >
      <div className="absolute inset-0 bg-black/60" />
      <div className="absolute inset-0 backdrop-blur-md bg-white/10 rounded-b-3xl border-b-4 border-gold/30 shadow-elegant" />
      <div className="relative z-10 px-6">
        <h1 className="text-5xl md:text-7xl font-serif font-extrabold mb-4 text-gold drop-shadow-lg luxury-glow" style={{ fontFamily: 'Playfair Display, serif' }}>
          Shemesh Gold
        </h1>
        <p className="text-xl md:text-2xl mb-8 text-white font-light max-w-2xl mx-auto luxury-glass-text">
          Exquisite Rings for Life's Most Precious Moments
        </p>
        <a href="/shop" className="inline-block px-10 py-4 bg-gradient-to-r from-gold to-gold-dark text-black font-bold rounded-full shadow-lg hover:from-gold-dark hover:to-gold transition text-lg tracking-wide luxury-shimmer">
          Shop Now
        </a>
      </div>
    </section>
    {/* Featured Collections */}
    <section className="max-w-6xl mx-auto py-20 px-4">
      <div className="bg-white/60 backdrop-blur-lg rounded-3xl shadow-elegant border border-gold/20 p-10">
        <h2 className="text-3xl font-serif font-bold text-gold mb-10 text-center luxury-glow" style={{ fontFamily: 'Playfair Display, serif' }}>
          Featured Collections
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
          <div className="bg-gold-light/60 rounded-xl p-8 text-black shadow-elegant text-center hover:scale-105 hover:shadow-2xl transition cursor-pointer luxury-card">
            <h3 className="font-serif text-xl font-bold mb-2 luxury-glow" style={{ fontFamily: 'Playfair Display, serif' }}>Wedding Rings</h3>
            <p className="text-gray-700">Timeless bands for your forever.</p>
          </div>
          <div className="bg-gold-light/60 rounded-xl p-8 text-black shadow-elegant text-center hover:scale-105 hover:shadow-2xl transition cursor-pointer luxury-card">
            <h3 className="font-serif text-xl font-bold mb-2 luxury-glow" style={{ fontFamily: 'Playfair Display, serif' }}>Engagement Rings</h3>
            <p className="text-gray-700">Celebrate your unique love story.</p>
          </div>
          <div className="bg-gold-light/60 rounded-xl p-8 text-black shadow-elegant text-center hover:scale-105 hover:shadow-2xl transition cursor-pointer luxury-card">
            <h3 className="font-serif text-xl font-bold mb-2 luxury-glow" style={{ fontFamily: 'Playfair Display, serif' }}>Custom Designs</h3>
            <p className="text-gray-700">Design a ring as unique as you.</p>
          </div>
        </div>
      </div>
    </section>
    {/* Promo Banner */}
    <section className="bg-gradient-to-r from-gold to-gold-dark text-black py-6 text-center font-semibold text-lg tracking-wide shadow-inner luxury-shimmer">
      Summer Sale: <span className="font-bold">10% Off All Custom Rings!</span>
    </section>
    {/* Testimonials */}
    <section className="max-w-4xl mx-auto py-20 px-4">
      <div className="bg-white/70 backdrop-blur-lg rounded-3xl shadow-elegant border border-gold/20 p-10">
        <h2 className="text-3xl font-serif font-bold text-gold mb-10 text-center luxury-glow" style={{ fontFamily: 'Playfair Display, serif' }}>
          What Our Customers Say
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-10">
          <div className="bg-white/80 rounded-xl shadow-elegant p-8 border border-gold/40 text-black text-left luxury-card">
            <p className="italic text-lg mb-4">“Absolutely stunning craftsmanship. My fiancée loved her ring!”</p>
            <span className="block text-gold-dark font-semibold">– Daniel S.</span>
          </div>
          <div className="bg-white/80 rounded-xl shadow-elegant p-8 border border-gold/40 text-black text-left luxury-card">
            <p className="italic text-lg mb-4">“The custom design process was so easy and fun. Highly recommend!”</p>
            <span className="block text-gold-dark font-semibold">– Sarah L.</span>
          </div>
        </div>
      </div>
    </section>
    {/* Bestsellers Grid */}
    <section className="max-w-6xl mx-auto py-20 px-4">
      <div className="bg-white/60 backdrop-blur-lg rounded-3xl shadow-elegant border border-gold/20 p-10">
        <h2 className="text-3xl font-serif font-bold text-gold mb-10 text-center luxury-glow" style={{ fontFamily: 'Playfair Display, serif' }}>
          Bestsellers
        </h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-8">
          <div className="bg-white/90 rounded-xl shadow-elegant p-6 border-2 border-gold h-48 flex flex-col items-center justify-center text-black hover:scale-105 hover:shadow-2xl transition cursor-pointer luxury-card">
            <span className="font-serif text-lg font-bold mb-2 luxury-glow" style={{ fontFamily: 'Playfair Display, serif' }}>Ring 1</span>
          </div>
          <div className="bg-white/90 rounded-xl shadow-elegant p-6 border-2 border-gold h-48 flex flex-col items-center justify-center text-black hover:scale-105 hover:shadow-2xl transition cursor-pointer luxury-card">
            <span className="font-serif text-lg font-bold mb-2 luxury-glow" style={{ fontFamily: 'Playfair Display, serif' }}>Ring 2</span>
          </div>
          <div className="bg-white/90 rounded-xl shadow-elegant p-6 border-2 border-gold h-48 flex flex-col items-center justify-center text-black hover:scale-105 hover:shadow-2xl transition cursor-pointer luxury-card">
            <span className="font-serif text-lg font-bold mb-2 luxury-glow" style={{ fontFamily: 'Playfair Display, serif' }}>Ring 3</span>
          </div>
          <div className="bg-white/90 rounded-xl shadow-elegant p-6 border-2 border-gold h-48 flex flex-col items-center justify-center text-black hover:scale-105 hover:shadow-2xl transition cursor-pointer luxury-card">
            <span className="font-serif text-lg font-bold mb-2 luxury-glow" style={{ fontFamily: 'Playfair Display, serif' }}>Ring 4</span>
          </div>
        </div>
      </div>
    </section>
    <style>{`
      .luxury-bg {
        background: repeating-linear-gradient(135deg, #fff 0 40px, #f5e7b2 40px 80px);
      }
      .luxury-glow {
        text-shadow: 0 2px 12px #bfa14a99, 0 1px 0 #fff;
      }
      .luxury-shimmer {
        background-size: 200% 200%;
        animation: shimmer 2.5s linear infinite;
      }
      @keyframes shimmer {
        0% { background-position: 0% 50%; }
        100% { background-position: 100% 50%; }
      }
      .luxury-card {
        transition: box-shadow 0.3s, transform 0.3s;
      }
      .luxury-card:hover {
        box-shadow: 0 8px 32px 0 #bfa14a33, 0 1.5px 6px 0 #bfa14a22;
        border-color: #bfa14a;
      }
      .luxury-glass-text {
        background: rgba(255,255,255,0.2);
        border-radius: 1rem;
        padding: 0.5rem 1.5rem;
        box-shadow: 0 2px 12px #bfa14a33;
        display: inline-block;
      }
    `}</style>
  </div>
);

export default Home;
