import React, { useState } from 'react';
import { supabase } from '../lib/supabase';

const Contact = () => {
  const [form, setForm] = useState({ name: '', email: '', message: '' });
  const [submitting, setSubmitting] = useState(false);
  const [status, setStatus] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setStatus('');
    console.log('Submitting contact form:', form);
    try {
      const { data, error } = await supabase.from('messages').insert({
        name: form.name,
        email: form.email,
        message: form.message,
        status: 'unread'
      });
      console.log('Supabase insert result:', { data, error });
      if (error) throw error;
      setStatus('Message sent! We will get back to you soon.');
      setForm({ name: '', email: '', message: '' });
    } catch (err) {
      setStatus('Error sending message. Please try again.');
      console.error('Contact form error:', err);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="bg-white min-h-screen font-sans px-4 py-12">
      <h1 className="text-4xl font-serif font-bold mb-8 text-center text-black" style={{ fontFamily: 'Playfair Display, serif' }}>
        Contact Us
      </h1>
      <form onSubmit={handleSubmit} className="bg-white rounded-lg shadow p-8 flex flex-col gap-4 mb-8 border border-gray-200 max-w-xl mx-auto">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Name</label>
          <input type="text" name="name" value={form.name} onChange={handleChange} className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-gold" placeholder="Your Name" required />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
          <input type="email" name="email" value={form.email} onChange={handleChange} className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-gold" placeholder="you@email.com" required />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Message</label>
          <textarea name="message" value={form.message} onChange={handleChange} className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-gold" rows={4} placeholder="How can we help you?" required />
        </div>
        <button type="submit" disabled={submitting} className="bg-gold hover:bg-black hover:text-gold text-white font-semibold px-6 py-2 rounded-md shadow transition border border-gold disabled:opacity-50">
          {submitting ? 'Sending...' : 'Send Message'}
        </button>
        {status && <div className={`text-center mt-2 ${status.startsWith('Error') ? 'text-red-600' : 'text-green-700'}`}>{status}</div>}
      </form>
      <div className="text-center text-gray-700">
        <p className="mb-1 font-semibold">Shemesh Gold</p>
        <p>123 Gold Street, Tel Aviv, Israel</p>
        <p>+972 3-123-4567</p>
        <p className="mt-2">Email: <a href="mailto:info@shemeshgold.com" className="text-gold hover:underline">info@shemeshgold.com</a></p>
      </div>
    </div>
  );
};

export default Contact;
