import React, { useState, useEffect } from 'react';
import { Upload, Image, Settings, Sparkles, Gem, Zap, Check, X, Trash2, Eye, AlertCircle, ChevronDown, ChevronRight } from 'lucide-react';
import { supabase } from '../lib/supabase';

const AdminPanel = () => {
  const [selectedMetal, setSelectedMetal] = useState('');
  const [selectedDesign, setSelectedDesign] = useState('');
  const [selectedShape, setSelectedShape] = useState('');
  const [selectedCarat, setSelectedCarat] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [uploading, setUploading] = useState(false);
  const [uploadedImages, setUploadedImages] = useState([]);
  const [message, setMessage] = useState('');
  const [expandedSections, setExpandedSections] = useState({});

  // Updated options based on your specifications
  const metals = [
    { value: 'white-gold', label: 'White Gold (14K/18K)' },
    { value: 'yellow-gold', label: 'Yellow Gold (14K/18K)' },
    { value: 'rose-gold', label: 'Rose Gold (14K/18K)' },
    { value: 'platinum', label: 'Platinum (950)' }
  ];

  const designs = [
    { value: 'classic-solitaire', label: 'Classic Solitaire' },
    { value: 'halo-setting', label: 'Halo Setting' },
    { value: 'vintage-antique', label: 'Vintage/Antique Style' },
    { value: 'three-stone', label: 'Three Stone (Trinity)' }
  ];

  const shapes = [
    { value: 'round', label: 'Round Brilliant Cut' },
    { value: 'princess', label: 'Princess Cut (Square)' },
    { value: 'emerald', label: 'Emerald Cut (Rectangular)' },
    { value: 'oval', label: 'Oval Brilliant Cut' }
  ];

  const carats = [
    { value: '1.0', label: '1.0 Carat' },
    { value: '1.5', label: '1.5 Carat' },
    { value: '2.0', label: '2.0 Carat' },
    { value: '2.5', label: '2.5 Carat' }
  ];

  useEffect(() => {
    fetchUploadedImages();
  }, []);

  const fetchUploadedImages = async () => {
    try {
      const { data, error } = await supabase
        .from('ring_images')
        .select('*')
        .order('metal', { ascending: true })
        .order('design', { ascending: true })
        .order('diamond_shape', { ascending: true })
        .order('carat', { ascending: true });

      if (error) throw error;
      setUploadedImages(data || []);
    } catch (error) {
      console.error('Error fetching images:', error);
      setMessage('Error fetching uploaded images: ' + error.message);
    }
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Validate file type
      if (!file.type.startsWith('image/')) {
        setMessage('Please select an image file (PNG, JPG, etc.)');
        return;
      }
      
      // Validate file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setMessage('File size must be less than 5MB');
        return;
      }

      setSelectedFile(file);
      const url = URL.createObjectURL(file);
      setPreviewUrl(url);
      setMessage('');
    }
  };

  const generateImagePath = () => {
    if (!selectedMetal) return '';
    
    let path = `rings/${selectedMetal}`;
    
    if (selectedDesign) {
      path += `/${selectedDesign}`;
    }
    
    if (selectedShape) {
      path += `/${selectedShape}`;
    }
    
    if (selectedCarat) {
      path += `/${selectedCarat}ct.png`;
    } else {
      path += '/band.png';
    }
    
    return path;
  };

  const checkDuplicate = async () => {
    const imagePath = generateImagePath();
    if (!imagePath) return false;

    try {
      const { data, error } = await supabase
        .from('ring_images')
        .select('*')
        .eq('image_url', imagePath)
        .single();

      if (error && error.code !== 'PGRST116') {
        throw error;
      }

      return !!data;
    } catch (error) {
      console.error('Error checking duplicate:', error);
      return false;
    }
  };

  const uploadImage = async () => {
    // Validate required fields
    if (!selectedFile) {
      setMessage('Please select an image file');
      return;
    }

    if (!selectedMetal) {
      setMessage('Please select a metal type');
      return;
    }

    const imagePath = generateImagePath();
    if (!imagePath) {
      setMessage('Please select all required options');
      return;
    }

    // Check for duplicate
    const isDuplicate = await checkDuplicate();
    if (isDuplicate) {
      setMessage('An image for this combination already exists');
      return;
    }

    setUploading(true);
    setMessage('');

    try {
      // Upload file to Supabase Storage
      const { data: uploadData, error: uploadError } = await supabase.storage
        .from('ring-images')
        .upload(imagePath, selectedFile, {
          cacheControl: '3600',
          upsert: false
        });

      if (uploadError) {
        console.error('Storage upload error:', uploadError);
        throw new Error(`Storage error: ${uploadError.message}`);
      }

      // Get public URL
      const { data: urlData } = supabase.storage
        .from('ring-images')
        .getPublicUrl(imagePath);

      // Save metadata to database
      const { error: dbError } = await supabase
        .from('ring_images')
        .insert({
          metal: selectedMetal,
          design: selectedDesign || null,
          diamond_shape: selectedShape || null,
          carat: selectedCarat ? parseFloat(selectedCarat) : null,
          image_url: imagePath,
          public_url: urlData.publicUrl
        });

      if (dbError) {
        console.error('Database error:', dbError);
        throw new Error(`Database error: ${dbError.message}`);
      }

      setMessage('✅ Image uploaded successfully!');
      setSelectedFile(null);
      setPreviewUrl('');
      setSelectedMetal('');
      setSelectedDesign('');
      setSelectedShape('');
      setSelectedCarat('');
      
      // Reset file input
      const fileInput = document.getElementById('file-input');
      if (fileInput) fileInput.value = '';

      // Refresh the list
      fetchUploadedImages();

    } catch (error) {
      console.error('Upload error:', error);
      setMessage('❌ Error uploading image: ' + error.message);
    } finally {
      setUploading(false);
    }
  };

  const deleteImage = async (id, imageUrl) => {
    try {
      // Delete from storage
      const { error: storageError } = await supabase.storage
        .from('ring-images')
        .remove([imageUrl]);

      if (storageError) throw storageError;

      // Delete from database
      const { error: dbError } = await supabase
        .from('ring_images')
        .delete()
        .eq('id', id);

      if (dbError) throw dbError;

      setMessage('✅ Image deleted successfully!');
      fetchUploadedImages();
    } catch (error) {
      console.error('Delete error:', error);
      setMessage('❌ Error deleting image: ' + error.message);
    }
  };

  const toggleSection = (sectionKey) => {
    setExpandedSections(prev => ({
      ...prev,
      [sectionKey]: !prev[sectionKey]
    }));
  };

  const getMetalLabel = (value) => {
    return metals.find(m => m.value === value)?.label || value;
  };

  const getDesignLabel = (value) => {
    return designs.find(d => d.value === value)?.label || value;
  };

  const getShapeLabel = (value) => {
    return shapes.find(s => s.value === value)?.label || value;
  };

  // Group images by metal
  const groupedImages = uploadedImages.reduce((acc, image) => {
    const metal = image.metal || 'unknown';
    if (!acc[metal]) {
      acc[metal] = [];
    }
    acc[metal].push(image);
    return acc;
  }, {});

  // Helper to get available options for each dropdown
  const getAvailableDesigns = () => {
    if (!selectedMetal) return designs;
    // Exclude designs that already have a band image for this metal
    const usedDesigns = uploadedImages
      .filter(img => img.metal === selectedMetal && img.design && !img.diamond_shape && !img.carat)
      .map(img => img.design);
    return designs.filter(d => !usedDesigns.includes(d.value));
  };

  const getAvailableShapes = () => {
    if (!selectedMetal || !selectedDesign) return shapes;
    // Exclude shapes that already have a band image for this metal+design
    const usedShapes = uploadedImages
      .filter(img => img.metal === selectedMetal && img.design === selectedDesign && img.diamond_shape && !img.carat)
      .map(img => img.diamond_shape);
    return shapes.filter(s => !usedShapes.includes(s.value));
  };

  const getAvailableCarats = () => {
    if (!selectedMetal || !selectedDesign || !selectedShape) return carats;
    // Exclude carats that already have an image for this metal+design+shape
    const usedCarats = uploadedImages
      .filter(img => img.metal === selectedMetal && img.design === selectedDesign && img.diamond_shape === selectedShape && img.carat)
      .map(img => String(img.carat));
    return carats.filter(c => !usedCarats.includes(c.value));
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Ring Image Admin Panel</h1>
          <p className="text-gray-600">Upload and manage ring images for the customizer</p>
        </div>

        {/* Upload Section */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-8">
          <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
            <Upload className="w-5 h-5 mr-2" />
            Upload New Image
          </h2>

          {/* Selection Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
            {/* Metal Selection */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Metal Type *
              </label>
              <select
                value={selectedMetal}
                onChange={(e) => setSelectedMetal(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="">Select Metal</option>
                {metals.map((metal) => (
                  <option key={metal.value} value={metal.value}>
                    {metal.label}
                  </option>
                ))}
              </select>
            </div>

            {/* Design Selection */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Ring Design
              </label>
              <select
                value={selectedDesign}
                onChange={(e) => setSelectedDesign(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                disabled={!selectedMetal || getAvailableDesigns().length === 0}
              >
                <option value="">Select Design</option>
                {getAvailableDesigns().map((design) => (
                  <option key={design.value} value={design.value}>
                    {design.label}
                  </option>
                ))}
              </select>
              {selectedMetal && getAvailableDesigns().length === 0 && (
                <p className="text-xs text-red-500 mt-1">All designs for this metal are uploaded.</p>
              )}
            </div>

            {/* Shape Selection */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Diamond Shape
              </label>
              <select
                value={selectedShape}
                onChange={(e) => setSelectedShape(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                disabled={!selectedDesign || getAvailableShapes().length === 0}
              >
                <option value="">Select Shape</option>
                {getAvailableShapes().map((shape) => (
                  <option key={shape.value} value={shape.value}>
                    {shape.label}
                  </option>
                ))}
              </select>
              {selectedDesign && getAvailableShapes().length === 0 && (
                <p className="text-xs text-red-500 mt-1">All shapes for this metal/design are uploaded.</p>
              )}
            </div>

            {/* Carat Selection */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Carat Weight
              </label>
              <select
                value={selectedCarat}
                onChange={(e) => setSelectedCarat(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                disabled={!selectedShape || getAvailableCarats().length === 0}
              >
                <option value="">Select Carat</option>
                {getAvailableCarats().map((carat) => (
                  <option key={carat.value} value={carat.value}>
                    {carat.label}
                  </option>
                ))}
              </select>
              {selectedShape && getAvailableCarats().length === 0 && (
                <p className="text-xs text-red-500 mt-1">All carats for this metal/design/shape are uploaded.</p>
              )}
            </div>
          </div>

          {/* File Upload */}
          <div className="mb-6">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Image File *
            </label>
            <input
              id="file-input"
              type="file"
              accept="image/*"
              onChange={handleFileSelect}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <p className="text-sm text-gray-500 mt-1">
              Accepted formats: PNG, JPG, JPEG. Max size: 5MB
            </p>
          </div>

          {/* Preview */}
          {previewUrl && (
            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Preview
              </label>
              <div className="w-32 h-32 border border-gray-300 rounded-lg overflow-hidden">
                <img
                  src={previewUrl}
                  alt="Preview"
                  className="w-full h-full object-cover"
                />
              </div>
            </div>
          )}

          {/* Upload Button */}
          <button
            onClick={uploadImage}
            disabled={uploading || !selectedFile || !selectedMetal}
            className="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed flex items-center"
          >
            {uploading ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                Uploading...
              </>
            ) : (
              <>
                <Upload className="w-4 h-4 mr-2" />
                Upload Image
              </>
            )}
          </button>

          {/* Message */}
          {message && (
            <div className={`mt-4 p-3 rounded-md ${
              message.includes('✅') ? 'bg-green-50 text-green-800' : 
              message.includes('❌') ? 'bg-red-50 text-red-800' : 
              'bg-blue-50 text-blue-800'
            }`}>
              {message}
            </div>
          )}
        </div>

        {/* Uploaded Images Section */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
            <Image className="w-5 h-5 mr-2" />
            Uploaded Images ({uploadedImages.length})
          </h2>

          {uploadedImages.length === 0 ? (
            <p className="text-gray-500 text-center py-8">No images uploaded yet.</p>
          ) : (
            <div className="space-y-4">
              {Object.entries(groupedImages).map(([metal, images]) => (
                <div key={metal} className="border border-gray-200 rounded-lg">
                  <button
                    onClick={() => toggleSection(metal)}
                    className="w-full px-4 py-3 bg-gray-50 hover:bg-gray-100 flex items-center justify-between text-left"
                  >
                    <div className="flex items-center">
                      <Gem className="w-5 h-5 mr-2 text-blue-600" />
                      <span className="font-semibold text-gray-900">
                        {getMetalLabel(metal)} ({images.length} images)
                      </span>
                    </div>
                    {expandedSections[metal] ? (
                      <ChevronDown className="w-5 h-5 text-gray-500" />
                    ) : (
                      <ChevronRight className="w-5 h-5 text-gray-500" />
                    )}
                  </button>

                  {expandedSections[metal] && (
                    <div className="p-4 space-y-3">
                      {images.map((image) => (
                        <div key={image.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                          <div className="flex items-center space-x-4">
                            <div className="w-16 h-16 border border-gray-300 rounded-lg overflow-hidden bg-white">
                              <img
                                src={image.public_url}
                                alt="Ring"
                                className="w-full h-full object-cover"
                                onError={(e) => {
                                  e.target.src = 'data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iNjQiIGhlaWdodD0iNjQiIHZpZXdCb3g9IjAgMCA2NCA2NCIgZmlsbD0ibm9uZSIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj4KPHJlY3Qgd2lkdGg9IjY0IiBoZWlnaHQ9IjY0IiBmaWxsPSIjRjNGNEY2Ii8+CjxwYXRoIGQ9Ik0zMiAzMkwyMCAyMEg0NEwzMiAzMloiIGZpbGw9IiM5Q0EzQUYiLz4KPC9zdmc+';
                                }}
                              />
                            </div>
                            <div>
                              <div className="font-medium text-gray-900">
                                {image.design ? getDesignLabel(image.design) : 'Band Only'}
                              </div>
                              <div className="text-sm text-gray-600">
                                {image.diamond_shape && getShapeLabel(image.diamond_shape)}
                                {image.carat && ` • ${image.carat}ct`}
                              </div>
                              <div className="text-xs text-gray-500">
                                {image.image_url}
                              </div>
                            </div>
                          </div>
                          <div className="flex items-center space-x-2">
                            <button
                              onClick={() => window.open(image.public_url, '_blank')}
                              className="p-2 text-gray-600 hover:text-blue-600 hover:bg-blue-50 rounded-md"
                              title="View Image"
                            >
                              <Eye className="w-4 h-4" />
                            </button>
                            <button
                              onClick={() => deleteImage(image.id, image.image_url)}
                              className="p-2 text-gray-600 hover:text-red-600 hover:bg-red-50 rounded-md"
                              title="Delete Image"
                            >
                              <Trash2 className="w-4 h-4" />
                            </button>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
